  const [selectedConfig, setSelectedConfig] = useState<AiAgentConfig | null>(null);
  const [newDo, setNewDo] = useState("");
  const [newDont, setNewDont] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: configs, isLoading } = useQuery({
    queryKey: ["/api/ai-agent-configs"],
  });

  const { data: activeConfig } = useQuery({
    queryKey: ["/api/ai-agent-configs/active"],
  });

  const form = useForm<z.infer<typeof formSchema>>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      name: "",
      tonality: "professional",
      personality: "",
      dosList: [],
      dontsList: [],
      industry: "automotive",
      responseStyle: "helpful",
      isActive: false,
    },
  });

  const createMutation = useMutation({
    mutationFn: async (data: z.infer<typeof formSchema>) => {
      return await apiRequest("/api/ai-agent-configs", "POST", data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "AI agent configuration created successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/ai-agent-configs"] });
      setEditDialogOpen(false);
      form.reset();
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create AI agent configuration",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<z.infer<typeof formSchema>> }) => {
      return await apiRequest(`/api/ai-agent-configs/${id}`, "PUT", data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "AI agent configuration updated successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/ai-agent-configs"] });
      setEditDialogOpen(false);
      form.reset();
      setSelectedConfig(null);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update AI agent configuration",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest(`/api/ai-agent-configs/${id}`, "DELETE");
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "AI agent configuration deleted successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/ai-agent-configs"] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete AI agent configuration",
        variant: "destructive",
      });
    },
  });

  const activateMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest(`/api/ai-agent-configs/${id}/activate`, "POST");
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "AI agent configuration activated successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/ai-agent-configs"] });
      queryClient.invalidateQueries({ queryKey: ["/api/ai-agent-configs/active"] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to activate AI agent configuration",
        variant: "destructive",
      });
    },
  });

  const handleEdit = (config: AiAgentConfig | null = null) => {
    setSelectedConfig(config);
    if (config) {
      form.reset({
        name: config.name,
        tonality: config.tonality,
        personality: config.personality || "",
        dosList: (config.dosList as string[]) || [],
        dontsList: (config.dontsList as string[]) || [],
        industry: config.industry || "automotive",
        responseStyle: config.responseStyle || "helpful",
        isActive: config.isActive,
      });
    } else {
      form.reset();
    }
    setEditDialogOpen(true);
  };

  const onSubmit = (data: z.infer<typeof formSchema>) => {
    if (selectedConfig) {
      updateMutation.mutate({ id: selectedConfig.id, data });
    } else {
      createMutation.mutate(data);
    }
  };

  const addDo = () => {
    if (newDo.trim()) {
      const currentDos = form.getValues("dosList");
      form.setValue("dosList", [...currentDos, newDo.trim()]);
      setNewDo("");
    }
  };

  const removeDo = (index: number) => {
    const currentDos = form.getValues("dosList");
    form.setValue("dosList", currentDos.filter((_, i) => i !== index));
  };

  const addDont = () => {
    if (newDont.trim()) {
      const currentDonts = form.getValues("dontsList");
      form.setValue("dontsList", [...currentDonts, newDont.trim()]);
      setNewDont("");
    }
  };

  const removeDont = (index: number) => {
    const currentDonts = form.getValues("dontsList");
    form.setValue("dontsList", currentDonts.filter((_, i) => i !== index));
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-semibold text-gray-900">AI Agent Settings</h1>
        </div>
        <div className="animate-pulse">
          <div className="bg-gray-200 rounded-lg h-32 mb-4"></div>
          <div className="grid gap-4 md:grid-cols-2">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="bg-gray-200 rounded-lg h-48"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-semibold text-gray-900">AI Agent Settings</h1>
        <Button onClick={() => handleEdit()}>
          <Plus className="h-4 w-4 mr-2" />
          New Configuration
        </Button>
      </div>

      {/* Active Configuration Card */}
      {activeConfig && (
        <Card className="mb-6 border-green-200 bg-green-50">{/* Fixed type assertion */}
          <CardHeader>
            <div className="flex justify-between items-start">
              <div>
                <CardTitle className="text-lg flex items-center gap-2">
                  <Settings className="h-5 w-5" />
                  Active Configuration: {(activeConfig as any)?.name || 'Default'}
                </CardTitle>
                <CardDescription>
                  Currently controlling AI agent behavior
                </CardDescription>
              </div>
              <Badge className="bg-green-100 text-green-800 border-green-300">
                Active
              </Badge>
            </div>
          </CardHeader>
          <CardContent>
            <div className="grid md:grid-cols-2 gap-4 text-sm">
              <div>
                <strong>Tonality:</strong> {(activeConfig as any)?.tonality || 'Professional'}
              </div>
              <div>
                <strong>Response Style:</strong> {(activeConfig as any)?.responseStyle || 'Conversational'}
              </div>
              <div>
                <strong>Industry:</strong> {(activeConfig as any)?.industry || 'Automotive'}
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Configuration List */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {Array.isArray(configs) && configs.map((config: AiAgentConfig) => (
          <Card key={config.id} className={`hover:shadow-md transition-shadow ${config.isActive ? 'ring-2 ring-green-200' : ''}`}>
            <CardHeader>
              <div className="flex justify-between items-start">
                <div className="space-y-1">
                  <CardTitle className="text-lg">{config.name}</CardTitle>
                  <CardDescription className="line-clamp-2">
                    {config.personality || "No description"}
                  </CardDescription>
                </div>
                {config.isActive && (
                  <Badge className="bg-green-100 text-green-800 border-green-300">
                    Active
                  </Badge>
                )}
              </div>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                <div className="text-sm text-gray-600">
                  <div><strong>Tonality:</strong> {config.tonality}</div>
                  <div><strong>Style:</strong> {config.responseStyle}</div>
                  <div><strong>Industry:</strong> {config.industry}</div>
                  <div><strong>Do's:</strong> {(config.dosList as string[])?.length || 0} rules</div>
                  <div><strong>Don'ts:</strong> {(config.dontsList as string[])?.length || 0} rules</div>
                </div>

                <div className="flex gap-2">
                  {!config.isActive && (
                    <Button 
                      variant="outline" 
                      size="sm" 
                      className="flex-1"
                      onClick={() => activateMutation.mutate(config.id)}
                      disabled={activateMutation.isPending}
                    >
                      <Check className="h-4 w-4 mr-1" />
                      Activate
                    </Button>
                  )}
                  <Button 
                    variant="outline" 
                    size="sm" 
                    className="flex-1"
                    onClick={() => handleEdit(config)}
                  >
                    <Edit className="h-4 w-4 mr-1" />
                    Edit
                  </Button>
                  <AlertDialog>
                    <AlertDialogTrigger asChild>
                      <Button variant="outline" size="sm" disabled={Boolean(config.isActive)}>
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </AlertDialogTrigger>
                    <AlertDialogContent>
                      <AlertDialogHeader>
                        <AlertDialogTitle>Delete Configuration</AlertDialogTitle>
                        <AlertDialogDescription>
                          Are you sure you want to delete "{config.name}"? This action cannot be undone.
                        </AlertDialogDescription>
                      </AlertDialogHeader>
                      <AlertDialogFooter>
                        <AlertDialogCancel>Cancel</AlertDialogCancel>
                        <AlertDialogAction
                          onClick={() => deleteMutation.mutate(config.id)}
                          className="bg-red-600 hover:bg-red-700"
                        >
                          Delete
                        </AlertDialogAction>
                      </AlertDialogFooter>
                    </AlertDialogContent>
                  </AlertDialog>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Edit/Create Dialog */}
      <Dialog open={editDialogOpen} onOpenChange={setEditDialogOpen}>
        <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>
              {selectedConfig ? "Edit" : "Create"} AI Agent Configuration
            </DialogTitle>
            <DialogDescription>
              Configure how the AI agent behaves and responds to users.
            </DialogDescription>
          </DialogHeader>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="name"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Configuration Name</FormLabel>
                    <FormControl>
                      <Input placeholder="e.g., Professional Automotive Agent" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <div className="grid md:grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="tonality"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Tonality</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Select tonality" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="professional">Professional</SelectItem>
                          <SelectItem value="friendly">Friendly</SelectItem>
                          <SelectItem value="casual">Casual</SelectItem>
                          <SelectItem value="enthusiastic">Enthusiastic</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="responseStyle"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Response Style</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value || undefined}>
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Select response style" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="helpful">Helpful</SelectItem>
                          <SelectItem value="consultative">Consultative</SelectItem>
                          <SelectItem value="direct">Direct</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <FormField
                control={form.control}
                name="personality"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Personality Description</FormLabel>
                    <FormControl>
                      <Textarea 
                        placeholder="Describe the agent's personality and approach..."
                        {...field}
                        value={field.value || ''}
                      />
                    </FormControl>
                    <FormDescription>
                      Provide a brief description of how the agent should behave.
                    </FormDescription>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Do's Section */}
              <div className="space-y-2">
                <FormLabel>Do's - What the agent should always do</FormLabel>
                <div className="flex gap-2">
                  <Input
                    placeholder="Add a do..."
                    value={newDo}
                    onChange={(e) => setNewDo(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addDo())}
                  />
                  <Button type="button" onClick={addDo} size="sm">
                    Add
                  </Button>
                </div>
                <div className="flex flex-wrap gap-2">
                  {form.watch("dosList").map((item, index) => (
                    <Badge key={index} variant="secondary" className="cursor-pointer" onClick={() => removeDo(index)}>
                      {item} Ã—
                    </Badge>
                  ))}
                </div>
              </div>

              {/* Don'ts Section */}
              <div className="space-y-2">
                <FormLabel>Don'ts - What the agent should never do</FormLabel>
                <div className="flex gap-2">
                  <Input
                    placeholder="Add a don't..."
                    value={newDont}
                    onChange={(e) => setNewDont(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addDont())}
                  />
                  <Button type="button" onClick={addDont} size="sm">
                    Add
                  </Button>
                </div>
                <div className="flex flex-wrap gap-2">
                  {form.watch("dontsList").map((item, index) => (
                    <Badge key={index} variant="destructive" className="cursor-pointer" onClick={() => removeDont(index)}>
                      {item} Ã—
                    </Badge>
                  ))}
                </div>
              </div>

              <div className="flex justify-end space-x-2 pt-4">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setEditDialogOpen(false)}
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  disabled={createMutation.isPending || updateMutation.isPending}
                >
                  {selectedConfig 
                    ? (updateMutation.isPending ? "Updating..." : "Update Configuration")
                    : (createMutation.isPending ? "Creating..." : "Create Configuration")
                  }
                </Button>
              </div>
            </form>
          </Form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/campaigns.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { apiRequest } from "@/lib/queryClient";
import { Copy, Edit, Trash2, Plus, Eye, Play } from "lucide-react";
import CampaignExecutionModal from "@/components/campaigns/CampaignExecutionModal";
import type { Campaign } from "@shared/schema";

export default function CampaignsPage() {
  const [cloneDialogOpen, setCloneDialogOpen] = useState(false);
  const [selectedCampaign, setSelectedCampaign] = useState<Campaign | null>(null);
  const [cloneName, setCloneName] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: campaigns, isLoading } = useQuery({
    queryKey: ["/api/campaigns"],
  });

  const cloneMutation = useMutation({
    mutationFn: async ({ id, name }: { id: string; name?: string }) => {
      return await apiRequest(`/api/campaigns/${id}/clone`, "POST", { name });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Campaign cloned successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/campaigns"] });
      setCloneDialogOpen(false);
      setCloneName("");
      setSelectedCampaign(null);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to clone campaign",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest(`/api/campaigns/${id}`, "DELETE");
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Campaign deleted successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/campaigns"] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete campaign",
        variant: "destructive",
      });
    },
  });

  const handleClone = (campaign: Campaign) => {
    setSelectedCampaign(campaign);
    setCloneName(`${campaign.name} (Copy)`);
    setCloneDialogOpen(true);
  };

  const handleCloneConfirm = () => {
    if (selectedCampaign) {
      cloneMutation.mutate({
        id: selectedCampaign.id,
        name: cloneName || undefined,
      });
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "active":
        return "bg-green-100 text-green-800 border-green-300";
      case "scheduled":
        return "bg-blue-100 text-blue-800 border-blue-300";
      case "completed":
        return "bg-gray-100 text-gray-800 border-gray-300";
      default:
        return "bg-yellow-100 text-yellow-800 border-yellow-300";
    }
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-semibold text-gray-900">Campaigns</h1>
        </div>
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
          {[...Array(6)].map((_, i) => (
            <div key={i} className="animate-pulse">
              <div className="bg-gray-200 rounded-lg h-48"></div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-semibold text-gray-900">Campaigns</h1>
        <Button>
          <Plus className="h-4 w-4 mr-2" />
          New Campaign
        </Button>
      </div>

      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {Array.isArray(campaigns) && campaigns.map((campaign: Campaign) => (
          <Card key={campaign.id} className="hover:shadow-md transition-shadow">
            <CardHeader>
              <div className="flex justify-between items-start">
                <div className="space-y-1">
                  <CardTitle className="text-lg">{campaign.name}</CardTitle>
                  <CardDescription className="line-clamp-2">
                    {campaign.context}
                  </CardDescription>
                </div>
                <Badge className={getStatusColor(campaign.status)}>
                  {campaign.status}
                </Badge>
              </div>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                <div className="text-sm text-gray-600">
                  <div>Templates: {campaign.numberOfTemplates || 0}</div>
                  <div>Frequency: Every {campaign.daysBetweenMessages || 3} days</div>
                  {campaign.openRate && (
                    <div>Open Rate: {campaign.openRate}%</div>
                  )}
                </div>

                <div className="flex gap-2">
                  <CampaignExecutionModal campaign={{
                    ...campaign,
                    templates: typeof campaign.templates === 'string' ? campaign.templates : JSON.stringify(campaign.templates || [])
                  } as Campaign}>
                    <Button size="sm" className="flex-1">
                      <Play className="h-4 w-4 mr-1" />
                      Execute
                    </Button>
                  </CampaignExecutionModal>
                  <Button variant="outline" size="sm" className="flex-1">
                    <Eye className="h-4 w-4 mr-1" />
                    View
                  </Button>
                  <Button 
                    variant="outline" 
                    size="sm" 
                    onClick={() => handleClone(campaign)}
                    disabled={cloneMutation.isPending}
                  >
                    <Copy className="h-4 w-4" />
                  </Button>
                  <AlertDialog>
                    <AlertDialogTrigger asChild>
                      <Button variant="outline" size="sm">
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </AlertDialogTrigger>
                    <AlertDialogContent>
                      <AlertDialogHeader>
                        <AlertDialogTitle>Delete Campaign</AlertDialogTitle>
                        <AlertDialogDescription>
                          Are you sure you want to delete "{campaign.name}"? This action cannot be undone.
                        </AlertDialogDescription>
                      </AlertDialogHeader>
                      <AlertDialogFooter>
                        <AlertDialogCancel>Cancel</AlertDialogCancel>
                        <AlertDialogAction
                          onClick={() => deleteMutation.mutate(campaign.id)}
                          className="bg-red-600 hover:bg-red-700"
                        >
                          Delete
                        </AlertDialogAction>
                      </AlertDialogFooter>
                    </AlertDialogContent>
                  </AlertDialog>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Clone Dialog */}
      <Dialog open={cloneDialogOpen} onOpenChange={setCloneDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Clone Campaign</DialogTitle>
            <DialogDescription>
              Create a copy of "{selectedCampaign?.name}" with a new name.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <label htmlFor="clone-name" className="text-sm font-medium">
                Campaign Name
              </label>
              <Input
                id="clone-name"
                value={cloneName}
                onChange={(e) => setCloneName(e.target.value)}
                placeholder="Enter new campaign name"
                className="mt-1"
              />
            </div>
            <div className="flex justify-end space-x-2">
              <Button
                variant="outline"
                onClick={() => setCloneDialogOpen(false)}
              >
                Cancel
              </Button>
              <Button
                onClick={handleCloneConfirm}
                disabled={cloneMutation.isPending || !cloneName.trim()}
              >
                {cloneMutation.isPending ? "Cloning..." : "Clone Campaign"}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/conversations.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { MessageCircle, Plus, Clock, User, CheckCircle, AlertCircle } from "lucide-react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import type { Conversation, ConversationMessage } from "@shared/schema";

export default function ConversationsPage() {
  const { toast } = useToast();
  const [selectedConversationId, setSelectedConversationId] = useState<string | null>(null);
  const [newConversationOpen, setNewConversationOpen] = useState(false);

  // Fetch conversations
  const { data: conversations = [], isLoading } = useQuery<Conversation[]>({
    queryKey: ["/api/conversations"],
  });

  // Fetch selected conversation messages
  const { data: messages = [] } = useQuery<ConversationMessage[]>({
    queryKey: ["/api/conversations", selectedConversationId, "messages"],
    enabled: !!selectedConversationId,
  });

  // Create conversation mutation
  const createConversationMutation = useMutation({
    mutationFn: async (data: { subject: string; priority: string; campaignId?: string }) => {
      return apiRequest("/api/conversations", "POST", {
        subject: data.subject,
        priority: data.priority,
        campaignId: data.campaignId || null,
        userId: "current-user", // In real app, get from auth context
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/conversations"] });
      setNewConversationOpen(false);
      toast({
        title: "Conversation Created",
        description: "New conversation has been started successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create conversation.",
        variant: "destructive",
      });
    },
  });

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async (data: { conversationId: string; content: string }) => {
      return apiRequest(`/api/conversations/${data.conversationId}/messages`, "POST", {
        content: data.content,
        senderId: "current-user", // In real app, get from auth context
        isFromAI: 0,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/conversations", selectedConversationId, "messages"] });
      queryClient.invalidateQueries({ queryKey: ["/api/conversations"] });
    },
  });

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "active":
        return <MessageCircle className="h-4 w-4 text-blue-500" />;
      case "closed":
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case "archived":
        return <AlertCircle className="h-4 w-4 text-gray-500" />;
      default:
        return <MessageCircle className="h-4 w-4" />;
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case "high":
        return "bg-red-100 text-red-800";
      case "normal":
        return "bg-blue-100 text-blue-800";
      case "low":
        return "bg-gray-100 text-gray-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="animate-pulse space-y-4">
          <div className="h-8 bg-gray-200 rounded w-1/4"></div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {[1, 2, 3, 4].map((i) => (
              <div key={i} className="h-32 bg-gray-200 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Conversations</h1>
          <p className="text-gray-600 mt-1">Manage customer conversations and support requests</p>
        </div>

        <div className="text-sm text-gray-500">
          Conversations are created automatically when campaigns are launched
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Conversations List */}
        <div className="lg:col-span-1 space-y-4">
          <h2 className="text-lg font-semibold text-gray-900">Active Conversations</h2>

          {conversations.length === 0 ? (
            <Card>
              <CardContent className="p-6 text-center">
                <MessageCircle className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-500">No conversations yet</p>
                <p className="text-sm text-gray-400 mt-2">Start a new conversation to get started</p>
              </CardContent>
            </Card>
          ) : (
            conversations.map((conversation) => (
              <Card
                key={conversation.id}
                className={`cursor-pointer transition-all hover:shadow-md ${
                  selectedConversationId === conversation.id ? "ring-2 ring-blue-500" : ""
                }`}
                onClick={() => setSelectedConversationId(conversation.id)}
              >
                <CardContent className="p-4">
                  <div className="flex items-start justify-between mb-2">
                    <div className="flex items-center space-x-2">
                      {getStatusIcon(conversation.status)}
                      <h3 className="font-medium text-gray-900 truncate">{conversation.subject}</h3>
                    </div>
                    <Badge className={getPriorityColor(conversation.priority)}>{conversation.priority}</Badge>
                  </div>

                  <div className="flex items-center text-sm text-gray-500 space-x-4">
                    <div className="flex items-center space-x-1">
                      <Clock className="h-3 w-3" />
                      <span>{new Date(conversation.updatedAt).toLocaleDateString()}</span>
                    </div>
                    <div className="flex items-center space-x-1">
                      <User className="h-3 w-3" />
                      <span>User</span>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))
          )}
        </div>

        {/* Conversation View */}
        <div className="lg:col-span-2">
          {selectedConversationId ? (
            <ConversationView
              conversationId={selectedConversationId}
              messages={messages}
              onSendMessage={(content) =>
                sendMessageMutation.mutate({ conversationId: selectedConversationId, content })
              }
              isLoading={sendMessageMutation.isPending}
            />
          ) : (
            <Card className="h-96">
              <CardContent className="p-6 flex items-center justify-center h-full">
                <div className="text-center">
                  <MessageCircle className="h-16 w-16 text-gray-400 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">Select a Conversation</h3>
                  <p className="text-gray-500">Choose a conversation from the list to view messages</p>
                </div>
              </CardContent>
            </Card>
          )}
        </div>
      </div>
    </div>
  );
}

function ConversationView({
  conversationId,
  messages,
  onSendMessage,
  isLoading,
}: {
  conversationId: string;
  messages: ConversationMessage[];
  onSendMessage: (content: string) => void;
  isLoading: boolean;
}) {
  const [newMessage, setNewMessage] = useState("");

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (newMessage.trim()) {
      onSendMessage(newMessage.trim());
      setNewMessage("");
    }
  };

  return (
    <Card className="h-96 flex flex-col">
      <CardHeader className="pb-3">
        <CardTitle className="text-lg">Conversation Messages</CardTitle>
      </CardHeader>

      <CardContent className="flex-1 flex flex-col p-4">
        {/* Messages */}
        <div className="flex-1 overflow-y-auto space-y-4 mb-4">
          {messages.length === 0 ? (
            <div className="text-center text-gray-500 py-8">
              <MessageCircle className="h-8 w-8 mx-auto mb-2 text-gray-400" />
              <p>No messages yet. Start the conversation!</p>
            </div>
          ) : (
            messages.map((message) => (
              <div
                key={message.id}
                className={`flex ${message.isFromAI ? "justify-start" : "justify-end"}`}
              >
                <div
                  className={`max-w-xs lg:max-w-md px-3 py-2 rounded-lg ${
                    message.isFromAI
                      ? "bg-gray-100 text-gray-900"
                      : "bg-blue-500 text-white"
                  }`}
                >
                  <p className="text-sm">{message.content}</p>
                  <p className="text-xs mt-1 opacity-70">
                    {new Date(message.createdAt).toLocaleTimeString()}
                  </p>
                </div>
              </div>
            ))
          )}
        </div>

        {/* Message Input */}
        <form onSubmit={handleSubmit} className="flex space-x-2">
          <Textarea
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Type your message..."
            className="flex-1 min-h-[60px] max-h-32 resize-none"
            onKeyDown={(e) => {
              if (e.key === "Enter" && !e.shiftKey) {
                e.preventDefault();
                handleSubmit(e);
              }
            }}
          />
          <Button type="submit" disabled={isLoading || !newMessage.trim()}>
            {isLoading ? "..." : "Send"}
          </Button>
        </form>
      </CardContent>
    </Card>
  );
}

