import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Users, Shield, UserCheck, Clock } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import type { User } from "@shared/schema";

export default function UserManagementPage() {
  const { toast } = useToast();

  // Mock user data for demo - in real app this would come from API
  const mockUsers: User[] = [
    {
      id: "1",
      username: "admin_user",
      password: "***",
      role: "admin",
      email: "admin@dealership.com",
      createdAt: new Date("2024-01-15"),
      clientId: null,
    },
    {
      id: "2", 
      username: "sales_manager",
      password: "***",
      role: "manager",
      email: "manager@dealership.com",
      createdAt: new Date("2024-02-10"),
      clientId: null,
    },
    {
      id: "3",
      username: "sales_rep1",
      password: "***", 
      role: "user",
      email: "rep1@dealership.com",
      createdAt: new Date("2024-03-05"),
      clientId: null,
    },
    {
      id: "4",
      username: "sales_rep2",
      password: "***",
      role: "user", 
      email: "rep2@dealership.com",
      createdAt: new Date("2024-03-12"),
      clientId: null,
    },
  ];

  const [users, setUsers] = useState<User[]>(mockUsers);

  // Update user role mutation
  const updateRoleMutation = useMutation({
    mutationFn: async ({ userId, role }: { userId: string; role: string }) => {
      return apiRequest(`/api/users/${userId}/role`, "PUT", { role });
    },
    onSuccess: (_, { userId, role }) => {
      setUsers(prev => prev.map(user => 
        user.id === userId ? { ...user, role } : user
      ));
      toast({
        title: "Role Updated",
        description: "User role has been updated successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update user role.",
        variant: "destructive",
      });
    },
  });

  const getRoleIcon = (role: string) => {
    switch (role) {
      case "admin":
        return <Shield className="h-4 w-4 text-red-500" />;
      case "manager":
        return <UserCheck className="h-4 w-4 text-blue-500" />;
      case "user":
        return <Users className="h-4 w-4 text-green-500" />;
      default:
        return <Users className="h-4 w-4" />;
    }
  };

  const getRoleColor = (role: string) => {
    switch (role) {
      case "admin":
        return "bg-red-100 text-red-800";
      case "manager":
        return "bg-blue-100 text-blue-800";
      case "user":
        return "bg-green-100 text-green-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  const getRolePermissions = (role: string) => {
    switch (role) {
      case "admin":
        return "Full system access, user management, all campaigns";
      case "manager":
        return "Campaign management, team oversight, reporting";
      case "user":
        return "Campaign creation, email sending, basic reporting";
      default:
        return "Limited access";
    }
  };

  return (
    <div className="p-6">
      <div className="mb-6">
        <h1 className="text-2xl font-bold text-gray-900">User Management</h1>
        <p className="text-gray-600 mt-1">Manage user roles and permissions</p>
      </div>

      {/* Role Overview Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-3">
              <Shield className="h-8 w-8 text-red-500" />
              <div>
                <h3 className="font-semibold text-gray-900">Administrators</h3>
                <p className="text-2xl font-bold text-red-600">
                  {users.filter(u => u.role === "admin").length}
                </p>
                <p className="text-sm text-gray-500">Full access</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-3">
              <UserCheck className="h-8 w-8 text-blue-500" />
              <div>
                <h3 className="font-semibold text-gray-900">Managers</h3>
                <p className="text-2xl font-bold text-blue-600">
                  {users.filter(u => u.role === "manager").length}
                </p>
                <p className="text-sm text-gray-500">Team oversight</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-3">
              <Users className="h-8 w-8 text-green-500" />
              <div>
                <h3 className="font-semibold text-gray-900">Sales Reps</h3>
                <p className="text-2xl font-bold text-green-600">
                  {users.filter(u => u.role === "user").length}
                </p>
                <p className="text-sm text-gray-500">Standard access</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Users Table */}
      <Card>
        <CardHeader>
          <CardTitle>All Users</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {users.map((user) => (
              <div
                key={user.id}
                className="flex items-center justify-between p-4 border rounded-lg hover:bg-gray-50 transition-colors"
              >
                <div className="flex items-center space-x-4">
                  <div className="flex items-center space-x-2">
                    {getRoleIcon(user.role)}
                    <div>
                      <h3 className="font-medium text-gray-900">{user.username}</h3>
                      <p className="text-sm text-gray-500">{user.email}</p>
                    </div>
                  </div>
                </div>

                <div className="flex items-center space-x-4">
                  <div className="text-right">
                    <Badge className={getRoleColor(user.role)}>{user.role}</Badge>
                    <p className="text-xs text-gray-500 mt-1">
                      {getRolePermissions(user.role)}
                    </p>
                  </div>

                  <div className="flex items-center space-x-2 text-sm text-gray-500">
                    <Clock className="h-4 w-4" />
                    <span>Joined {new Date(user.createdAt).toLocaleDateString()}</span>
                  </div>

                  <Select
                    value={user.role}
                    onValueChange={(newRole) => {
                      if (newRole !== user.role) {
                        updateRoleMutation.mutate({ userId: user.id, role: newRole });
                      }
                    }}
                  >
                    <SelectTrigger className="w-32">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="admin">Admin</SelectItem>
                      <SelectItem value="manager">Manager</SelectItem>
                      <SelectItem value="user">User</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Role Permissions Info */}
      <Card className="mt-6">
        <CardHeader>
          <CardTitle>Role Permissions</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div>
              <div className="flex items-center space-x-2 mb-3">
                <Shield className="h-5 w-5 text-red-500" />
                <h3 className="font-semibold text-red-600">Administrator</h3>
              </div>
              <ul className="space-y-1 text-sm text-gray-600">
                <li>â€¢ Full system access</li>
                <li>â€¢ User role management</li>
                <li>â€¢ All campaign operations</li>
                <li>â€¢ System configuration</li>
                <li>â€¢ Analytics and reporting</li>
              </ul>
            </div>

            <div>
              <div className="flex items-center space-x-2 mb-3">
                <UserCheck className="h-5 w-5 text-blue-500" />
                <h3 className="font-semibold text-blue-600">Manager</h3>
              </div>
              <ul className="space-y-1 text-sm text-gray-600">
                <li>â€¢ Campaign management</li>
                <li>â€¢ Team oversight</li>
                <li>â€¢ Advanced reporting</li>
                <li>â€¢ Template management</li>
                <li>â€¢ Performance tracking</li>
              </ul>
            </div>

            <div>
              <div className="flex items-center space-x-2 mb-3">
                <Users className="h-5 w-5 text-green-500" />
                <h3 className="font-semibold text-green-600">Sales Rep</h3>
              </div>
              <ul className="space-y-1 text-sm text-gray-600">
                <li>â€¢ Campaign creation</li>
                <li>â€¢ Email sending</li>
                <li>â€¢ Basic reporting</li>
                <li>â€¢ Customer conversations</li>
                <li>â€¢ Personal analytics</li>
              </ul>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/white-label.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { useToast } from "@/hooks/use-toast";
import { Plus, Edit, Trash2, Save, Settings, Palette, Globe } from "lucide-react";
import type { Client, InsertClient } from "@shared/schema";

export default function WhiteLabelPage() {
  const [selectedClient, setSelectedClient] = useState<Client | null>(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: clients, isLoading } = useQuery({
    queryKey: ["/api/clients"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: InsertClient) => {
      return await apiRequest("/api/clients", "POST", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/clients"] });
      setShowCreateForm(false);
      toast({
        title: "Success",
        description: "Client created successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create client",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<InsertClient> }) => {
      return await apiRequest(`/api/clients/${id}`, "PUT", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/clients"] });
      setEditMode(false);
      toast({
        title: "Success",
        description: "Client updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update client",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest(`/api/clients/${id}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/clients"] });
      setSelectedClient(null);
      toast({
        title: "Success",
        description: "Client deleted successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete client",
        variant: "destructive",
      });
    },
  });

  const handleCreateClient = (formData: FormData) => {
    const name = formData.get('name') as string;
    const domain = formData.get('domain') as string;
    const companyName = formData.get('companyName') as string;
    const primaryColor = formData.get('primaryColor') as string;
    const secondaryColor = formData.get('secondaryColor') as string;

    createMutation.mutate({
      name,
      domain,
      brandingConfig: {
        companyName,
        primaryColor,
        secondaryColor,
        logoUrl: '',
        favicon: '',
        customCss: ''
      },
      settings: {},
      active: true
    });
  };

  const handleUpdateClient = (formData: FormData) => {
    if (!selectedClient) return;

    const name = formData.get('name') as string;
    const domain = formData.get('domain') as string;
    const companyName = formData.get('companyName') as string;
    const primaryColor = formData.get('primaryColor') as string;
    const secondaryColor = formData.get('secondaryColor') as string;
    const logoUrl = formData.get('logoUrl') as string;
    const customCss = formData.get('customCss') as string;
    const active = formData.get('active') === 'on';

    updateMutation.mutate({
      id: selectedClient.id,
      data: {
        name,
        domain,
        brandingConfig: {
          companyName,
          primaryColor,
          secondaryColor,
          logoUrl,
          favicon: '',
          customCss
        },
        settings: {},
        active
      }
    });
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 p-8">
        <div className="max-w-7xl mx-auto">
          <div className="animate-pulse space-y-4">
            <div className="h-8 bg-gray-200 rounded w-1/4"></div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {[1, 2, 3].map((i) => (
                <div key={i} className="h-48 bg-gray-200 rounded"></div>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="bg-white border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="flex justify-between items-center">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">White Label Management</h1>
              <p className="text-lg text-gray-600 mt-2">
                Manage client branding and multi-tenant configurations
              </p>
            </div>
            <Button 
              onClick={() => setShowCreateForm(true)}
              className="flex items-center gap-2"
            >
              <Plus className="w-4 h-4" />
              New Client
            </Button>
          </div>
        </div>
      </div>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Client List */}
          <div className="lg:col-span-1">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Globe className="w-5 h-5" />
                  Clients
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  {Array.isArray(clients) && clients.map((client: Client) => (
                    <div
                      key={client.id}
                      onClick={() => {
                        setSelectedClient(client);
                        setEditMode(false);
                      }}
                      className={`p-3 rounded-lg border cursor-pointer transition-colors ${
                        selectedClient?.id === client.id
                          ? 'border-blue-500 bg-blue-50'
                          : 'border-gray-200 hover:border-gray-300'
                      }`}
                    >
                      <div className="flex justify-between items-start">
                        <div className="flex-1">
                          <h3 className="font-medium text-gray-900">{client.name}</h3>
                          <p className="text-sm text-gray-500">{client.domain || 'No domain'}</p>
                        </div>
                        <div className={`w-2 h-2 rounded-full ${client.active ? 'bg-green-500' : 'bg-gray-400'}`} />
                      </div>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Client Details/Form */}
          <div className="lg:col-span-2">
            {showCreateForm ? (
              <Card>
                <CardHeader>
                  <CardTitle>Create New Client</CardTitle>
                </CardHeader>
                <CardContent>
                  <form onSubmit={(e) => {
                    e.preventDefault();
                    const formData = new FormData(e.currentTarget);
                    handleCreateClient(formData);
                  }} className="space-y-4">
                    <div>
                      <Label htmlFor="name">Client Name</Label>
                      <Input id="name" name="name" required />
                    </div>
                    <div>
                      <Label htmlFor="domain">Domain</Label>
                      <Input id="domain" name="domain" placeholder="client.yourdomain.com" />
                    </div>
                    <div>
                      <Label htmlFor="companyName">Company Name</Label>
                      <Input id="companyName" name="companyName" required />
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <Label htmlFor="primaryColor">Primary Color</Label>
                        <Input id="primaryColor" name="primaryColor" type="color" defaultValue="#2563eb" />
                      </div>
                      <div>
                        <Label htmlFor="secondaryColor">Secondary Color</Label>
                        <Input id="secondaryColor" name="secondaryColor" type="color" defaultValue="#1e40af" />
                      </div>
                    </div>
                    <div className="flex gap-2">
                      <Button type="submit" disabled={createMutation.isPending}>
                        <Save className="w-4 h-4 mr-2" />
                        Create Client
                      </Button>
                      <Button
                        type="button"
                        variant="outline"
                        onClick={() => setShowCreateForm(false)}
                      >
                        Cancel
                      </Button>
                    </div>
                  </form>
                </CardContent>
              </Card>
            ) : selectedClient ? (
              <Card>
                <CardHeader>
                  <div className="flex justify-between items-center">
                    <CardTitle className="flex items-center gap-2">
                      <Palette className="w-5 h-5" />
                      {selectedClient.name}
                    </CardTitle>
                    <div className="flex gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setEditMode(!editMode)}
                      >
                        <Edit className="w-4 h-4" />
                      </Button>
                      <Button
                        variant="destructive"
                        size="sm"
                        onClick={() => {
                          if (confirm('Are you sure you want to delete this client?')) {
                            deleteMutation.mutate(selectedClient.id);
                          }
                        }}
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </div>
                  </div>
                </CardHeader>
                <CardContent>
                  {editMode ? (
                    <form onSubmit={(e) => {
                      e.preventDefault();
                      const formData = new FormData(e.currentTarget);
                      handleUpdateClient(formData);
                    }} className="space-y-4">
                      <div>
                        <Label htmlFor="name">Client Name</Label>
                        <Input id="name" name="name" defaultValue={selectedClient.name} required />
                      </div>
                      <div>
                        <Label htmlFor="domain">Domain</Label>
                        <Input id="domain" name="domain" defaultValue={selectedClient.domain || ''} />
                      </div>
                      <div>
                        <Label htmlFor="companyName">Company Name</Label>
                        <Input 
                          id="companyName" 
                          name="companyName" 
                          defaultValue={(selectedClient.brandingConfig as any)?.companyName || ''} 
                          required 
                        />
                      </div>
                      <div className="grid grid-cols-2 gap-4">
                        <div>
                          <Label htmlFor="primaryColor">Primary Color</Label>
                          <Input 
                            id="primaryColor" 
                            name="primaryColor" 
                            type="color" 
                            defaultValue={(selectedClient.brandingConfig as any)?.primaryColor || '#2563eb'} 
                          />
                        </div>
                        <div>
                          <Label htmlFor="secondaryColor">Secondary Color</Label>
                          <Input 
                            id="secondaryColor" 
                            name="secondaryColor" 
                            type="color" 
                            defaultValue={(selectedClient.brandingConfig as any)?.secondaryColor || '#1e40af'} 
                          />
                        </div>
                      </div>
                      <div>
                        <Label htmlFor="logoUrl">Logo URL</Label>
                        <Input 
                          id="logoUrl" 
                          name="logoUrl" 
                          type="url"
                          defaultValue={(selectedClient.brandingConfig as any)?.logoUrl || ''} 
                        />
                      </div>
                      <div>
                        <Label htmlFor="customCss">Custom CSS</Label>
                        <Textarea 
                          id="customCss" 
                          name="customCss" 
                          rows={6}
                          defaultValue={(selectedClient.brandingConfig as any)?.customCss || ''} 
                        />
                      </div>
                      <div className="flex items-center space-x-2">
                        <Switch 
                          id="active" 
                          name="active" 
                          defaultChecked={selectedClient.active} 
                        />
                        <Label htmlFor="active">Active</Label>
                      </div>
                      <div className="flex gap-2">
                        <Button type="submit" disabled={updateMutation.isPending}>
                          <Save className="w-4 h-4 mr-2" />
                          Save Changes
                        </Button>
                        <Button
                          type="button"
                          variant="outline"
                          onClick={() => setEditMode(false)}
                        >
                          Cancel
                        </Button>
                      </div>
                    </form>
                  ) : (
                    <div className="space-y-4">
                      <div className="grid grid-cols-2 gap-4">
                        <div>
                          <Label className="text-sm font-medium text-gray-500">Domain</Label>
                          <p className="text-sm">{selectedClient.domain || 'Not set'}</p>
                        </div>
                        <div>
                          <Label className="text-sm font-medium text-gray-500">Status</Label>
                          <p className={`text-sm ${selectedClient.active ? 'text-green-600' : 'text-red-600'}`}>
                            {selectedClient.active ? 'Active' : 'Inactive'}
                          </p>
                        </div>
                      </div>
                      <div>
                        <Label className="text-sm font-medium text-gray-500">Company Name</Label>
                        <p className="text-sm">{(selectedClient.brandingConfig as any)?.companyName || 'Not set'}</p>
                      </div>
                      <div className="grid grid-cols-2 gap-4">
                        <div>
                          <Label className="text-sm font-medium text-gray-500">Primary Color</Label>
                          <div className="flex items-center gap-2">
                            <div 
                              className="w-6 h-6 rounded border"
                              style={{ backgroundColor: (selectedClient.brandingConfig as any)?.primaryColor || '#2563eb' }}
                            />
                            <span className="text-sm">{(selectedClient.brandingConfig as any)?.primaryColor || '#2563eb'}</span>
                          </div>
                        </div>
                        <div>
                          <Label className="text-sm font-medium text-gray-500">Secondary Color</Label>
                          <div className="flex items-center gap-2">
                            <div 
                              className="w-6 h-6 rounded border"
                              style={{ backgroundColor: (selectedClient.brandingConfig as any)?.secondaryColor || '#1e40af' }}
                            />
                            <span className="text-sm">{(selectedClient.brandingConfig as any)?.secondaryColor || '#1e40af'}</span>
                          </div>
                        </div>
                      </div>
                      {(selectedClient.brandingConfig as any)?.logoUrl && (
                        <div>
                          <Label className="text-sm font-medium text-gray-500">Logo</Label>
                          <img 
                            src={(selectedClient.brandingConfig as any).logoUrl} 
                            alt="Client logo" 
                            className="mt-2 max-w-32 h-auto border rounded"
                          />
                        </div>
                      )}
                    </div>
                  )}
                </CardContent>
              </Card>
            ) : (
              <Card>
                <CardContent className="py-12 text-center">
                  <Settings className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">No Client Selected</h3>
                  <p className="text-gray-500">Select a client from the list to view and edit their configuration.</p>
                </CardContent>
              </Card>
            )}
          </div>
        </div>
      </main>
    </div>
  );
}

client/src/components/ai-chat/AIChatInte.content:
import { useState, useRef, useEffect } from "react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Bot, User, Send, Sparkles, Settings, MessageCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
// import CampaignModal from "@/components/campaign/CampaignModal";

interface ChatMessage {
  id: string;
  content: string;
  isFromAI: boolean;
  timestamp: Date;
}

interface CampaignData {
  name: string;
  context: string;
  handoverGoals: string;
  numberOfTemplates: number;
  daysBetweenMessages: number;
}

export default function AIChatInterface() {
  const { toast } = useToast();
  const [messages, setMessages] = useState<ChatMessage[]>([
    {
      id: "welcome",
      content: "Hi! I'm your AI Campaign Agent for automotive marketing. I'll help you create a personalized email campaign by asking a few questions. What type of automotive campaign would you like to create today?",
      isFromAI: true,
      timestamp: new Date(),
    }
  ]);

  const [currentMessage, setCurrentMessage] = useState("");
  const [campaignData, setCampaignData] = useState<Partial<CampaignData>>({});
  const [currentStep, setCurrentStep] = useState("context");
  const [isAdvancedMode, setIsAdvancedMode] = useState(false);
  const [suggestions, setSuggestions] = useState<string[]>([]);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // AI Chat mutation
  const chatMutation = useMutation({
    mutationFn: async (userMessage: string) => {
      const response = await fetch("/api/ai/chat-campaign", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          message: userMessage,
          currentStep,
          campaignData,
        }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      return response.json();
    },
    onSuccess: (response: any) => {
      const aiMessage: ChatMessage = {
        id: Date.now().toString(),
        content: response.message || "I understand. Let me help you with that.",
        isFromAI: true,
        timestamp: new Date(),
      };

      setMessages(prev => [...prev, aiMessage]);

      if (response.campaignData) {
        setCampaignData(prev => ({ ...prev, ...response.campaignData }));
      }

      if (response.nextStep) {
        setCurrentStep(response.nextStep);
      }

      // Update suggestions from server
      if (response.suggestions) {
        setSuggestions(response.suggestions);
      }

      // Update progress from server if provided
      if (response.progress) {
        // Use server-provided progress for accuracy
      }

      if (response.isComplete) {
        // Campaign is ready to be created
        toast({
          title: "Campaign Ready!",
          description: "Your campaign information has been collected. Ready to generate templates?",
        });
      }
    },
    onError: () => {
      const errorMessage: ChatMessage = {
        id: Date.now().toString(),
        content: "I'm having trouble processing that. Could you try rephrasing your request?",
        isFromAI: true,
        timestamp: new Date(),
      };
      setMessages(prev => [...prev, errorMessage]);
    },
  });

  const handleSendMessage = (e: React.FormEvent) => {
    e.preventDefault();
    if (!currentMessage.trim()) return;

    const userMessage: ChatMessage = {
      id: Date.now().toString(),
      content: currentMessage,
      isFromAI: false,
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    chatMutation.mutate(currentMessage);
    setCurrentMessage("");
  };

  const handleSuggestionClick = (suggestion: string) => {
    setCurrentMessage(suggestion);
    // Auto-send the suggestion
    const userMessage: ChatMessage = {
      id: Date.now().toString(),
      content: suggestion,
      isFromAI: false,
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    chatMutation.mutate(suggestion);
  };

  const getStepIndicator = () => {
    const steps = ["context", "goals", "target_audience", "name", "handover_criteria", "email_templates"];
    const currentIndex = steps.indexOf(currentStep);
    const progress = ((currentIndex + 1) / steps.length) * 100;

    return (
      <div className="mb-4">
        <div className="flex justify-between text-xs text-gray-500 mb-1">
          <span>Campaign Setup Progress</span>
          <span>{Math.round(progress)}%</span>
        </div>
        <div className="w-full bg-gray-200 rounded-full h-2">
          <div 
            className="bg-blue-500 h-2 rounded-full transition-all duration-300"
            style={{ width: `${progress}%` }}
          ></div>
        </div>
      </div>
    );
  };

  return (
    <div className="max-w-4xl mx-auto">
      <div className="flex justify-between items-center mb-6">
        <div>
          <h2 className="text-2xl font-bold text-gray-900 flex items-center">
            <Bot className="w-8 h-8 text-blue-600 mr-3" />
            AI Campaign Agent
          </h2>
          <p className="text-gray-600 mt-1">Let's create your automotive email campaign together</p>
        </div>

        <div className="flex space-x-2">
          <Button
            variant="outline"
            size="sm"
            onClick={() => setIsAdvancedMode(true)}
            className="flex items-center"
          >
            <Settings className="w-4 h-4 mr-2" />
            Advanced Mode
          </Button>
        </div>
      </div>

      <Card className="h-[600px] flex flex-col">
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between">
            <CardTitle className="text-lg flex items-center">
              <MessageCircle className="w-5 h-5 mr-2 text-blue-600" />
              Campaign Chat
            </CardTitle>
            <div className="flex items-center space-x-2 text-sm text-gray-500">
              <Sparkles className="w-4 h-4" />
              <span>AI-Powered</span>
            </div>
          </div>
          {currentStep !== "complete" && getStepIndicator()}
        </CardHeader>

        <CardContent className="flex-1 flex flex-col p-0">
          {/* Messages */}
          <div className="flex-1 overflow-y-auto p-4 space-y-4">
            {messages.map((message) => (
              <div
                key={message.id}
                className={`flex ${message.isFromAI ? "justify-start" : "justify-end"}`}
              >
                <div className={`flex items-start space-x-2 max-w-xs lg:max-w-md`}>
                  {message.isFromAI && (
                    <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center flex-shrink-0">
                      <Bot className="w-5 h-5 text-blue-600" />
                    </div>
                  )}

                  <div
                    className={`px-4 py-2 rounded-lg ${
                      message.isFromAI
                        ? "bg-gray-100 text-gray-900"
                        : "bg-blue-500 text-white"
                    }`}
                  >
                    <p className="text-sm whitespace-pre-wrap">{message.content}</p>
                    <p className="text-xs mt-1 opacity-70">
                      {message.timestamp.toLocaleTimeString()}
                    </p>
                  </div>

                  {!message.isFromAI && (
                    <div className="w-8 h-8 bg-gray-100 rounded-full flex items-center justify-center flex-shrink-0">
                      <User className="w-5 h-5 text-gray-600" />
                    </div>
                  )}
                </div>
              </div>
            ))}

            {chatMutation.isPending && (
              <div className="flex justify-start">
                <div className="flex items-start space-x-2">
                  <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center">
                    <Bot className="w-5 h-5 text-blue-600" />
                  </div>
                  <div className="bg-gray-100 text-gray-900 px-4 py-2 rounded-lg">
                    <div className="flex space-x-1">
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: "0.1s" }}></div>
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: "0.2s" }}></div>
                    </div>
                  </div>
                </div>
              </div>
            )}

            <div ref={messagesEndRef} />
          </div>

          {/* Quick Suggestions */}
          {suggestions.length > 0 && (
            <div className="border-t px-4 py-3">
              <div className="text-xs text-gray-500 mb-2">Quick responses:</div>
              <div className="flex flex-wrap gap-2">
                {suggestions.map((suggestion, index) => (
                  <button
                    key={index}
                    onClick={() => handleSuggestionClick(suggestion)}
                    className="px-3 py-1 text-xs bg-gray-100 hover:bg-gray-200 rounded-full border transition-colors"
                    disabled={chatMutation.isPending}
                  >
                    {suggestion}
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* Input */}
          <div className="border-t p-4">
            <form onSubmit={handleSendMessage} className="flex space-x-2">
              <Input
                value={currentMessage}
                onChange={(e) => setCurrentMessage(e.target.value)}
                placeholder="Type your message..."
                className="flex-1"
                disabled={chatMutation.isPending}
              />
              <Button 
                type="submit" 
                disabled={chatMutation.isPending || !currentMessage.trim()}
                className="flex items-center"
              >
                <Send className="w-4 h-4" />
              </Button>
            </form>
          </div>
        </CardContent>
      </Card>

      {/* Campaign Data Summary */}
      {Object.keys(campaignData).length > 0 && (
        <Card className="mt-4">
          <CardHeader>
            <CardTitle className="text-sm">Campaign Information Collected</CardTitle>
          </CardHeader>
          <CardContent className="text-sm">
            <div className="grid grid-cols-2 gap-4">
              {campaignData.name && (
                <div>
                  <span className="font-medium text-gray-600">Campaign Name:</span>
                  <p className="text-gray-900">{campaignData.name}</p>
                </div>
              )}
              {campaignData.context && (
                <div>
                  <span className="font-medium text-gray-600">Campaign Type:</span>
                  <p className="text-gray-900">{campaignData.context}</p>
                </div>
              )}
              {(campaignData.numberOfTemplates || (campaignData as any).templateCount) && (
                <div>
                  <span className="font-medium text-gray-600">Number of Templates:</span>
                  <p className="text-gray-900">{campaignData.numberOfTemplates || (campaignData as any).templateCount}</p>
                </div>
              )}
              {(campaignData as any).targetAudience && (
                <div>
                  <span className="font-medium text-gray-600">Target Audience:</span>
                  <p className="text-gray-900">{(campaignData as any).targetAudience}</p>
                </div>
              )}
              {(campaignData as any).handoverGoals && (
                <div>
                  <span className="font-medium text-gray-600">Handover Goals:</span>
                  <p className="text-gray-900">{(campaignData as any).handoverGoals}</p>
                </div>
              )}
              {campaignData.daysBetweenMessages && (
                <div>
                  <span className="font-medium text-gray-600">Days Between Messages:</span>
                  <p className="text-gray-900">{campaignData.daysBetweenMessages}</p>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Advanced Mode Modal - Coming Soon */}
      {isAdvancedMode && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-md mx-4">
            <h3 className="text-lg font-bold mb-4">Advanced Mode</h3>
            <p className="text-gray-600 mb-4">
              Advanced form-based campaign creation will be available soon. 
              For now, please use the AI chat interface above.
            </p>
            <Button onClick={() => setIsAdvancedMode(false)}>
              Continue with AI Chat
            </Button>
          </div>
        </div>
      )}
    </div>
  );
}

client/src/components/campaign/CampaignF.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { insertCampaignSchema } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Lightbulb, Sparkles, Mail, Type, MessageSquare, CalendarDays } from "lucide-react";
import { SMSIntegration } from "@/components/SMSIntegration";
import { CampaignScheduler } from "@/components/CampaignScheduler";
import { z } from "zod";

const formSchema = insertCampaignSchema.extend({
  name: z.string().min(1, "Campaign name is required"),
  context: z.string().min(10, "Please provide more detailed context"),
}).omit({ 
  createdAt: true, 
  updatedAt: true, 
  id: true,
  openRate: true,
  templates: true,
  subjectLines: true
});

type FormData = z.infer<typeof formSchema>;

interface CampaignFormProps {
  onClose: () => void;
  currentStep: number;
  onStepChange: (step: number) => void;
}

export default function CampaignForm({ onClose, currentStep, onStepChange }: CampaignFormProps) {
  const [aiSuggestions, setAiSuggestions] = useState<string[]>([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [nameSuggestions, setNameSuggestions] = useState<string[]>([]);
  const [showNameSuggestions, setShowNameSuggestions] = useState(false);
  const [generatedTemplates, setGeneratedTemplates] = useState<string[]>([]);
  const [numberOfTemplates, setNumberOfTemplates] = useState(5);
  const [daysBetweenMessages, setDaysBetweenMessages] = useState(3);
  const [communicationType, setCommunicationType] = useState<'email' | 'email_sms' | 'sms'>('email');
  const [createdCampaignId, setCreatedCampaignId] = useState<string | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<FormData>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      name: "",
      context: "",
      handoverGoals: "",
      status: "draft",
      numberOfTemplates: 5,
      daysBetweenMessages: 3,
      communicationType: "email",
      scheduleType: "immediate",
      isTemplate: false,
    },
  });

  const createCampaign = useMutation({
    mutationFn: (data: FormData) => apiRequest('/api/campaigns', 'POST', data),
    onSuccess: (response: any) => {
      queryClient.invalidateQueries({ queryKey: ['/api/campaigns'] });
      toast({ title: "Campaign created successfully!" });
      setCreatedCampaignId(response.id);
      onStepChange(2); // Move to next step for scheduling
    },
    onError: () => {
      toast({ title: "Failed to create campaign", variant: "destructive" });
    },
  });

  const generateGoals = useMutation({
    mutationFn: (context: string) => 
      apiRequest('/api/ai/suggest-goals', 'POST', { context }),
    onSuccess: (response: any) => {
      const goals = response.json?.goals || [];
      setAiSuggestions(goals);
      setShowSuggestions(true);
    },
    onError: () => {
      toast({ title: "Failed to generate AI suggestions", variant: "destructive" });
    },
  });

  const enhanceTemplates = useMutation({
    mutationFn: ({ context, name }: { context: string; name: string }) =>
      apiRequest('/api/ai/enhance-templates', 'POST', { context, name }),
    onSuccess: () => {
      toast({ title: "Templates enhanced with AI!" });
      onStepChange(3);
    },
    onError: () => {
      toast({ title: "Failed to enhance templates", variant: "destructive" });
    },
  });

  const generateSubjects = useMutation({
    mutationFn: ({ context, name }: { context: string; name: string }) =>
      apiRequest('POST', '/api/ai/generate-subjects', { context, name }),
    onSuccess: () => {
      toast({ title: "Subject lines generated!" });
    },
    onError: () => {
      toast({ title: "Failed to generate subject lines", variant: "destructive" });
    },
  });

  const generateNames = useMutation({
    mutationFn: (context: string) => 
      apiRequest('POST', '/api/ai/suggest-names', { context }),
    onSuccess: (response: any) => {
      const names = response.json?.names || [];
      setNameSuggestions(names);
      setShowNameSuggestions(true);
    },
    onError: () => {
      toast({ title: "Failed to generate campaign names", variant: "destructive" });
    },
  });

  const generateTemplates = useMutation({
    mutationFn: ({ context, name, numberOfTemplates }: { context: string; name: string; numberOfTemplates: number }) =>
      apiRequest('POST', '/api/ai/generate-templates', { context, name, numberOfTemplates }),
    onSuccess: (response: any) => {
      const templates = response.json?.templates || [];
      setGeneratedTemplates(templates);
      toast({ title: `Generated ${templates.length} email templates!` });
    },
    onError: () => {
      toast({ title: "Failed to generate email templates", variant: "destructive" });
    },
  });

  const onSubmit = (data: FormData) => {
    const campaignData = {
      ...data,
      templates: generatedTemplates,
      numberOfTemplates,
      daysBetweenMessages,
    };
    createCampaign.mutate(campaignData);
  };

  const contextSuggestions = [
    "Promote the latest SUV models with special financing offers",
    "Highlight seasonal service offers and maintenance packages", 
    "Announce upcoming dealership events and test drive opportunities"
  ];

  const fillContextSuggestion = (suggestion: string) => {
    form.setValue('context', suggestion);
  };

  const handleSuggestGoals = () => {
    const context = form.getValues('context');
    if (context.length < 10) {
      toast({ title: "Please provide more campaign context first", variant: "destructive" });
      return;
    }
    generateGoals.mutate(context);
  };

  const selectGoal = (goal: string) => {
    form.setValue('handoverGoals', goal);
    setShowSuggestions(false);
  };

  const handleSuggestNames = () => {
    const context = form.getValues('context');
    if (context.length < 10) {
      toast({ title: "Please provide campaign context first", variant: "destructive" });
      return;
    }
    generateNames.mutate(context);
  };

  const selectName = (name: string) => {
    form.setValue('name', name);
    setShowNameSuggestions(false);
  };

  const handleGenerateTemplates = () => {
    const context = form.getValues('context');
    const name = form.getValues('name');

    if (!context || !name) {
      toast({ title: "Please fill in campaign name and context first", variant: "destructive" });
      return;
    }

    generateTemplates.mutate({ context, name, numberOfTemplates });
  };

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
        {/* AI Tips */}
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
          <div className="flex items-start space-x-3">
            <div className="w-8 h-8 bg-blue-100 rounded-lg flex items-center justify-center flex-shrink-0">
              <Lightbulb className="w-4 h-4 text-blue-600" />
            </div>
            <div>
              <h4 className="text-sm font-semibold text-blue-900 mb-1">AI Campaign Tips</h4>
              <p className="text-sm text-blue-700 leading-relaxed">
                Be specific about vehicle models, services, or offers. Include target audience details and clear objectives for best AI-generated content.
              </p>
            </div>
          </div>
        </div>

        {/* Campaign Name Field */}
        <FormField
          control={form.control}
          name="name"
          render={({ field }) => (
            <FormItem>
              <div className="flex items-center justify-between">
                <FormLabel>Campaign Name</FormLabel>
                <Button
                  type="button"
                  variant="outline"
                  size="sm"
                  onClick={handleSuggestNames}
                  disabled={generateNames.isPending || form.getValues('context').length < 10}
                  className="flex items-center space-x-1"
                >
                  {generateNames.isPending ? (
                    <>
                      <div className="w-3 h-3 border border-gray-400 border-t-transparent rounded-full animate-spin" />
                      <span>Generating...</span>
                    </>
                  ) : (
                    <>
                      <Sparkles className="w-3 h-3" />
                      <span>AI Suggest</span>
                    </>
                  )}
                </Button>
              </div>
              <FormControl>
                <Input 
                  placeholder="e.g., 2024 Holiday Sales Event" 
                  {...field} 
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* AI Name Suggestions */}
        {showNameSuggestions && nameSuggestions.length > 0 && (
          <div className="bg-gray-50 border border-gray-200 rounded-lg p-4">
            <h4 className="text-sm font-semibold text-gray-900 mb-3 flex items-center">
              <Sparkles className="w-4 h-4 mr-2 text-blue-600" />
              AI Campaign Name Suggestions
            </h4>
            <div className="space-y-2">
              {nameSuggestions.map((name, index) => (
                <button
                  key={index}
                  type="button"
                  onClick={() => selectName(name)}
                  className="w-full text-left p-3 bg-white hover:bg-blue-50 border border-gray-200 hover:border-blue-300 rounded-lg transition-colors text-sm text-gray-700 hover:text-blue-900"
                >
                  {name}
                </button>
              ))}
            </div>
            <button
              type="button"
              onClick={() => setShowNameSuggestions(false)}
              className="mt-3 text-xs text-gray-500 hover:text-gray-700"
            >
              Hide suggestions
            </button>
          </div>
        )}

        {/* Campaign Context Field */}
        <FormField
          control={form.control}
          name="context"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Campaign Context</FormLabel>
              <FormControl>
                <Textarea
                  rows={4}
                  placeholder="Describe your campaign goals, target audience, and key messaging..."
                  className="resize-none"
                  {...field}
                />
              </FormControl>

              {/* Context Suggestions */}
              <div className="mt-3">
                <p className="text-sm text-gray-600 mb-2">Quick suggestions for automotive campaigns:</p>
                <div className="flex flex-wrap gap-2">
                  {contextSuggestions.map((suggestion, index) => (
                    <button
                      key={index}
                      type="button"
                      onClick={() => fillContextSuggestion(suggestion)}
                      className="px-3 py-1 text-xs bg-gray-100 text-gray-700 rounded-full hover:bg-gray-200 transition-colors"
                    >
                      {suggestion.slice(0, 30) + (suggestion.length > 30 ? '...' : '')}
                    </button>
                  ))}
                </div>
              </div>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Handover Goals Field */}
        <FormField
          control={form.control}
          name="handoverGoals"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Handover Goals</FormLabel>
              <div className="flex space-x-3">
                <FormControl>
                  <Input 
                    placeholder="Define your campaign objectives..."
                    className="flex-1"
                    {...field}
                    value={field.value || ""}
                  />
                </FormControl>
                <Button
                  type="button"
                  variant="outline"
                  onClick={handleSuggestGoals}
                  disabled={generateGoals.isPending}
                  className="flex items-center space-x-2 border-blue-200 text-blue-700 hover:bg-blue-50 hover:border-blue-300"
                >
                  <Sparkles className="w-4 h-4" />
                  <span>AI Suggest</span>
                </Button>
              </div>

              {/* Goal Suggestions */}
              {showSuggestions && (
                <div className="mt-3">
                  <p className="text-sm text-gray-600 mb-2">AI suggested goals for automotive campaigns:</p>
                  <div className="space-y-2">
                    {aiSuggestions.map((goal, index) => (
                      <button
                        key={index}
                        type="button"
                        onClick={() => selectGoal(goal)}
                        className="w-full text-left px-3 py-2 text-sm bg-blue-50 text-blue-800 rounded-lg hover:bg-blue-100 transition-colors border border-blue-100"
                      >
                        {goal}
                      </button>
                    ))}
                  </div>
                </div>
              )}
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Email Template Generation Section */}
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-6">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-blue-100 rounded-lg flex items-center justify-center">
                <Mail className="w-4 h-4 text-blue-600" />
              </div>
              <div>
                <h3 className="text-lg font-semibold text-blue-900">AI Email Templates</h3>
                <p className="text-sm text-blue-700">Generate email templates automatically based on your offer details and campaign goals.</p>
              </div>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Number of Messages
              </label>
              <Input
                type="number"
                min="1"
                max="30"
                value={numberOfTemplates}
                onChange={(e) => setNumberOfTemplates(parseInt(e.target.value) || 5)}
                className="w-full"
              />
              <p className="text-xs text-gray-500 mt-1">Number of templated emails to send (if no response)</p>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Days Between Messages
              </label>
              <Input
                type="number"
                min="1"
                max="30"
                value={daysBetweenMessages}
                onChange={(e) => setDaysBetweenMessages(parseInt(e.target.value) || 3)}
                className="w-full"
              />
              <p className="text-xs text-gray-500 mt-1">Wait time between each templated email</p>
            </div>
          </div>

          {generatedTemplates.length === 0 ? (
            <div className="text-center py-8">
              <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <Mail className="w-8 h-8 text-gray-400" />
              </div>
              <p className="text-gray-500 mb-4">No templates generated yet</p>
              <Button
                type="button"
                onClick={handleGenerateTemplates}
                disabled={generateTemplates.isPending}
                className="bg-blue-600 hover:bg-blue-700 text-white"
              >
                {generateTemplates.isPending ? (
                  <>
                    <div className="w-4 h-4 border border-white border-t-transparent rounded-full animate-spin mr-2" />
                    Generating Templates...
                  </>
                ) : (
                  <>
                    <Sparkles className="w-4 h-4 mr-2" />
                    Generate {numberOfTemplates} Email Templates
                  </>
                )}
              </Button>
            </div>
          ) : (
            <div>
              <div className="flex items-center justify-between mb-3">
                <p className="text-sm font-medium text-gray-900">
                  Generated {generatedTemplates.length} email templates
                </p>
                <Button
                  type="button"
                  variant="outline"
                  size="sm"
                  onClick={handleGenerateTemplates}
                  disabled={generateTemplates.isPending}
                >
                  Regenerate
                </Button>
              </div>
              <div className="space-y-3 max-h-60 overflow-y-auto">
                {generatedTemplates.map((template, index) => (
                  <div key={index} className="bg-white border border-gray-200 rounded-lg p-3">
                    <div className="flex items-center justify-between mb-2">
                      <span className="text-sm font-medium text-gray-900">Email {index + 1}</span>
                      <span className="text-xs text-gray-500">{template.length} characters</span>
                    </div>
                    <div className="text-xs text-gray-600 line-clamp-3">
                      {template.replace(/<[^>]*>/g, '').substring(0, 120)}...
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mt-4">
            <div className="flex items-start space-x-3">
              <div className="w-5 h-5 bg-blue-100 rounded flex items-center justify-center flex-shrink-0 mt-0.5">
                <Lightbulb className="w-3 h-3 text-blue-600" />
              </div>
              <div>
                <h4 className="text-sm font-semibold text-blue-900 mb-1">AI Response Mode</h4>
                <p className="text-sm text-blue-700 leading-relaxed">
                  When a lead replies to any email, the remaining templated emails are cancelled. The AI agent takes over for personalized back-and-forth conversation until handover.
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* AI Enhancement Section */}
        <div className="border-t border-gray-200 pt-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">AI Enhancement Options</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <button
              type="button"
              onClick={() => {
                const context = form.getValues('context');
                const name = form.getValues('name');
                if (!context || !name) {
                  toast({ title: "Please fill in campaign name and context first", variant: "destructive" });
                  return;
                }
                enhanceTemplates.mutate({ context, name });
              }}
              disabled={enhanceTemplates.isPending}
              className="p-4 border border-gray-200 rounded-lg hover:border-blue-300 hover:bg-blue-50 transition-all text-left"
            >
              <div className="flex items-center space-x-3 mb-2">
                <div className="w-8 h-8 bg-blue-100 rounded-lg flex items-center justify-center">
                  <Mail className="w-4 h-4 text-blue-600" />
                </div>
                <span className="font-medium text-gray-900">AI Enhance Templates</span>
              </div>
              <p className="text-sm text-gray-600">
                Generate automotive email templates with vehicle showcases, service reminders, and personalized follow-ups
              </p>
            </button>

            <button
              type="button"
              onClick={() => {
                const context = form.getValues('context');
                const name = form.getValues('name');
                if (!context || !name) {
                  toast({ title: "Please fill in campaign name and context first", variant: "destructive" });
                  return;
                }
                generateSubjects.mutate({ context, name });
              }}
              disabled={generateSubjects.isPending}
              className="p-4 border border-gray-200 rounded-lg hover:border-green-300 hover:bg-green-50 transition-all text-left"
            >
              <div className="flex items-center space-x-3 mb-2">
                <div className="w-8 h-8 bg-green-100 rounded-lg flex items-center justify-center">
                  <Type className="w-4 h-4 text-green-600" />
                </div>
                <span className="font-medium text-gray-900">Generate Subject Lines</span>
              </div>
              <p className="text-sm text-gray-600">
                Create compelling subject lines optimized for automotive campaigns and customer engagement
              </p>
            </button>
          </div>
        </div>

        {/* Form Footer */}
        <div className="flex items-center justify-between pt-6 border-t border-gray-200">
          <Button type="button" variant="ghost" onClick={onClose}>
            Cancel
          </Button>
          <div className="flex space-x-3">
            <Button 
              type="submit" 
              variant="outline"
              disabled={createCampaign.isPending}
              onClick={() => form.setValue('status', 'draft')}
            >
              Save Draft
            </Button>
            <Button 
              type="submit"
              disabled={createCampaign.isPending}
              className="bg-blue-600 hover:bg-blue-700"
            >
              {createCampaign.isPending ? 'Creating...' : 'Create Campaign'}
            </Button>
          </div>
        </div>
      </form>
    </Form>
  );
}

