import React, { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Send, Bot, User } from 'lucide-react';
import { useWebSocket } from '@/hooks/useWebSocket';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';

interface Message {
  id: string;
  content: string;
  senderId: string;
  isFromAI: number;
  createdAt: string;
}

interface ConversationMessagingProps {
  conversationId: string;
  userId?: string;
}

export function ConversationMessaging({ conversationId, userId = "current-user" }: ConversationMessagingProps) {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const queryClient = useQueryClient();

  // Load conversation messages
  const { data: initialMessages, isLoading } = useQuery({
    queryKey: ['/api/conversations', conversationId, 'messages'],
    enabled: !!conversationId
  });

  // Initialize messages from API
  useEffect(() => {
    if (initialMessages) {
      setMessages(Array.isArray(initialMessages) ? initialMessages : []);
    }
  }, [initialMessages]);

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // WebSocket connection for real-time messaging
  const { isConnected, sendChatMessage, joinConversation } = useWebSocket({
    onMessage: (message) => {
      if (message.type === 'new_message' && message.message) {
        setMessages(prev => [...prev, message.message]);
      } else if (message.type === 'conversation_history') {
        setMessages(message.messages || []);
      }
    },
    onConnect: () => {
      // Join the conversation room when connected
      joinConversation(conversationId, userId);
    }
  });

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async (content: string) => {
      return apiRequest(`/api/conversations/${conversationId}/messages`, 'POST', {
        content,
        senderId: userId,
        isFromAI: 0
      });
    },
    onSuccess: (newMessage) => {
      // Update UI immediately if WebSocket is not connected
      if (!isConnected && newMessage) {
        setMessages(prev => [...prev, newMessage as Message]);
      }

      // Invalidate conversation queries
      queryClient.invalidateQueries({ queryKey: ['/api/conversations'] });
    }
  });

  const handleSendMessage = async () => {
    if (!messageInput.trim()) return;

    const messageContent = messageInput.trim();
    setMessageInput('');

    try {
      if (isConnected) {
        // Send via WebSocket for real-time delivery
        sendChatMessage(messageContent);
      } else {
        // Fallback to API if WebSocket is not connected
        sendMessageMutation.mutate(messageContent);
      }
    } catch (error) {
      console.error('Failed to send message:', error);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  if (isLoading) {
    return (
      <Card>
        <CardContent className="p-6">
          <div className="flex items-center justify-center">
            <div className="text-muted-foreground">Loading conversation...</div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="h-[600px] flex flex-col">
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <CardTitle>Live Conversation</CardTitle>
          <div className="flex items-center gap-2">
            <Badge variant={isConnected ? "default" : "secondary"}>
              {isConnected ? "Connected" : "Offline"}
            </Badge>
          </div>
        </div>
      </CardHeader>

      <CardContent className="flex flex-col flex-1 p-6 pt-0">
        {/* Messages Area */}
        <ScrollArea className="flex-1 mb-4 border rounded-lg p-4">
          <div className="space-y-4">
            {messages.length === 0 ? (
              <div className="text-center text-muted-foreground py-8">
                No messages yet. Start the conversation!
              </div>
            ) : (
              messages.map((message) => (
                <div
                  key={message.id}
                  className={`flex items-start gap-3 ${
                    message.isFromAI ? 'justify-start' : 'justify-end'
                  }`}
                >
                  {message.isFromAI && (
                    <div className="w-8 h-8 rounded-full bg-blue-100 flex items-center justify-center">
                      <Bot className="w-4 h-4 text-blue-600" />
                    </div>
                  )}

                  <div
                    className={`max-w-[70%] rounded-lg p-3 ${
                      message.isFromAI
                        ? 'bg-gray-100 text-gray-900'
                        : 'bg-blue-600 text-white'
                    }`}
                  >
                    <div className="text-sm">{message.content}</div>
                    <div className={`text-xs mt-1 ${
                      message.isFromAI ? 'text-gray-500' : 'text-blue-100'
                    }`}>
                      {new Date(message.createdAt).toLocaleTimeString()}
                    </div>
                  </div>

                  {!message.isFromAI && (
                    <div className="w-8 h-8 rounded-full bg-blue-600 flex items-center justify-center">
                      <User className="w-4 h-4 text-white" />
                    </div>
                  )}
                </div>
              ))
            )}
            <div ref={messagesEndRef} />
          </div>
        </ScrollArea>

        {/* Message Input */}
        <div className="flex gap-2">
          <Input
            value={messageInput}
            onChange={(e) => setMessageInput(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Type your message..."
            disabled={sendMessageMutation.isPending}
            className="flex-1"
          />
          <Button
            onClick={handleSendMessage}
            disabled={!messageInput.trim() || sendMessageMutation.isPending}
            size="icon"
          >
            <Send className="w-4 h-4" />
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/campaigns/EmailVal.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Separator } from "@/components/ui/separator";
import { apiRequest } from "@/lib/queryClient";
import { 
  Shield, 
  CheckCircle, 
  XCircle, 
  AlertTriangle, 
  Clock,
  BarChart3,
  Eye,
  Zap
} from "lucide-react";

interface EmailValidationResult {
  allowed: boolean;
  blocked: boolean;
  quarantined: boolean;
  requiresApproval: boolean;
  reasons: string[];
  triggeredRules: string[];
  riskScore: number;
}

export default function EmailValidationPanel() {
  const [emailContent, setEmailContent] = useState({
    to: ["test@example.com"],
    subject: "",
    htmlContent: "",
    textContent: ""
  });
  const [validationResult, setValidationResult] = useState<EmailValidationResult | null>(null);

  // Get validation statistics
  const { data: validationStats } = useQuery({
    queryKey: ["/api/email/validation-stats"],
  });

  // Validate email content mutation
  const validateContentMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest("/api/email/validate-content", "POST", data);
    },
    onSuccess: (result) => {
      setValidationResult(result);
    },
  });

  const handleValidate = () => {
    if (emailContent.subject && emailContent.htmlContent) {
      validateContentMutation.mutate(emailContent);
    }
  };

  const getRiskScoreColor = (score: number) => {
    if (score >= 70) return "text-red-600 bg-red-50 border-red-200";
    if (score >= 40) return "text-yellow-600 bg-yellow-50 border-yellow-200";
    return "text-green-600 bg-green-50 border-green-200";
  };

  const getStatusIcon = (result: EmailValidationResult) => {
    if (result.blocked) return <XCircle className="h-5 w-5 text-red-500" />;
    if (result.quarantined) return <AlertTriangle className="h-5 w-5 text-yellow-500" />;
    if (result.requiresApproval) return <Clock className="h-5 w-5 text-blue-500" />;
    if (result.allowed) return <CheckCircle className="h-5 w-5 text-green-500" />;
    return <Shield className="h-5 w-5 text-gray-500" />;
  };

  const getStatusText = (result: EmailValidationResult) => {
    if (result.blocked) return "BLOCKED";
    if (result.quarantined) return "QUARANTINED";
    if (result.requiresApproval) return "REQUIRES APPROVAL";
    if (result.allowed) return "APPROVED";
    return "UNKNOWN";
  };

  const getStatusColor = (result: EmailValidationResult) => {
    if (result.blocked) return "bg-red-100 text-red-800 border-red-300";
    if (result.quarantined) return "bg-yellow-100 text-yellow-800 border-yellow-300";
    if (result.requiresApproval) return "bg-blue-100 text-blue-800 border-blue-300";
    if (result.allowed) return "bg-green-100 text-green-800 border-green-300";
    return "bg-gray-100 text-gray-800 border-gray-300";
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center gap-2 mb-4">
        <Shield className="h-6 w-6 text-blue-600" />
        <h2 className="text-2xl font-bold">Email Validation System</h2>
      </div>

      <Tabs defaultValue="test" className="space-y-4">
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="test">Test Email</TabsTrigger>
          <TabsTrigger value="stats">Statistics</TabsTrigger>
          <TabsTrigger value="rules">Validation Rules</TabsTrigger>
        </TabsList>

        <TabsContent value="test" className="space-y-4">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Email Input */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Eye className="h-5 w-5" />
                  Test Email Content
                </CardTitle>
                <CardDescription>
                  Test your email content against validation rules before sending
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label htmlFor="test-email">Test Email Address</Label>
                  <Input
                    id="test-email"
                    value={emailContent.to[0]}
                    onChange={(e) => setEmailContent(prev => ({
                      ...prev,
                      to: [e.target.value]
                    }))}
                    placeholder="test@example.com"
                  />
                </div>

                <div>
                  <Label htmlFor="test-subject">Subject Line</Label>
                  <Input
                    id="test-subject"
                    value={emailContent.subject}
                    onChange={(e) => setEmailContent(prev => ({
                      ...prev,
                      subject: e.target.value
                    }))}
                    placeholder="Enter email subject"
                  />
                </div>

                <div>
                  <Label htmlFor="test-content">Email Content (HTML)</Label>
                  <Textarea
                    id="test-content"
                    value={emailContent.htmlContent}
                    onChange={(e) => setEmailContent(prev => ({
                      ...prev,
                      htmlContent: e.target.value
                    }))}
                    placeholder="Enter email HTML content"
                    rows={8}
                  />
                </div>

                <Button 
                  onClick={handleValidate}
                  disabled={validateContentMutation.isPending || !emailContent.subject || !emailContent.htmlContent}
                  className="w-full"
                >
                  <Shield className="h-4 w-4 mr-2" />
                  {validateContentMutation.isPending ? "Validating..." : "Validate Email"}
                </Button>
              </CardContent>
            </Card>

            {/* Validation Results */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Zap className="h-5 w-5" />
                  Validation Results
                </CardTitle>
                <CardDescription>
                  Real-time validation feedback and recommendations
                </CardDescription>
              </CardHeader>
              <CardContent>
                {!validationResult ? (
                  <div className="text-center py-8">
                    <Shield className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                    <p className="text-gray-500">No validation results yet</p>
                    <p className="text-sm text-gray-400">Enter email content and click validate</p>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {/* Status Overview */}
                    <div className="flex items-center justify-between p-3 border rounded-lg">
                      <div className="flex items-center gap-2">
                        {getStatusIcon(validationResult)}
                        <span className="font-medium">Status</span>
                      </div>
                      <Badge className={`${getStatusColor(validationResult)} border`}>
                        {getStatusText(validationResult)}
                      </Badge>
                    </div>

                    {/* Risk Score */}
                    <div className={`p-3 border rounded-lg ${getRiskScoreColor(validationResult.riskScore)}`}>
                      <div className="flex items-center justify-between">
                        <span className="font-medium">Risk Score</span>
                        <span className="text-xl font-bold">{validationResult.riskScore}/100</span>
                      </div>
                      <div className="w-full bg-gray-200 rounded-full h-2 mt-2">
                        <div 
                          className="h-2 rounded-full transition-all duration-300"
                          style={{ 
                            width: `${validationResult.riskScore}%`,
                            backgroundColor: validationResult.riskScore >= 70 ? '#dc2626' : 
                                           validationResult.riskScore >= 40 ? '#d97706' : '#16a34a'
                          }}
                        />
                      </div>
                    </div>

                    {/* Triggered Rules */}
                    {validationResult.triggeredRules.length > 0 && (
                      <div>
                        <h4 className="font-medium mb-2">Triggered Rules ({validationResult.triggeredRules.length})</h4>
                        <div className="space-y-1">
                          {validationResult.triggeredRules.map((rule, index) => (
                            <Badge key={index} variant="secondary" className="text-xs">
                              {rule}
                            </Badge>
                          ))}
                        </div>
                      </div>
                    )}

                    {/* Reasons */}
                    {validationResult.reasons.length > 0 && (
                      <div>
                        <h4 className="font-medium mb-2">Issues Found ({validationResult.reasons.length})</h4>
                        <div className="space-y-2">
                          {validationResult.reasons.map((reason, index) => (
                            <Alert key={index}>
                              <AlertTriangle className="h-4 w-4" />
                              <AlertDescription className="text-sm">
                                {reason}
                              </AlertDescription>
                            </Alert>
                          ))}
                        </div>
                      </div>
                    )}

                    {/* Success Message */}
                    {validationResult.allowed && validationResult.reasons.length === 0 && (
                      <Alert>
                        <CheckCircle className="h-4 w-4" />
                        <AlertDescription>
                          Email content passes all validation checks and is safe to send.
                        </AlertDescription>
                      </Alert>
                    )}
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        </TabsContent>

        <TabsContent value="stats" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <BarChart3 className="h-5 w-5" />
                Validation Statistics
              </CardTitle>
              <CardDescription>
                Overview of email validation system status and configuration
              </CardDescription>
            </CardHeader>
            <CardContent>
              {!validationStats ? (
                <div className="text-center py-8">
                  <BarChart3 className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-500">Loading validation statistics...</p>
                </div>
              ) : (
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div className="text-center p-4 border rounded-lg">
                    <div className="text-2xl font-bold text-blue-600">{(validationStats as any)?.rulesCount || 0}</div>
                    <div className="text-sm text-gray-600">Total Rules</div>
                  </div>
                  <div className="text-center p-4 border rounded-lg">
                    <div className="text-2xl font-bold text-green-600">{(validationStats as any)?.enabledRules || 0}</div>
                    <div className="text-sm text-gray-600">Active Rules</div>
                  </div>
                  <div className="text-center p-4 border rounded-lg">
                    <div className="text-2xl font-bold text-purple-600">{(validationStats as any)?.spamKeywordsCount || 0}</div>
                    <div className="text-sm text-gray-600">Spam Keywords</div>
                  </div>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="rules" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Validation Rules</CardTitle>
              <CardDescription>
                Active email validation rules and their configurations
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="p-4 border rounded-lg">
                  <h4 className="font-medium flex items-center gap-2">
                    <CheckCircle className="h-4 w-4 text-green-500" />
                    Critical Field Validation
                  </h4>
                  <p className="text-sm text-gray-600 mt-1">
                    Ensures required fields (to, subject, content) are present and valid
                  </p>
                </div>

                <div className="p-4 border rounded-lg">
                  <h4 className="font-medium flex items-center gap-2">
                    <CheckCircle className="h-4 w-4 text-green-500" />
                    Content Completeness Check
                  </h4>
                  <p className="text-sm text-gray-600 mt-1">
                    Validates email content is not empty and checks for unresolved template placeholders
                  </p>
                </div>

                <div className="p-4 border rounded-lg">
                  <h4 className="font-medium flex items-center gap-2">
                    <CheckCircle className="h-4 w-4 text-green-500" />
                    Email Address Validation
                  </h4>
                  <p className="text-sm text-gray-600 mt-1">
                    Validates email address format and checks against blocked domains
                  </p>
                </div>

                <div className="p-4 border rounded-lg">
                  <h4 className="font-medium flex items-center gap-2">
                    <CheckCircle className="h-4 w-4 text-green-500" />
                    Spam Prevention
                  </h4>
                  <p className="text-sm text-gray-600 mt-1">
                    Scans for spam keywords and suspicious content patterns
                  </p>
                </div>

                <div className="p-4 border rounded-lg">
                  <h4 className="font-medium flex items-center gap-2">
                    <CheckCircle className="h-4 w-4 text-green-500" />
                    Bulk Send Limits
                  </h4>
                  <p className="text-sm text-gray-600 mt-1">
                    Enforces recipient limits and requires approval for large campaigns
                  </p>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

server/services/webhook-handler.ts.content:
import { Request, Response } from 'express';
import { storage } from '../storage';
import { liveConversationService } from './live-conversation';
import { InboundEmailService } from './inbound-email';
import { SuppressionManager } from './deliverability/SuppressionManager';

export class WebhookHandler {
  /**
   * Handle Mailgun webhook for inbound emails
   * Endpoint: POST /api/webhooks/mailgun/inbound
   */
  static async handleMailgunInbound(req: Request, res: Response) {
    try {
      await InboundEmailService.handleInboundEmail(req, res);

      // Store Mailgun events in Supermemory for AI recall
      try {
        const event = req.body;
        const { ingestMemory } = await import('./supermemory');
        await ingestMemory('mail_event', {
          event: event.event || 'unknown',
          messageId: event['message-id'],
          recipient: event.recipient,
          timestamp: event.timestamp || new Date().toISOString(),
          eventData: event
        }, {
          clientId: 'default', // TODO: resolve from recipient->lead->clientId
          leadEmail: event.recipient
        });
      } catch (error) {
        console.warn('Failed to store Mailgun event in Supermemory:', error);
      }
    } catch (error) {
      console.error('Mailgun webhook error:', error);
      res.status(500).json({ error: 'Webhook processing failed' });
    }
  }

  /**
   * Handle Mailgun delivery status webhooks
   * Endpoint: POST /api/webhooks/mailgun/events
   */
  static async handleMailgunEvents(req: Request, res: Response) {
    try {
      const event = req.body;

      if (!event.event || !event['message-id']) {
        return res.status(400).json({ error: 'Invalid webhook payload' });
      }

      // Process suppression events first
      await SuppressionManager.processWebhookEvent(event);

      // Process different event types
      switch (event.event) {
        case 'delivered':
          await this.handleEmailDelivered(event);
          break;
        case 'opened':
          await this.handleEmailOpened(event);
          break;
        case 'clicked':
          await this.handleEmailClicked(event);
          break;
        case 'bounced':
        case 'failed':
          await this.handleEmailFailed(event);
          break;
        case 'unsubscribed':
          await this.handleEmailUnsubscribed(event);
          break;
        default:
          console.log('Unhandled Mailgun event:', event.event);
      }

      res.status(200).json({ message: 'Event processed' });
    } catch (error) {
      console.error('Mailgun event processing error:', error);
      res.status(500).json({ error: 'Event processing failed' });
    }
  }

  /**
   * Handle Twilio SMS webhook for inbound messages
   * Endpoint: POST /api/webhooks/twilio/sms
   */
  static async handleTwilioSMS(req: Request, res: Response) {
    try {
      await InboundEmailService.handleInboundSMS(req, res);
    } catch (error) {
      console.error('Twilio SMS webhook error:', error);
      res.status(500).send('<?xml version="1.0" encoding="UTF-8"?><Response><Message>Error processing message</Message></Response>');
    }
  }

  /**
   * Handle campaign execution webhooks (for scheduled campaigns)
   * Endpoint: POST /api/webhooks/campaign/execute
   */
  static async handleCampaignExecution(req: Request, res: Response) {
    try {
      const { campaignId, leadId, templateId, scheduledTime } = req.body;

      if (!campaignId || !leadId) {
        return res.status(400).json({ error: 'Missing required fields' });
      }

      // Get lead and campaign info
      const lead = await storage.getLead(leadId);
      const campaign = await storage.getCampaign(campaignId);

      if (!lead || !campaign) {
        return res.status(404).json({ error: 'Lead or campaign not found' });
      }

      // Send email using the specified template
      const mailgunService = await import('./mailgun');
      await mailgunService.sendCampaignEmail(
        lead.email,
        campaign.name,
        templateId || campaign.emailTemplate,
        {
          leadName: `${lead.firstName || ''} ${lead.lastName || ''}`.trim() || 'Customer',
          vehicleInterest: lead.vehicleInterest,
          dealershipName: 'OneKeel Swarm Demo'
        },
{}
      );

      // Update lead activity
      await storage.updateLead(leadId, {
        status: 'contacted'
      });

      res.status(200).json({ message: 'Campaign executed successfully' });
    } catch (error) {
      console.error('Campaign execution webhook error:', error);
      res.status(500).json({ error: 'Campaign execution failed' });
    }
  }

  private static async handleEmailDelivered(event: any) {
    // Update delivery status
    await this.updateEmailStatus(event['message-id'], 'delivered', {
      deliveredAt: new Date(event.timestamp * 1000),
      recipient: event.recipient
    });
  }

  private static async handleEmailOpened(event: any) {
    // Track email open
    await this.updateEmailStatus(event['message-id'], 'opened', {
      openedAt: new Date(event.timestamp * 1000),
      userAgent: event['user-agent'],
      clientInfo: event['client-info']
    });

    // Update lead engagement score
    if (event.recipient) {
      const lead = await storage.getLeadByEmail(event.recipient);
      if (lead) {
        // Note: qualificationScore field doesn't exist in Lead schema
        console.log(`Email opened by lead: ${lead.email}`);
      }
    }
  }

  private static async handleEmailClicked(event: any) {
    // Track email click
    await this.updateEmailStatus(event['message-id'], 'clicked', {
      clickedAt: new Date(event.timestamp * 1000),
      clickedUrl: event.url,
      userAgent: event['user-agent']
    });

    // Higher engagement score for clicks
    if (event.recipient) {
      const lead = await storage.getLeadByEmail(event.recipient);
      if (lead) {
        // Note: qualificationScore field doesn't exist in Lead schema
        console.log(`Email link clicked by lead: ${lead.email}`);
      }
    }
  }

  private static async handleEmailFailed(event: any) {
    // Track email failure
    await this.updateEmailStatus(event['message-id'], 'failed', {
      failedAt: new Date(event.timestamp * 1000),
      reason: event.reason,
      description: event.description
    });

    // Mark lead as having delivery issues
    if (event.recipient) {
      const lead = await storage.getLeadByEmail(event.recipient);
      if (lead) {
        await storage.updateLead(lead.id, {
          status: 'delivery_failed',
          notes: `Email delivery failed: ${event.reason}`
        });
      }
    }
  }

  private static async handleEmailUnsubscribed(event: any) {
    // Mark lead as unsubscribed
    if (event.recipient) {
      const lead = await storage.getLeadByEmail(event.recipient);
      if (lead) {
        await storage.updateLead(lead.id, {
          status: 'unsubscribed'
        });
      }
    }
  }

  private static async updateEmailStatus(messageId: string, status: string, metadata: any) {
    // This would update the email/communication record
    // For now, we'll just log it since we don't have a communications table in storage
    console.log(`Email ${messageId} status: ${status}`, metadata);
  }

  /**
   * Test webhook endpoint for development
   * Endpoint: POST /api/webhooks/test
   */
  static async handleTest(req: Request, res: Response) {
    try {
      const { type, payload } = req.body;

      console.log(`Test webhook received - Type: ${type}`, payload);

      res.status(200).json({
        message: 'Test webhook received',
        timestamp: new Date().toISOString(),
        type,
        payload
      });
    } catch (error) {
      console.error('Test webhook error:', error);
      res.status(500).json({ error: 'Test webhook failed' });
    }
  }
}

server/services/websocket.ts.content:
import { WebSocketServer, WebSocket } from 'ws';
import type { Server } from 'http';
import { storage } from '../storage';

interface ClientConnection {
  ws: WebSocket;
  userId?: string;
  conversationId?: string;
}

class WebSocketService {
  private wss: WebSocketServer | null = null;
  private clients: Map<string, ClientConnection> = new Map();

  initialize(server: Server) {
    this.wss = new WebSocketServer({ 
      server,
      path: '/ws',
      perMessageDeflate: false
    });

    this.wss.on('connection', (ws: WebSocket, req) => {
      const clientId = this.generateClientId();
      this.clients.set(clientId, { ws });

      console.log(`WebSocket client connected: ${clientId}`);

      ws.on('message', async (data) => {
        try {
          const message = JSON.parse(data.toString());
          await this.handleMessage(clientId, message);
        } catch (error) {
          console.error('WebSocket message error:', error);
          ws.send(JSON.stringify({ type: 'error', message: 'Invalid message format' }));
        }
      });

      ws.on('close', () => {
        console.log(`WebSocket client disconnected: ${clientId}`);
        this.clients.delete(clientId);
      });

      ws.on('error', (error) => {
        console.error('WebSocket error:', error);
        this.clients.delete(clientId);
      });

      // Send welcome message
      ws.send(JSON.stringify({
        type: 'connected',
        clientId,
        timestamp: new Date().toISOString()
      }));
    });

    console.log('âœ… WebSocket server initialized on /ws');
  }

  private generateClientId(): string {
    return `client_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private async handleMessage(clientId: string, message: any) {
    const client = this.clients.get(clientId);
    if (!client) return;

    switch (message.type) {
      case 'join_conversation':
        await this.handleJoinConversation(clientId, message.conversationId, message.userId);
        break;
      case 'send_message':
        await this.handleSendMessage(clientId, message);
        break;
      case 'ping':
        client.ws.send(JSON.stringify({ type: 'pong', timestamp: new Date().toISOString() }));
        break;
      default:
        client.ws.send(JSON.stringify({ type: 'error', message: 'Unknown message type' }));
    }
  }

  private async handleJoinConversation(clientId: string, conversationId: string, userId: string) {
    const client = this.clients.get(clientId);
    if (!client) return;

    client.conversationId = conversationId;
    client.userId = userId;

    // Send conversation history
    try {
      const messages = await storage.getConversationMessages(conversationId);
      client.ws.send(JSON.stringify({
        type: 'conversation_history',
        conversationId,
        messages: messages.reverse() // Show oldest first
      }));
    } catch (error) {
      console.error('Error loading conversation history:', error);
      client.ws.send(JSON.stringify({
        type: 'error',
        message: 'Failed to load conversation history'
      }));
    }
  }

  private async handleSendMessage(clientId: string, message: any) {
    const client = this.clients.get(clientId);
    if (!client || !client.conversationId || !client.userId) {
      client?.ws.send(JSON.stringify({
        type: 'error',
        message: 'Not joined to a conversation'
      }));
      return;
    }

    try {
      // Save message to database
      const newMessage = await storage.createConversationMessage({
        conversationId: client.conversationId,
        content: message.content,
        senderId: client.userId,
        isFromAI: 0
      });

      // Broadcast to all clients in this conversation
      this.broadcastToConversation(client.conversationId, {
        type: 'new_message',
        message: newMessage
      });

    } catch (error) {
      console.error('Error sending message:', error);
      client.ws.send(JSON.stringify({
        type: 'error',
        message: 'Failed to send message'
      }));
    }
  }

  // Public methods for external services
  broadcastNewLead(lead: any) {
    this.internalBroadcast({
      type: 'new_lead',
      lead
    });
  }

  broadcastNewConversation(conversation: any) {
    this.internalBroadcast({
      type: 'new_conversation',
      conversation
    });
  }

  broadcastToConversation(conversationId: string, data: any) {
    this.clients.forEach((client) => {
      if (client.conversationId === conversationId && client.ws.readyState === WebSocket.OPEN) {
        client.ws.send(JSON.stringify(data));
      }
    });
  }

  broadcast(type: string, data: any) {
    const message = { type, ...data };
    this.clients.forEach((client) => {
      if (client.ws.readyState === WebSocket.OPEN) {
        client.ws.send(JSON.stringify(message));
      }
    });
  }

  private internalBroadcast(data: any) {
    this.clients.forEach((client) => {
      if (client.ws.readyState === WebSocket.OPEN) {
        client.ws.send(JSON.stringify(data));
      }
    });
  }

  getConnectedClients(): number {
    return this.clients.size;
  }
}

export const webSocketService = new WebSocketService();

server/services/campaign-execution/Campa.content:
import { storage } from '../../storage';
import { webSocketService } from '../websocket';
import { userNotificationService } from '../user-notification';

export interface CampaignExecutionOptions {
  campaignId: string;
  testMode?: boolean;
  scheduleAt?: Date;
  selectedLeadIds?: string[];
  maxLeadsPerBatch?: number;
}

export interface CampaignExecutionResult {
  success: boolean;
  message: string;
  emailsSent?: number;
  emailsFailed?: number;
  totalLeads?: number;
  errors?: string[];
  executionId?: string;
  testMode?: boolean;
  error?: string;
}

export interface ScheduledExecution {
  id: string;
  campaignId: string;
  options: CampaignExecutionOptions;
  scheduledAt: Date;
  status: 'pending' | 'processing' | 'completed' | 'failed';
}

export class CampaignOrchestrator {
  private activeExecutions = new Map<string, any>();

  constructor() {
    // Services will be imported dynamically to avoid circular dependencies
  }

  async executeCampaign(options: CampaignExecutionOptions): Promise<CampaignExecutionResult> {
    const { campaignId, testMode = false, selectedLeadIds, maxLeadsPerBatch = 50 } = options;

    try {
      // Import services dynamically
      const { ExecutionProcessor } = await import('./ExecutionProcessor');
      const { LeadAssignmentService } = await import('./LeadAssignmentService');

      const executionProcessor = new ExecutionProcessor();
      const leadAssignmentService = new LeadAssignmentService();

      // Get campaign
      const campaign = await storage.getCampaign(campaignId);
      if (!campaign) {
        return {
          success: false,
          message: "Campaign not found",
          error: "Campaign not found"
        };
      }

      // Get leads
      let targetLeads: any[];
      if (selectedLeadIds && selectedLeadIds.length > 0) {
        targetLeads = await Promise.all(
          selectedLeadIds.map(id => storage.getLead(id))
        );
        targetLeads = targetLeads.filter(Boolean);
      } else {
        const allLeads = await storage.getLeads();
        targetLeads = allLeads.filter(lead => 
          !lead.campaignId || lead.campaignId === campaignId
        );
      }

      if (targetLeads.length === 0) {
        return {
          success: false,
          message: "No leads found for this campaign",
          error: "No leads available"
        };
      }

      // Assign unassigned leads to this campaign
      const unassignedLeads = targetLeads.filter(lead => !lead.campaignId);
      if (unassignedLeads.length > 0) {
        const assignmentResult = await leadAssignmentService.assignLeadsToCampaigns(
          unassignedLeads,
          [campaign]
        );
        console.log(`Assigned ${assignmentResult.assignedLeads} leads to campaign`);
      }

      // Update campaign status
      await storage.updateCampaign(campaignId, { 
        status: options.scheduleAt ? 'scheduled' : 'active'
      });

      // If test mode, limit to first lead
      if (testMode) {
        targetLeads = targetLeads.slice(0, 1);
      }

      // Process email sequence
      const processingResult = await executionProcessor.processEmailSequence(
        campaign,
        targetLeads,
        0, // Start with first template
        {
          batchSize: maxLeadsPerBatch,
          testMode,
          delayBetweenEmails: testMode ? 0 : 1000
        }
      );

      // Record a send event for predictive insights (once per execution)
      try {
        const { predictiveOptimizationService } = await import('../predictive-optimization-instance');
        predictiveOptimizationService.ingestSend(campaign.id, new Date());
      } catch (e) {
        console.warn('Predictive ingestion (send) failed:', e);
      }

      // Create conversations for successful sends
      if (!testMode && processingResult.emailsSent > 0) {
        for (const lead of targetLeads.slice(0, processingResult.emailsSent)) {
          try {
            await storage.createConversation({
              subject: `Campaign: ${campaign.name}`,
              status: 'active',
              priority: 'normal',
              campaignId: campaignId,
            });
          } catch (convError) {
            console.error(`Failed to create conversation for lead ${lead.id}:`, convError);
          }
        }
      }

      // Send user notification (for non-test executions)
      if (!testMode && processingResult.emailsSent > 0) {
        try {
          // Get the first template for the notification
          const templates = campaign.templates as any[] || [];
          const firstTemplate = templates[0];

          await userNotificationService.notifyCampaignExecuted(
            "075f86dc-d36e-4ef2-ab61-2919f9468515", // Default user ID - in real app, get from context
            {
              campaignName: campaign.name,
              campaignId: campaignId,
              emailsSent: processingResult.emailsSent,
              leadsTargeted: targetLeads.length,
              templateTitle: firstTemplate?.title || 'Email Template',
              executedAt: new Date()
            }
          );
        } catch (notificationError) {
          console.error('Failed to send campaign notification:', notificationError);
        }
      }

      // Broadcast execution update
      try {
        if (webSocketService.broadcast) {
          webSocketService.broadcast('campaignExecuted', {
            campaignId,
            emailsSent: processingResult.emailsSent,
            emailsFailed: processingResult.emailsFailed,
            testMode,
            timestamp: new Date()
          });
        }
      } catch (wsError) {
        console.error('WebSocket broadcast error:', wsError);
      }

      return {
        success: processingResult.success,
        message: testMode 
          ? `Test email sent to ${processingResult.emailsSent} lead(s)`
          : `Campaign executed successfully`,
        emailsSent: processingResult.emailsSent,
        emailsFailed: processingResult.emailsFailed,
        totalLeads: targetLeads.length,
        errors: processingResult.errors,
        executionId: processingResult.executionId,
        testMode
      };

    } catch (error) {
      console.error('Campaign execution error:', error);
      return {
        success: false,
        message: "Failed to execute campaign",
        error: error instanceof Error ? error.message : 'Unknown error',
        emailsSent: 0,
        emailsFailed: 0,
        totalLeads: 0
      };
    }
  }

  /**
   * Schedule campaign execution for later
   */
  async scheduleCampaign(options: CampaignExecutionOptions): Promise<{ success: boolean; scheduledId?: string; message: string }> {
    try {
      if (!options.scheduleAt) {
        return { success: false, message: 'Schedule date is required' };
      }

      const scheduledId = `scheduled_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

      // In a production system, you would store this in a job queue like Bull/Redis
      // For now, we'll just validate and return success

      await storage.updateCampaign(options.campaignId, {
        status: 'scheduled'
      });

      return {
        success: true,
        scheduledId,
        message: `Campaign scheduled for ${options.scheduleAt.toISOString()}`
      };

    } catch (error) {
      return {
        success: false,
        message: error instanceof Error ? error.message : 'Failed to schedule campaign'
      };
    }
  }

  /**
   * Get active executions
   */
  getActiveExecutions(): any[] {
    return Array.from(this.activeExecutions.values());
  }

  /**
   * Cancel active execution
   */
  cancelExecution(executionId: string): boolean {
    if (this.activeExecutions.has(executionId)) {
      this.activeExecutions.delete(executionId);
      console.log(`Cancelled execution: ${executionId}`);
      return true;
    }
    return false;
  }
}

export const campaignOrchestrator = new CampaignOrchestrator();

