export class UserNotificationService {

  /**
   * Send a notification to a user
   */
  async sendNotification(options: UserNotificationOptions): Promise<boolean> {
    try {
      const { userId, type, data, urgency = 'medium', sendEmail = true } = options;

      if (!sendEmail) {
        console.log(`Notification queued for user ${userId}: ${type}`, data);
        return true;
      }

      // Get user email
      const [user] = await db.select().from(users).where(eq(users.id, userId));
      if (!user?.email) {
        console.error(`User ${userId} not found or has no email`);
        return false;
      }

      // Generate notification content
      const template = notificationTemplates[type];
      if (!template) {
        console.error(`No template found for notification type: ${type}`);
        return false;
      }

      const content = template(data);

      // Send email notification using campaign email service
      const success = await sendCampaignEmail(
        user.email,
        content.subject,
        content.html,
        {},
        { isAutoResponse: false }
      );

      if (success) {
        console.log(`âœ… Notification sent to ${user.email}: ${type}`);
      } else {
        console.error(`âŒ Failed to send notification to ${user.email}: ${type}`);
      }

      return success;
    } catch (error) {
      console.error('Error sending user notification:', error);
      return false;
    }
  }

  /**
   * Send campaign execution notification
   */
  async notifyCampaignExecuted(userId: string, campaignData: {
    campaignName: string;
    campaignId: string;
    emailsSent: number;
    leadsTargeted: number;
    templateTitle: string;
    executedAt: Date;
  }): Promise<boolean> {
    return this.sendNotification({
      userId,
      type: NotificationType.CAMPAIGN_EXECUTED,
      data: campaignData,
      urgency: 'medium'
    });
  }

  /**
   * Send campaign completion notification  
   */
  async notifyCampaignCompleted(userId: string, campaignData: {
    campaignName: string;
    campaignId: string;
    totalEmailsSent: number;
    openRate: number;
    leadsEngaged: number;
    duration: string;
  }): Promise<boolean> {
    return this.sendNotification({
      userId,
      type: NotificationType.CAMPAIGN_COMPLETED,
      data: campaignData,
      urgency: 'low'
    });
  }

  /**
   * Send new lead assignment notification
   */
  async notifyLeadAssigned(userId: string, leadData: {
    leadName: string;
    leadEmail: string;
    leadPhone?: string;
    vehicleInterest?: string;
    leadSource?: string;
    campaignName: string;
  }): Promise<boolean> {
    return this.sendNotification({
      userId,
      type: NotificationType.LEAD_ASSIGNED,
      data: leadData,
      urgency: 'high'
    });
  }

  /**
   * Send high engagement alert
   */
  async notifyHighEngagement(userId: string, engagementData: {
    campaignName: string;
    campaignId: string;
    openRate: number;
    clickRate: number;
    responses: number;
    engagementScore: number;
    benchmark: number;
  }): Promise<boolean> {
    return this.sendNotification({
      userId,
      type: NotificationType.HIGH_ENGAGEMENT,
      data: engagementData,
      urgency: 'medium'
    });
  }

  /**
   * Send system alert
   */
  async sendSystemAlert(userId: string, alertData: {
    alertTitle: string;
    message: string;
    details?: string;
    actionRequired?: string;
  }): Promise<boolean> {
    return this.sendNotification({
      userId,
      type: NotificationType.SYSTEM_ALERT,
      data: alertData,
      urgency: 'high'
    });
  }

  /**
   * Send monthly report
   */
  async sendMonthlyReport(userId: string, reportData: {
    month: string;
    year: number;
    campaignsExecuted: number;
    totalEmailsSent: number;
    newLeads: number;
    avgOpenRate: number;
    responseRate: number;
    conversions: number;
  }): Promise<boolean> {
    return this.sendNotification({
      userId,
      type: NotificationType.MONTHLY_REPORT,
      data: reportData,
      urgency: 'low'
    });
  }

  /**
   * Send email validation warning
   */
  async sendValidationWarning(userId: string, validationData: {
    campaignName: string;
    campaignId: string;
    issues: string[];
  }): Promise<boolean> {
    return this.sendNotification({
      userId,
      type: NotificationType.EMAIL_VALIDATION_WARNING,
      data: validationData,
      urgency: 'high'
    });
  }

  /**
   * Send quota warning
   */
  async sendQuotaWarning(userId: string, quotaData: {
    percentage: number;
    emailsSent: number;
    emailsQuota: number;
    resetDate: string;
  }): Promise<boolean> {
    return this.sendNotification({
      userId,
      type: NotificationType.QUOTA_WARNING,
      data: quotaData,
      urgency: 'medium'
    });
  }
}

