server/services/campaign-chat.ts.content:
import { LLMClient } from './llm-client';
import { searchForCampaignChat, campaignChatPrompt } from '../integrations/supermemory';

interface CampaignChatResponse {
  message: string;
  nextStep?: string;
  data?: any;
  completed?: boolean;
  actions?: string[];
  suggestions?: string[];
  progress?: { stepIndex: number; total: number; percent: number };
}

interface CampaignStep {
  id: string;
  question: string;
  dataField: string;
  followUp?: string;
}

export class CampaignChatService {
  private static campaignSteps: CampaignStep[] = [
    {
      id: 'context',
      question: "What type of automotive campaign would you like to create? (e.g., new vehicle launch, service reminders, test drive promotion)",
      dataField: 'context',
      followUp: 'Perfect! Tell me more about your goals and what you want to achieve.'
    },
    {
      id: 'goals',
      question: "What are your main goals for this campaign? What do you want customers to do?",
      dataField: 'handoverGoals',
      followUp: 'Excellent! That gives me a clear picture of what you want to accomplish.'
    },
    {
      id: 'target_audience',
      question: "Who is your target audience? (e.g., existing customers, new prospects, specific demographics)",
      dataField: 'targetAudience',
      followUp: 'Great! Understanding your audience helps me create better content.'
    },
    {
      id: 'name',
      question: "What would you like to name this campaign?",
      dataField: 'name',
      followUp: 'Perfect name! Now that I understand your campaign goals and audience...'
    },
    {
      id: 'handover_criteria',
      question: "When do you want leads to be handed over to your sales team? Based on your goals, describe the signals that show a customer is ready (e.g., 'when they ask about pricing', 'when they want to schedule a test drive', 'when they seem urgent or ready to buy')",
      dataField: 'handoverCriteria',
      followUp: 'Perfect! I\'ll create smart handover rules based on what you described and your campaign goals.'
    },
    {
      id: 'email_templates',
      question: "How many email templates would you like in your sequence? (1-30 templates)",
      dataField: 'numberOfTemplates'
    }
  ];

  // Quick reply suggestions by step
  private static suggestionsByStep: Record<string, string[]> = {
    context: ["New vehicle launch", "Service reminders", "Test drive follow-up"],
    goals: ["Book test drives", "Book service", "Get trade-in leads"],
    target_audience: ["New prospects", "Current owners", "Leads with SUV interest"],
    email_templates: ["3", "5", "7"],
  };

  /**
   * Process campaign creation chat conversation
   */
  static async processCampaignChat(
    userMessage: string,
    currentStep: string = 'context',
    existingData: any = {}
  ): Promise<CampaignChatResponse> {
    try {
      // Get relevant past campaign data from Supermemory for context
      let ragResults: any = null;
      try {
        const clientId = existingData.clientId || 'default';

        ragResults = await searchForCampaignChat({
          clientId,
          campaignId: existingData.id,
          userTurn: userMessage,
          detectedType: existingData.context,
          vehicleKeywords: this.extractVehicleKeywords(userMessage + ' ' + (existingData.context || ''))
        });
      } catch (error) {
        console.warn('Failed to retrieve past campaigns from Supermemory:', error);
      }
      const stepIndex = this.campaignSteps.findIndex(step => step.id === currentStep);
      const currentStepData = this.campaignSteps[stepIndex];

      if (!currentStepData) {
        return {
          message: "I'm not sure what step we're on. Let's start over. What type of automotive campaign would you like to create?",
          nextStep: 'context',
          suggestions: this.suggestionsByStep['context'] || []
        };
      }

      // Process user's response for current step
      const updatedData = { ...existingData };
      updatedData[currentStepData.dataField] = userMessage;

      // Special processing for handover criteria - uses campaign context and goals
      if (currentStep === 'handover_criteria') {
        const handoverPrompt = await this.convertHandoverCriteriaToPrompt(
          userMessage, 
          updatedData.context,
          updatedData.handoverGoals,
          updatedData.targetAudience
        );
        updatedData.handoverPrompt = handoverPrompt;
      }

      // Special processing for template count
      if (currentStep === 'email_templates') {
        const count = parseInt(userMessage) || 5;
        updatedData.numberOfTemplates = Math.min(Math.max(count, 1), 30);
      }

      // Determine next step
      const nextStepIndex = stepIndex + 1;
      const isCompleted = nextStepIndex >= this.campaignSteps.length;

      if (isCompleted) {
        // Generate final campaign data
        const finalCampaign = await this.generateFinalCampaign(updatedData);

        // Calculate final progress
        const progress = {
          stepIndex: this.campaignSteps.length,
          total: this.campaignSteps.length,
          percent: 100
        };

        // Broadcast completion via WebSocket
        this.broadcastProgress(null, this.campaignSteps.length, this.campaignSteps.length, 100);

        return {
          message: `Excellent! I've created your "${finalCampaign.name}" campaign with smart handover rules and ${finalCampaign.numberOfTemplates || finalCampaign.templateCount || 5} email templates. Your campaign is ready to launch!`,
          completed: true,
          data: finalCampaign,
          actions: ['create_campaign', 'generate_templates'],
          progress
        };
      }

      const nextStep = this.campaignSteps[nextStepIndex];
      const responseMessage = currentStepData.followUp || 
        `Got it! ${nextStep.question}`;

      // Calculate progress
      const progress = {
        stepIndex: nextStepIndex,
        total: this.campaignSteps.length,
        percent: Math.round((nextStepIndex / this.campaignSteps.length) * 100)
      };

      // Broadcast progress via WebSocket
      this.broadcastProgress(null, nextStepIndex, this.campaignSteps.length, progress.percent);

      return {
        message: responseMessage,
        nextStep: nextStep.id,
        data: updatedData,
        actions: ["continue"],
        suggestions: this.suggestionsByStep[nextStep.id] || [],
        progress
      };

    } catch (error) {
      console.error('Campaign chat processing error:', error);
      return {
        message: "I encountered an issue. Let me help you create your campaign. What type of automotive campaign are you looking for?",
        nextStep: 'context'
      };
    }
  }

  /**
   * Broadcast progress updates via WebSocket
   */
  private static broadcastProgress(campaignId: string | null, stepIndex: number, total: number, percent: number) {
    try {
      // Import WebSocket service dynamically to avoid circular imports
      const broadcast = require('./websocket')?.broadcastMessage;
      if (broadcast) {
        broadcast('campaignChat:progress', { campaignId, stepIndex, total, percent });
      }
    } catch (error) {
      console.warn('Failed to broadcast campaign progress:', error);
    }
  }

  /**
   * Helper to safely parse JSON with fallbacks
   */
  private static coerceJson<T>(content: string, fallback: T): T {
    try {
      const parsed = JSON.parse(content);
      // Ensure arrays are actually arrays and strings aren't too long
      if (Array.isArray(fallback) && !Array.isArray(parsed)) {
        return fallback;
      }
      if (typeof fallback === 'string' && typeof parsed === 'string' && parsed.length > 1000) {
        return (parsed.substring(0, 1000) + '...') as T;
      }
      return parsed;
    } catch {
      return fallback;
    }
  }

  /**
   * Convert user's natural language handover criteria into structured AI prompt
   */
  private static async convertHandoverCriteriaToPrompt(
    userInput: string, 
    campaignContext?: string,
    campaignGoals?: string,
    targetAudience?: string
  ): Promise<string> {
    try {
      let contextSection = '';
      if (ragResults && ragResults.results && ragResults.results.length > 0) {
        const snippets = ragResults.results.map((r: any) => ({
          title: r.metadata?.name || r.metadata?.title,
          content: r.content
        }));

        contextSection = `
## RETRIEVED CONTEXT FROM PAST CAMPAIGNS:
${snippets.map((s: any) => `${s.title ? `${s.title}: ` : ''}${s.content}`).join('\n---\n')}
Use this historical data to inform your handover criteria generation.
`;
      }

      const conversionPrompt = `
# ROLE: Expert Automotive Handover Intelligence Designer
You are a world-class automotive sales intelligence expert who specializes in converting natural language into precise, actionable AI handover criteria. You understand buyer psychology, sales processes, and automotive industry nuances.

## CAMPAIGN INTELLIGENCE:
**Campaign Context:** "${campaignContext || 'General automotive campaign'}"
**Campaign Goals:** "${campaignGoals || 'Generate automotive leads'}"  
**Target Audience:** "${targetAudience || 'General automotive prospects'}"
**User's Natural Language Criteria:** "${userInput}"

${contextSection}

## YOUR MISSION:
Transform the user's informal handover criteria into a sophisticated AI evaluation prompt that captures the essence of buyer readiness for THIS specific campaign context.

## EVALUATION FRAMEWORK:
Consider these automotive buyer journey stages:
1. **Awareness** (just browsing, general interest)
2. **Consideration** (comparing options, seeking information)
3. **Intent** (serious about purchasing, specific needs)
4. **Decision** (ready to buy, urgency signals)

## REQUIRED OUTPUT:
Generate a comprehensive handover evaluation prompt that includes:

### CAMPAIGN-SPECIFIC TRIGGERS:
- Extract keywords from user criteria and map to buyer stages
- Prioritize signals that align with campaign goals
- Include audience-specific language patterns

### CONTEXTUAL QUALIFICATION:
- Define qualification thresholds based on campaign objectives
- Specify conversation depth requirements
- Set engagement quality benchmarks

### URGENCY DETECTION:
- Temporal language indicating immediate need
- Competitive pressure signals
- Decision-making timeline indicators

### BEHAVIORAL ANALYSIS:
- Question patterns showing serious intent
- Information-seeking behaviors aligned with purchase readiness
- Emotional indicators (excitement, urgency, concern resolution)

Return ONLY this JSON structure:
{
  "handoverPrompt": "Comprehensive AI evaluation prompt for this specific campaign context with detailed triggers, scoring criteria, and handover conditions",
  "campaignSpecific": true,
  "triggerKeywords": ["specific", "campaign-relevant", "trigger", "words"],
  "qualificationCriteria": ["measurable", "campaign-aligned", "readiness", "signals"],
  "urgencyIndicators": ["time-sensitive", "decision-ready", "language"],
  "scoringThresholds": {"qualification": 75, "urgency": 85, "handover": 80},
  "reasoning": "Brief explanation of why these criteria align with campaign goals and audience"
}

CRITICAL: The handover prompt must be laser-focused on the specific campaign context and goals provided.`;

      const { content } = await LLMClient.generateAutomotiveContent(conversionPrompt, { json: true });
      const parsed = this.coerceJson(content, { handoverPrompt: this.getDefaultHandoverPrompt() });

      return parsed.handoverPrompt || this.getDefaultHandoverPrompt();

    } catch (error) {
      console.error('Failed to convert handover criteria:', error);
      return this.getDefaultHandoverPrompt();
    }
  }

  /**
   * Generate final campaign with all AI-enhanced content
   */
  private static async generateFinalCampaign(data: any): Promise<any> {
    try {
      // Generate campaign name if not provided
      if (!data.name || data.name.length < 3) {
        const namePrompt = `Generate a catchy, professional name for an automotive campaign. Context: ${data.context}. Goals: ${data.handoverGoals}. Return only the campaign name, no quotes or extra text.`;
        const { content } = await LLMClient.generateAutomotiveContent(namePrompt);
        data.name = content.trim().replace(/^"|"$/g, ''); // Remove quotes if present
      }

      // Generate email templates based on context and goals
      const templatePrompt = `
Create ${data.numberOfTemplates || 5} automotive email templates for this campaign:
Context: ${data.context}
Goals: ${data.handoverGoals}
Handover Criteria: ${data.handoverCriteria}

Each template should:
- Be automotive industry focused
- Include personalization placeholders like [Name] and [vehicleInterest]
- Progress from introduction to call-to-action
- Be professional but engaging
- Include automotive-specific offers or information

Return JSON array of template objects with "subject" and "content" fields.`;

      const { content: templatesResult } = await LLMClient.generateAutomotiveContent(templatePrompt, { json: true });
      let templates = this.coerceJson(templatesResult, []);
      if (Array.isArray(templates) && templates.length === 0) {
        // Fallback templates
        templates = [
          { subject: `Welcome to ${data.name}`, content: `Hi [Name], welcome to our ${data.context} campaign!` }
        ];
      }

      // Generate subject lines
      const subjectPrompt = `Generate ${data.numberOfTemplates || 5} compelling email subject lines for automotive campaign: ${data.context}. Return as JSON array of strings.`;
      const { content: subjectsResult } = await LLMClient.generateAutomotiveContent(subjectPrompt, { json: true });
      let subjects = this.coerceJson(subjectsResult, [`${data.name} - Special Offer`, `${data.name} - Update`, `${data.name} - Reminder`]);

      return {
        name: data.name,
        context: data.context,
        handoverGoals: data.handoverGoals,
        targetAudience: data.targetAudience,
        handoverPrompt: data.handoverPrompt,
        handoverCriteria: data.handoverCriteria,
        numberOfTemplates: data.numberOfTemplates || 5,
        templates: templates,
        subjectLines: subjects,
        status: 'draft',
        createdAt: new Date().toISOString()
      };

    } catch (error) {
      console.error('Failed to generate final campaign:', error);
      return {
        name: data.name || 'New Automotive Campaign',
        context: data.context || 'General automotive campaign',
        handoverGoals: data.handoverGoals || 'Generate leads and drive sales',
        targetAudience: data.targetAudience || 'General automotive prospects',
        handoverPrompt: data.handoverPrompt || this.getDefaultHandoverPrompt(),
        numberOfTemplates: data.numberOfTemplates || 5,
        templates: [],
        subjectLines: [],
        status: 'draft'
      };
    }
  }

  /**
   * Get enhanced default handover prompt for fallback
   */
  private static getDefaultHandoverPrompt(): string {
    return `# AUTOMOTIVE SALES INTELLIGENCE EVALUATION SYSTEM

## YOUR ROLE:
You are an expert automotive sales intelligence AI analyzing customer conversations to identify optimal handover moments. Your mission is to detect genuine buying interest and qualification signals with precision.

## HANDOVER EVALUATION FRAMEWORK:

### IMMEDIATE HANDOVER TRIGGERS (Score: 90-100)
**High-Intent Purchase Signals:**
- Direct purchase language: "ready to buy", "want to purchase", "I'll take it", "let's move forward"
- Pricing commitment: "what's the best price", "can you match this price", "what's my payment"
- Scheduling urgency: "today", "this weekend", "ASAP", "how soon can I"
- Human escalation: "speak to someone", "talk to a person", "manager", "sales rep"

### STRONG QUALIFICATION SIGNALS (Score: 75-89)
**Serious Consideration Indicators:**
- Financial readiness: "financing options", "down payment", "monthly payment", "lease terms"
- Vehicle specificity: mentions specific models, years, trim levels, colors, features  
- Comparison shopping: "versus", "compared to", "better than", competitor mentions
- Trade-in discussions: "trade my current car", "trade value", "what's it worth"
- Timeline establishment: "when available", "delivery time", "how long"

### MODERATE INTEREST SIGNALS (Score: 50-74)
**Developing Interest Indicators:**
- Information gathering: detailed feature questions, specification requests
- Availability checks: "do you have", "in stock", "on the lot"
- Appointment interest: "come look", "visit", "see it", "test drive" (without urgency)
- General pricing: "how much", "price range", "cost" (without commitment language)

### EVALUATION CRITERIA:
1. **Conversation Depth**: 8+ meaningful exchanges indicate serious engagement
2. **Question Quality**: Specific, detailed questions show genuine interest
3. **Response Speed**: Quick replies suggest active engagement
4. **Language Intensity**: Emotional language ("love", "perfect", "exactly") indicates strong interest
5. **Multiple Signal Types**: Customers showing 2+ different signal categories are handover-ready

### HANDOVER DECISION MATRIX:
- **Score 90-100**: Immediate handover (within 5 minutes)
- **Score 80-89**: Priority handover (within 15 minutes)  
- **Score 75-79**: Standard handover (within 30 minutes)
- **Score 50-74**: Continue nurturing, reassess in 24 hours
- **Below 50**: Standard marketing sequence

### SPECIAL CONSIDERATIONS:
- **Urgency Language**: "today", "now", "immediately" = automatic +10 points
- **Competitor Mentions**: Active shopping = automatic +5 points
- **Emotional Indicators**: Excitement or frustration = manual review
- **Technical Questions**: Deep product knowledge needs = specialist referral

**HANDOVER TRIGGER**: Execute handover when score â‰¥ 80 OR any immediate trigger is detected.`;
  }

  /**
   * Get current campaign creation progress
   */
  static getCampaignProgress(currentStep: string): any {
    const stepIndex = this.campaignSteps.findIndex(step => step.id === currentStep);
    const totalSteps = this.campaignSteps.length;

    return {
      currentStep: stepIndex + 1,
      totalSteps,
      progress: Math.round(((stepIndex + 1) / totalSteps) * 100),
      stepName: currentStep
    };
  }

  /**
   * Extract vehicle-related keywords from user input for better RAG retrieval
   */
  private static extractVehicleKeywords(text: string): string[] {
    const vehicleTypes = ['truck', 'suv', 'sedan', 'coupe', 'convertible', 'wagon', 'hatchback', 'minivan', 'crossover'];
    const brands = ['ford', 'toyota', 'honda', 'chevrolet', 'nissan', 'hyundai', 'kia', 'subaru', 'mazda', 'volkswagen'];
    const keywords: string[] = [];

    const lowerText = text.toLowerCase();

    vehicleTypes.forEach(type => {
      if (lowerText.includes(type)) keywords.push(type);
    });

    brands.forEach(brand => {
      if (lowerText.includes(brand)) keywords.push(brand);
    });

    return keywords;
  }
}

server/services/campaign-prompts.ts.content:
/**
 * Enhanced Automotive Email Marketing Campaign Expert Prompt
 * Combines deep automotive retail knowledge with high-converting email content strategy
 */

export const ENHANCED_AUTOMOTIVE_EMAIL_MARKETING_PROMPT = `You are a seasoned automotive email marketing strategist with expertise in creating high-impact campaigns for car dealerships. You combine deep knowledge of automotive retail, buyer psychology, and dealership operations with the creativity and conversion skills needed to craft irresistible email campaigns that sell more vehicles and book more service appointments.

Your mission: Guide dealership teamsâ€”step by stepâ€”in creating **personalized, compelling, and visually engaging email campaigns** that keep shoppers engaged longer, strengthen loyalty, and generate measurable sales results.

## Your Expertise:
- Managed 1,000+ campaigns for 200+ dealerships spanning luxury, domestic, and import brands
- Skilled in automotive customer journey mapping from first touch to showroom visit
- Mastery of dealership email metrics: open rates, CTR, lead-to-appointment conversion, and service booking rates
- Deep understanding of manufacturer incentives, seasonal sales drivers, and local market competition
- Ability to integrate inventory feeds, service specials, and personalized offers into campaign templates for maximum relevance

## Core Expertise Areas:

### 1. Automotive Customer Psychology in Email
Car buyers:
- May be researching months before buying, but act fast when the right deal hits their inbox
- Care about price, availability, financing terms, and real photosâ€”avoid stock imagery where possible
- Respond to urgency: "Only 3 left in stock" or "Ends Monday" drives clicks
- Engage more with emails tailored to their history (vehicle owned, last service date, favorite models)
- Value transparent, helpful tone over generic sales language

### 2. Campaign Types You've Perfected
- **New Inventory Alerts**: Spotlight hot arrivals or popular trims before competitors promote them
- **Model-Specific Feature Spotlights**: Educate while enticingâ€”focus on what makes it worth a test drive
- **Owner Loyalty Offers**: Encourage trade-ins, lease pull-aheads, and upgrade opportunities
- **Seasonal Service Reminders**: Tie offers to climate and timing ("Get Winter-Ready Tires Installed This Week")
- **Event Invitations**: Launch parties, tent sales, VIP service clinics
- **Finance/Lease Specials**: Clearly display payments, terms, and incentive expirations

### 3. Email Quality Standards
- **Hook Fast**: Lead with what matters to themâ€”specific savings, fresh arrivals, or "this weekend only"
- **Visual Impact**: Real car photos, personalized hero images, fresh inventory pulls
- **Clarity and Brevity**: Trim excess textâ€”make "why they should care" instantly obvious
- **Compelling CTAs**: "Book My Test Drive" or "Claim My Service Discount"  
- **Mobile-Friendly**: 75%+ of opens happen on mobile, so design for scroll and tap

### 4. Analytics & Continuous Improvement
You measure:
- Open rate lift from subject line changes
- Click-through increases from personalized inventory blocks
- Conversions (test drives booked, service appointments made) within 72 hours
- Impact by segment: new leads, current owners, past customers
- Seasonal content performance trends

You are obsessed with testing:
- A/B subject lines: urgency vs curiosity
- CTA button color, placement, and wording
- Personalization depth: "Hi Alex" + "3 New F-150s Just In"

### 5. Your Communication Style Inside the Platform
- Keep it natural, friendly, and engagingâ€”like chatting with a helpful salesperson, not a bot
- Avoid formal or stiff openers; start with excitement or empathy
- Adapt to urgency:
   - **Frustrated user?** "I totally get itâ€”that's frustrating. Let's fix it now. Want me to draft a new offer?"
   - **Urgent user?** "No time to wasteâ€”let's push this out today. Just need your OK on the preview."
- Every guidance step ends with a clear, friendly action invite
   - "Shall we add those Silverado pics to the hero image?"
   - "Want me to pull live inventory for that model line?"
- Naturally weave in campaign best practices without jargon
   - "Tax season buyers love seeing payment examplesâ€”should I add those?"

### 6. You Naturally Ask:
- "Are we promoting sales, service, or both in this email?"
- "Any specific models or trims you want to highlight?"
- "Do you want fresh arrival photos or just stock imagery?"
- "What's the urgencyâ€”event dates, incentive deadlines, or inventory scarcity?"
- "Who's this targeting: new prospects, current owners, or lapsed customers?"

### 7. Seasonal & Manufacturer Context
You bake in:
- Model year-end clearance urgency in fall
- Winter safety service push
- Summer road trip prep content
- OEM compliance ("Toyota lease specials must follow these ad copy rulesâ€¦")
- Local market triggersâ€”snow forecast, tax refund season, gas price spikes

### 8. Response Flow Inside the Email Builder
1. **Acknowledge & Relate**: "Greatâ€”spring promo for SUVs is a smart move, lots of families shop now."
2. **Diagnose**: "To get the best clicks, we'll want fresh lifestyle photos and a payment example."
3. **Prescribe**: "Let's run with a subject like: '3-Day SUV Sale â€“ Family-Ready Deals Inside'."
4. **Prioritize Next Step**: "Should we make this a single-offer blast or a multi-offer inventory showcase?"
5. **Confirm & Launch**: "Once you approve, I'll schedule it for tomorrow morningâ€”more opens before lunch."

Remember: You're not just creating an emailâ€”you're creating a compelling reason for a shopper to stay with *this dealership* instead of browsing competitors. Every email you guide should feel timely, relevant, personalized, and action-drivenâ€¦ while staying true to the dealership's voice and brand.

Ultimate goal: **Keep shoppers engaged. The longer they engage with us, the higher the likelihood they buy with usâ€”not the other guy.**`;

export class CampaignPromptService {
  static getCampaignCreationPrompt(): string {
    return ENHANCED_AUTOMOTIVE_EMAIL_MARKETING_PROMPT;
  }

  static generateContextualPrompt(userInput?: string, campaignType?: string, urgency?: 'low' | 'medium' | 'high'): string {
    let prompt = ENHANCED_AUTOMOTIVE_EMAIL_MARKETING_PROMPT;

    // Add contextual guidance based on campaign type
    if (campaignType) {
      prompt += `\n\n## CURRENT CONTEXT:
Campaign Type Focus: ${campaignType}`;

      switch (campaignType) {
        case 'new_inventory':
          prompt += `\nPriority: Highlight fresh arrivals, specific model features, and availability urgency.
Key Questions to Ask: "Which new models just arrived?" "Any hot sellers we should feature?" "Want to include 'just arrived' messaging?"`;
          break;
        case 'seasonal_service':
          prompt += `\nPriority: Connect service needs to current season/weather, emphasize safety and convenience.
Key Questions to Ask: "What seasonal services are most needed now?" "Any service specials running?" "Want to tie this to weather conditions?"`;
          break;
        case 'finance_lease':
          prompt += `\nPriority: Clear payment examples, incentive deadlines, and qualification assistance.
Key Questions to Ask: "What are the current rates?" "Any manufacturer incentives ending soon?" "Want to include payment calculator?"`;
          break;
      }
    }

    // Add urgency-specific guidance
    if (urgency) {
      prompt += `\n\nUrgency Level: ${urgency}`;
      switch (urgency) {
        case 'high':
          prompt += `\nApproach: Act quickly, focus on immediate next steps, suggest urgent subject lines and time-sensitive offers.`;
          break;
        case 'medium':
          prompt += `\nApproach: Balance thoroughness with efficiency, ask key questions but move toward recommendations.`;
          break;
        case 'low':
          prompt += `\nApproach: Take time to explore options, educate on best practices, suggest A/B testing opportunities.`;
          break;
      }
    }

    return prompt;
  }

  static parseUserIntent(message: string): { campaignType?: string; urgency: 'low' | 'medium' | 'high'; keywords: string[] } {
    const content = message.toLowerCase();
    let campaignType: string | undefined;
    let urgency: 'low' | 'medium' | 'high' = 'low';
    const keywords: string[] = [];

    // Detect campaign type
    if (content.includes('new inventory') || content.includes('new arrivals') || content.includes('just arrived')) {
      campaignType = 'new_inventory';
      keywords.push('new inventory');
    }
    if (content.includes('service') || content.includes('maintenance') || content.includes('oil change')) {
      campaignType = 'seasonal_service';
      keywords.push('service');
    }
    if (content.includes('financing') || content.includes('lease') || content.includes('payment')) {
      campaignType = 'finance_lease';
      keywords.push('financing');
    }
    if (content.includes('event') || content.includes('sale') || content.includes('promotion')) {
      campaignType = 'promotional_event';
      keywords.push('event');
    }

    // Detect urgency
    if (content.includes('urgent') || content.includes('asap') || content.includes('today') || 
        content.includes('immediately') || content.includes('rush')) {
      urgency = 'high';
    } else if (content.includes('soon') || content.includes('this week') || 
               content.includes('quickly') || content.includes('fast')) {
      urgency = 'medium';
    }

    // Extract other keywords
    const automotiveKeywords = [
      'suv', 'truck', 'sedan', 'coupe', 'convertible', 'hybrid', 'electric',
      'ford', 'toyota', 'honda', 'chevrolet', 'bmw', 'mercedes', 'audi',
      'test drive', 'trade-in', 'warranty', 'certified pre-owned'
    ];

    automotiveKeywords.forEach(keyword => {
      if (content.includes(keyword)) {
        keywords.push(keyword);
      }
    });

    return { campaignType, urgency, keywords };
  }

  static generateResponseGuidance(userIntent: ReturnType<typeof CampaignPromptService.parseUserIntent>): string {
    const guidance = [];

    if (userIntent.campaignType) {
      guidance.push(`Focus on ${userIntent.campaignType.replace('_', ' ')} best practices`);
    }

    if (userIntent.urgency === 'high') {
      guidance.push("User needs quick turnaround - prioritize immediate next steps");
    }

    if (userIntent.keywords.length > 0) {
      guidance.push(`Key topics: ${userIntent.keywords.join(', ')}`);
    }

    return guidance.join('. ') + '.';
  }
}

server/services/dynamic-response-intelli.content:
import { storage } from '../storage';
import type { Conversation, ConversationMessage, Lead } from '@shared/schema';

export interface ConversationAnalysis {
  conversationId: string;
  leadId: string;
  mood: 'positive' | 'neutral' | 'negative' | 'frustrated' | 'excited';
  urgency: 'low' | 'medium' | 'high' | 'critical';
  intent: 'research' | 'comparison' | 'ready_to_buy' | 'price_focused' | 'undecided';
  buyingSignals: string[];
  riskFactors: string[];
  recommendedAction: 'continue' | 'escalate' | 'schedule_call' | 'send_offer' | 'urgent_followup';
  confidence: number;
  nextSteps: string[];
  escalationReason?: string;
}

export interface BuyingSignal {
  signal: string;
  weight: number;
  category: 'urgency' | 'financial' | 'decision' | 'timeline';
  description: string;
}

export interface ConversationPattern {
  patternType: 'high_intent' | 'comparison_shopping' | 'price_objection' | 'ready_to_close';
  indicators: string[];
  recommendedResponse: string;
  escalationTrigger: boolean;
  priority: number;
}

export class DynamicResponseIntelligenceService {
  private buyingSignals: BuyingSignal[] = [
    // Urgency signals
    { signal: 'need by', weight: 8, category: 'urgency', description: 'Specific timeline mentioned' },
    { signal: 'this week', weight: 9, category: 'urgency', description: 'Immediate timeline' },
    { signal: 'asap', weight: 9, category: 'urgency', description: 'Urgent request' },
    { signal: 'coming in today', weight: 10, category: 'urgency', description: 'Immediate visit planned' },

    // Financial signals
    { signal: 'pre-approved', weight: 9, category: 'financial', description: 'Financing already secured' },
    { signal: 'cash buyer', weight: 10, category: 'financial', description: 'Cash purchase ready' },
    { signal: 'monthly payment', weight: 7, category: 'financial', description: 'Payment discussion' },
    { signal: 'down payment', weight: 8, category: 'financial', description: 'Ready to put money down' },
    { signal: 'trade value', weight: 7, category: 'financial', description: 'Trade-in ready' },

    // Decision signals
    { signal: 'ready to buy', weight: 10, category: 'decision', description: 'Explicit buying intent' },
    { signal: 'make a deal', weight: 9, category: 'decision', description: 'Negotiation ready' },
    { signal: 'best price', weight: 8, category: 'decision', description: 'Price negotiation' },
    { signal: 'sign today', weight: 10, category: 'decision', description: 'Ready to close' },

    // Timeline signals
    { signal: 'lease expires', weight: 8, category: 'timeline', description: 'Current lease ending' },
    { signal: 'car died', weight: 9, category: 'timeline', description: 'Immediate need' },
    { signal: 'before', weight: 6, category: 'timeline', description: 'Specific deadline' }
  ];

  private conversationPatterns: ConversationPattern[] = [
    {
      patternType: 'high_intent',
      indicators: ['ready to buy', 'cash buyer', 'pre-approved', 'coming in today'],
      recommendedResponse: 'Immediate personal attention with finance manager',
      escalationTrigger: true,
      priority: 10
    },
    {
      patternType: 'comparison_shopping',
      indicators: ['other dealers', 'shopping around', 'better deal', 'competitor'],
      recommendedResponse: 'Focus on unique value propositions and exclusive offers',
      escalationTrigger: false,
      priority: 7
    },
    {
      patternType: 'price_objection',
      indicators: ['too expensive', 'over budget', 'cheaper elsewhere', 'price too high'],
      recommendedResponse: 'Present financing options and total value package',
      escalationTrigger: false,
      priority: 6
    },
    {
      patternType: 'ready_to_close',
      indicators: ['sign today', 'make a deal', 'best price', 'final offer'],
      recommendedResponse: 'Connect with sales manager for closing authority',
      escalationTrigger: true,
      priority: 9
    }
  ];

  async analyzeConversation(conversationId: string): Promise<ConversationAnalysis> {
    const conversation = await storage.getConversation(conversationId);
    if (!conversation) {
      throw new Error('Conversation not found');
    }

    const messages = conversation.messages || [];
    const leadMessages = messages.filter(m => !m.isFromAI);

    if (leadMessages.length === 0) {
      return this.createDefaultAnalysis(conversationId, conversation.leadId);
    }

    const analysis = await this.performDeepAnalysis(conversation, leadMessages);
    return analysis;
  }

  async analyzeAllActiveConversations(): Promise<ConversationAnalysis[]> {
    const conversations = await storage.getConversations();
    const activeConversations = conversations.filter(c => c.status === 'active');

    const analyses: ConversationAnalysis[] = [];

    for (const conversation of activeConversations) {
      try {
        const analysis = await this.analyzeConversation(conversation.id);
        analyses.push(analysis);
      } catch (error) {
        console.error(`Failed to analyze conversation ${conversation.id}:`, error);
      }
    }

    return analyses.sort((a, b) => {
      // Sort by urgency and buying signals
      const scoreA = this.calculatePriorityScore(a);
      const scoreB = this.calculatePriorityScore(b);
      return scoreB - scoreA;
    });
  }

  async getEscalationCandidates(): Promise<ConversationAnalysis[]> {
    const analyses = await this.analyzeAllActiveConversations();
    return analyses.filter(a => 
      a.recommendedAction === 'escalate' || 
      a.recommendedAction === 'urgent_followup' ||
      a.urgency === 'critical'
    );
  }

  private async performDeepAnalysis(conversation: Conversation, leadMessages: ConversationMessage[]): Promise<ConversationAnalysis> {
    const allContent = leadMessages.map(m => m.content).join(' ').toLowerCase();

    // Analyze mood
    const mood = this.analyzeMood(allContent);

    // Analyze urgency
    const urgency = this.analyzeUrgency(allContent);

    // Analyze intent
    const intent = this.analyzeIntent(allContent);

    // Detect buying signals
    const buyingSignals = this.detectBuyingSignals(allContent);

    // Identify risk factors
    const riskFactors = this.identifyRiskFactors(allContent);

    // Determine recommended action
    const { recommendedAction, escalationReason, nextSteps } = this.determineRecommendedAction(
      mood, urgency, intent, buyingSignals, riskFactors
    );

    // Calculate confidence
    const confidence = this.calculateConfidence(leadMessages.length, buyingSignals.length);

    return {
      conversationId: conversation.id,
      leadId: conversation.leadId,
      mood,
      urgency,
      intent,
      buyingSignals,
      riskFactors,
      recommendedAction,
      confidence,
      nextSteps,
      escalationReason
    };
  }

  private analyzeMood(content: string): ConversationAnalysis['mood'] {
    const positiveWords = ['great', 'excellent', 'perfect', 'love', 'excited', 'interested', 'wonderful'];
    const negativeWords = ['terrible', 'awful', 'hate', 'worst', 'horrible', 'disappointed'];
    const frustratedWords = ['frustrated', 'annoyed', 'upset', 'confused', 'difficult'];
    const excitedWords = ['excited', 'thrilled', 'can\'t wait', 'amazing', 'fantastic'];

    const positiveCount = positiveWords.filter(word => content.includes(word)).length;
    const negativeCount = negativeWords.filter(word => content.includes(word)).length;
    const frustratedCount = frustratedWords.filter(word => content.includes(word)).length;
    const excitedCount = excitedWords.filter(word => content.includes(word)).length;

    if (excitedCount > 0 || (positiveCount > 2 && negativeCount === 0)) return 'excited';
    if (frustratedCount > 0 || negativeCount > positiveCount) return 'frustrated';
    if (negativeCount > 0) return 'negative';
    if (positiveCount > 0) return 'positive';
    return 'neutral';
  }

  private analyzeUrgency(content: string): ConversationAnalysis['urgency'] {
    const urgencyTerms = {
      critical: ['emergency', 'immediately', 'asap', 'urgent', 'today only', 'right now'],
      high: ['this week', 'soon', 'quickly', 'need by', 'deadline'],
      medium: ['next week', 'by month end', 'sometime soon'],
      low: ['eventually', 'no rush', 'when convenient', 'looking ahead']
    };

    for (const [level, terms] of Object.entries(urgencyTerms)) {
      if (terms.some(term => content.includes(term))) {
        return level as ConversationAnalysis['urgency'];
      }
    }

    return 'medium';
  }

  private analyzeIntent(content: string): ConversationAnalysis['intent'] {
    const intentPatterns = {
      ready_to_buy: ['ready to buy', 'want to purchase', 'make a deal', 'sign today'],
      price_focused: ['best price', 'cheapest', 'discount', 'deal', 'lower price'],
      comparison: ['comparing', 'other dealers', 'shopping around', 'versus'],
      research: ['learning about', 'information', 'tell me about', 'curious'],
      undecided: ['not sure', 'thinking about', 'maybe', 'considering']
    };

    for (const [intent, patterns] of Object.entries(intentPatterns)) {
      if (patterns.some(pattern => content.includes(pattern))) {
        return intent as ConversationAnalysis['intent'];
      }
    }

    return 'research';
  }

  private detectBuyingSignals(content: string): string[] {
    const detectedSignals: string[] = [];

    for (const signal of this.buyingSignals) {
      if (content.includes(signal.signal)) {
        detectedSignals.push(signal.signal);
      }
    }

    return detectedSignals;
  }

  private identifyRiskFactors(content: string): string[] {
    const riskTerms = [
      'other dealers', 'better price elsewhere', 'not interested', 'too expensive',
      'thinking about it', 'call back later', 'not ready', 'just looking'
    ];

    return riskTerms.filter(term => content.includes(term));
  }

  private determineRecommendedAction(
    mood: ConversationAnalysis['mood'],
    urgency: ConversationAnalysis['urgency'],
    intent: ConversationAnalysis['intent'],
    buyingSignals: string[],
    riskFactors: string[]
  ): {
    recommendedAction: ConversationAnalysis['recommendedAction'];
    escalationReason?: string;
    nextSteps: string[];
  } {
    const highValueSignals = ['ready to buy', 'cash buyer', 'pre-approved', 'sign today'];
    const hasHighValueSignal = buyingSignals.some(signal => highValueSignals.includes(signal));

    if (hasHighValueSignal || urgency === 'critical') {
      return {
        recommendedAction: 'escalate',
        escalationReason: 'High-value buying signals detected',
        nextSteps: [
          'Immediately connect with sales manager',
          'Prepare financing options',
          'Schedule in-person appointment today'
        ]
      };
    }

    if (intent === 'ready_to_buy' || buyingSignals.length >= 3) {
      return {
        recommendedAction: 'urgent_followup',
        nextSteps: [
          'Call within 30 minutes',
          'Prepare vehicle availability information',
          'Have financing pre-approval ready'
        ]
      };
    }

    if (urgency === 'high' || (mood === 'excited' && buyingSignals.length > 0)) {
      return {
        recommendedAction: 'schedule_call',
        nextSteps: [
          'Schedule phone consultation within 2 hours',
          'Prepare vehicle comparison materials',
          'Have incentive offers ready'
        ]
      };
    }

    if (intent === 'price_focused' || buyingSignals.includes('best price')) {
      return {
        recommendedAction: 'send_offer',
        nextSteps: [
          'Prepare competitive pricing analysis',
          'Create personalized offer with incentives',
          'Schedule follow-up call to discuss'
        ]
      };
    }

    return {
      recommendedAction: 'continue',
      nextSteps: [
        'Continue nurturing conversation',
        'Provide requested information',
        'Monitor for buying signals'
      ]
    };
  }

  private calculateConfidence(messageCount: number, signalCount: number): number {
    let confidence = 50; // Base confidence

    // More messages = higher confidence
    confidence += Math.min(30, messageCount * 5);

    // More buying signals = higher confidence
    confidence += Math.min(20, signalCount * 10);

    return Math.min(100, confidence);
  }

  private calculatePriorityScore(analysis: ConversationAnalysis): number {
    let score = 0;

    // Urgency scoring
    const urgencyScores = { critical: 40, high: 30, medium: 20, low: 10 };
    score += urgencyScores[analysis.urgency];

    // Buying signals scoring
    score += analysis.buyingSignals.length * 10;

    // Intent scoring
    const intentScores = { ready_to_buy: 30, price_focused: 20, comparison: 15, research: 10, undecided: 5 };
    score += intentScores[analysis.intent];

    // Mood scoring
    const moodScores = { excited: 15, positive: 10, neutral: 5, negative: 0, frustrated: -5 };
    score += moodScores[analysis.mood];

    return score;
  }

  private createDefaultAnalysis(conversationId: string, leadId: string): ConversationAnalysis {
    return {
      conversationId,
      leadId,
      mood: 'neutral',
      urgency: 'medium',
      intent: 'research',
      buyingSignals: [],
      riskFactors: [],
      recommendedAction: 'continue',
      confidence: 30,
      nextSteps: ['Continue conversation', 'Gather more information']
    };
  }

  async learnFromSuccessfulConversations(): Promise<void> {
    // This would analyze conversations that led to conversions
    // and update patterns and signals accordingly
    const conversations = await storage.getConversations();
    const successfulConversations = conversations.filter(c => c.status === 'converted');

    // Machine learning would go here to improve signal detection
    console.log(`Learning from ${successfulConversations.length} successful conversations`);
  }
}

export const dynamicResponseIntelligenceService = new DynamicResponseIntelligenceService();

