import { Calendar, Clock, Repeat, PlayCircle, PauseCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";

interface CampaignSchedulerProps {
  campaignId: string;
  onScheduled?: () => void;
}

export function CampaignScheduler({ campaignId, onScheduled }: CampaignSchedulerProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [scheduleType, setScheduleType] = useState<'immediate' | 'scheduled' | 'recurring'>('immediate');
  const [scheduledDate, setScheduledDate] = useState('');
  const [scheduledTime, setScheduledTime] = useState('09:00');
  const [recurringPattern, setRecurringPattern] = useState<'daily' | 'weekly' | 'monthly'>('weekly');
  const [recurringDays, setRecurringDays] = useState<number[]>([1, 2, 3, 4, 5]); // Weekdays
  const [recurringTime, setRecurringTime] = useState('09:00');

  const weekdays = [
    { value: 1, label: 'Mon' },
    { value: 2, label: 'Tue' },
    { value: 3, label: 'Wed' },
    { value: 4, label: 'Thu' },
    { value: 5, label: 'Fri' },
    { value: 6, label: 'Sat' },
    { value: 0, label: 'Sun' }
  ];

  // Get current schedule
  const { data: schedule, isLoading } = useQuery({
    queryKey: ['/api/campaigns', campaignId, 'schedule'],
  });

  // Schedule campaign mutation
  const scheduleMutation = useMutation({
    mutationFn: async (scheduleData: any) => {
      return apiRequest(`/api/campaigns/${campaignId}/schedule`, 'POST', scheduleData);
    },
    onSuccess: () => {
      toast({
        title: "Campaign Scheduled",
        description: "Your campaign has been scheduled successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/campaigns', campaignId, 'schedule'] });
      onScheduled?.();
    },
    onError: (error) => {
      toast({
        title: "Scheduling Failed",
        description: "Failed to schedule campaign. Please try again.",
        variant: "destructive",
      });
      console.error('Schedule error:', error);
    },
  });

  // Cancel schedule mutation
  const cancelMutation = useMutation({
    mutationFn: async () => {
      return apiRequest(`/api/campaigns/${campaignId}/schedule`, 'DELETE');
    },
    onSuccess: () => {
      toast({
        title: "Schedule Cancelled",
        description: "Campaign schedule has been cancelled.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/campaigns', campaignId, 'schedule'] });
    },
    onError: (error) => {
      toast({
        title: "Cancellation Failed",
        description: "Failed to cancel schedule. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Execute campaign mutation
  const executeMutation = useMutation({
    mutationFn: async () => {
      return apiRequest(`/api/campaigns/${campaignId}/execute`, 'POST');
    },
    onSuccess: () => {
      toast({
        title: "Campaign Executed",
        description: "Campaign is now running.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/campaigns', campaignId, 'schedule'] });
    },
    onError: (error) => {
      toast({
        title: "Execution Failed",
        description: "Failed to execute campaign. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSchedule = () => {
    const scheduleData: any = {
      scheduleType,
    };

    if (scheduleType === 'scheduled') {
      if (!scheduledDate || !scheduledTime) {
        toast({
          title: "Missing Information",
          description: "Please select both date and time for scheduled campaigns.",
          variant: "destructive",
        });
        return;
      }
      scheduleData.scheduledStart = `${scheduledDate}T${scheduledTime}:00`;
    } else if (scheduleType === 'recurring') {
      scheduleData.recurringPattern = recurringPattern;
      scheduleData.recurringDays = recurringDays;
      scheduleData.recurringTime = `${recurringTime}:00`;
    }

    scheduleMutation.mutate(scheduleData);
  };

  const handleDayToggle = (day: number) => {
    setRecurringDays(prev => 
      prev.includes(day) 
        ? prev.filter(d => d !== day)
        : [...prev, day].sort()
    );
  };

  const getScheduleStatus = () => {
    if (!schedule) return null;

    const statusMap = {
      immediate: { icon: PlayCircle, label: "Running", color: "bg-green-500" },
      scheduled: { icon: Clock, label: "Scheduled", color: "bg-blue-500" },
      recurring: { icon: Repeat, label: "Recurring", color: "bg-purple-500" }
    };

    const status = statusMap[schedule.scheduleType as keyof typeof statusMap];
    if (!status) return null;

    const Icon = status.icon;
    return (
      <Badge className="flex items-center gap-1">
        <Icon className="h-3 w-3" />
        {status.label}
      </Badge>
    );
  };

  if (isLoading) {
    return <div className="p-4">Loading schedule...</div>;
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Calendar className="h-5 w-5" />
          Campaign Scheduling
          {getScheduleStatus()}
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        {schedule?.status === 'scheduled' || schedule?.status === 'active' ? (
          <div className="space-y-4">
            <div className="p-4 border rounded-lg bg-muted/50">
              <div className="space-y-2">
                <div className="text-sm font-medium">Current Schedule:</div>
                <div className="text-sm text-muted-foreground">
                  {schedule.scheduleType === 'immediate' && "Campaign is running immediately"}
                  {schedule.scheduleType === 'scheduled' && schedule.scheduledStart && 
                    `Scheduled for: ${new Date(schedule.scheduledStart).toLocaleString()}`}
                  {schedule.scheduleType === 'recurring' && (
                    <>
                      <div>Pattern: {schedule.recurringPattern}</div>
                      {schedule.recurringDays && schedule.recurringDays.length > 0 && (
                        <div>Days: {schedule.recurringDays.map((d: number) => 
                          weekdays.find(w => w.value === d)?.label
                        ).join(', ')}</div>
                      )}
                      {schedule.recurringTime && <div>Time: {schedule.recurringTime}</div>}
                    </>
                  )}
                </div>
                {schedule.nextExecution && (
                  <div className="text-sm text-muted-foreground">
                    Next execution: {new Date(schedule.nextExecution).toLocaleString()}
                  </div>
                )}
              </div>
            </div>

            <div className="flex gap-2">
              {schedule.scheduleType === 'immediate' && schedule.status === 'draft' && (
                <Button 
                  onClick={() => executeMutation.mutate()}
                  disabled={executeMutation.isPending}
                >
                  <PlayCircle className="h-4 w-4 mr-2" />
                  Execute Now
                </Button>
              )}

              {(schedule.scheduleType === 'scheduled' || schedule.scheduleType === 'recurring') && (
                <Button 
                  variant="destructive" 
                  onClick={() => cancelMutation.mutate()}
                  disabled={cancelMutation.isPending}
                >
                  <PauseCircle className="h-4 w-4 mr-2" />
                  Cancel Schedule
                </Button>
              )}
            </div>
          </div>
        ) : (
          <div className="space-y-4">
            <div className="space-y-2">
              <Label>Schedule Type</Label>
              <Select value={scheduleType} onValueChange={(value: any) => setScheduleType(value)}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="immediate">Run Immediately</SelectItem>
                  <SelectItem value="scheduled">Schedule for Later</SelectItem>
                  <SelectItem value="recurring">Recurring Schedule</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {scheduleType === 'scheduled' && (
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label>Date</Label>
                  <Input
                    type="date"
                    value={scheduledDate}
                    onChange={(e) => setScheduledDate(e.target.value)}
                    min={new Date().toISOString().split('T')[0]}
                  />
                </div>
                <div className="space-y-2">
                  <Label>Time</Label>
                  <Input
                    type="time"
                    value={scheduledTime}
                    onChange={(e) => setScheduledTime(e.target.value)}
                  />
                </div>
              </div>
            )}

            {scheduleType === 'recurring' && (
              <div className="space-y-4">
                <div className="space-y-2">
                  <Label>Frequency</Label>
                  <Select value={recurringPattern} onValueChange={(value: any) => setRecurringPattern(value)}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="daily">Daily</SelectItem>
                      <SelectItem value="weekly">Weekly</SelectItem>
                      <SelectItem value="monthly">Monthly</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                {recurringPattern === 'weekly' && (
                  <div className="space-y-2">
                    <Label>Days of Week</Label>
                    <div className="flex gap-2 flex-wrap">
                      {weekdays.map((day) => (
                        <div key={day.value} className="flex items-center space-x-2">
                          <Checkbox
                            id={`day-${day.value}`}
                            checked={recurringDays.includes(day.value)}
                            onCheckedChange={() => handleDayToggle(day.value)}
                          />
                          <Label htmlFor={`day-${day.value}`} className="text-sm">
                            {day.label}
                          </Label>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                <div className="space-y-2">
                  <Label>Time</Label>
                  <Input
                    type="time"
                    value={recurringTime}
                    onChange={(e) => setRecurringTime(e.target.value)}
                  />
                </div>
              </div>
            )}

            <Button 
              onClick={handleSchedule} 
              disabled={scheduleMutation.isPending}
              className="w-full"
            >
              {scheduleType === 'immediate' ? 'Execute Campaign' : 'Schedule Campaign'}
            </Button>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/LiveChat.tsx.content:
import React, { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Send, Bot, User, Clock } from 'lucide-react';

interface Message {
  id: string;
  senderId: string;
  senderType: 'lead' | 'agent' | 'ai';
  content: string;
  timestamp: string;
}

interface LiveChatProps {
  leadId: string;
  conversationId: string;
  leadName?: string;
  onClose?: () => void;
}

export function LiveChat({ leadId, conversationId, leadName, onClose }: LiveChatProps) {
  const [messages, setMessages] = useState<Message[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const [isConnected, setIsConnected] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const wsRef = useRef<WebSocket | null>(null);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Auto-scroll to bottom when new messages arrive
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Initialize WebSocket connection
  useEffect(() => {
    const protocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:';
    const wsUrl = `${protocol}//${window.location.host}/ws/conversations?leadId=${leadId}&conversationId=${conversationId}`;

    const ws = new WebSocket(wsUrl);
    wsRef.current = ws;

    ws.onopen = () => {
      setIsConnected(true);
      console.log('WebSocket connected');
    };

    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);

        switch (data.type) {
          case 'connected':
            console.log('Chat session established');
            break;

          case 'new_message':
          case 'ai_response':
          case 'agent_message':
            setMessages(prev => [...prev, data.message]);
            if (data.type === 'ai_response') {
              setIsTyping(false);
            }
            break;

          case 'error':
            console.error('WebSocket error:', data.message);
            break;

          default:
            console.log('Unknown message type:', data.type);
        }
      } catch (error) {
        console.error('Error parsing WebSocket message:', error);
      }
    };

    ws.onclose = () => {
      setIsConnected(false);
      console.log('WebSocket disconnected');
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
      setIsConnected(false);
    };

    // Cleanup on unmount
    return () => {
      ws.close();
    };
  }, [leadId, conversationId]);

  const sendMessage = () => {
    if (!newMessage.trim() || !wsRef.current || !isConnected) return;

    const message = {
      content: newMessage.trim(),
      metadata: {
        timestamp: new Date().toISOString()
      }
    };

    wsRef.current.send(JSON.stringify(message));
    setNewMessage('');
    setIsTyping(true); // Show AI is thinking
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const getSenderIcon = (senderType: string) => {
    switch (senderType) {
      case 'ai':
        return <Bot className="h-4 w-4 text-blue-600" />;
      case 'agent':
        return <User className="h-4 w-4 text-green-600" />;
      case 'lead':
        return <User className="h-4 w-4 text-gray-600" />;
      default:
        return <User className="h-4 w-4 text-gray-600" />;
    }
  };

  const getSenderName = (senderType: string, senderId: string) => {
    switch (senderType) {
      case 'ai':
        return 'AI Assistant';
      case 'agent':
        return 'Agent';
      case 'lead':
        return leadName || 'Customer';
      default:
        return senderId;
    }
  };

  const formatTimestamp = (timestamp: string) => {
    return new Date(timestamp).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  return (
    <Card className="h-[600px] flex flex-col">
      <CardHeader className="flex flex-row items-center justify-between py-3">
        <CardTitle className="text-lg flex items-center space-x-2">
          <span>Live Chat - {leadName || 'Customer'}</span>
          <Badge variant={isConnected ? 'default' : 'destructive'}>
            {isConnected ? 'Connected' : 'Disconnected'}
          </Badge>
        </CardTitle>
        {onClose && (
          <Button variant="ghost" size="sm" onClick={onClose}>
            Ã—
          </Button>
        )}
      </CardHeader>

      <CardContent className="flex-1 flex flex-col p-0">
        {/* Messages Area */}
        <ScrollArea className="flex-1 p-4">
          <div className="space-y-4">
            {messages.length === 0 ? (
              <div className="text-center text-gray-500 py-8">
                <Bot className="h-12 w-12 mx-auto mb-2 text-gray-400" />
                <p>No messages yet. Start a conversation!</p>
              </div>
            ) : (
              messages.map((message) => (
                <div
                  key={message.id}
                  className={`flex items-start space-x-3 ${
                    message.senderType === 'lead' ? 'justify-end' : 'justify-start'
                  }`}
                >
                  {message.senderType !== 'lead' && (
                    <div className="flex-shrink-0">
                      {getSenderIcon(message.senderType)}
                    </div>
                  )}

                  <div
                    className={`max-w-[80%] rounded-lg p-3 ${
                      message.senderType === 'lead'
                        ? 'bg-blue-600 text-white ml-auto'
                        : message.senderType === 'ai'
                        ? 'bg-gray-100 text-gray-900'
                        : 'bg-green-100 text-green-900'
                    }`}
                  >
                    <div className="flex items-center justify-between mb-1">
                      <span className="text-xs font-medium opacity-75">
                        {getSenderName(message.senderType, message.senderId)}
                      </span>
                      <span className="text-xs opacity-75 ml-2">
                        {formatTimestamp(message.timestamp)}
                      </span>
                    </div>
                    <p className="text-sm whitespace-pre-wrap">{message.content}</p>
                  </div>

                  {message.senderType === 'lead' && (
                    <div className="flex-shrink-0">
                      {getSenderIcon(message.senderType)}
                    </div>
                  )}
                </div>
              ))
            )}

            {/* AI Typing Indicator */}
            {isTyping && (
              <div className="flex items-start space-x-3">
                <Bot className="h-4 w-4 text-blue-600 mt-1" />
                <div className="bg-gray-100 rounded-lg p-3">
                  <div className="flex items-center space-x-1">
                    <span className="text-xs text-gray-500">AI is typing</span>
                    <div className="flex space-x-1">
                      <div className="w-1 h-1 bg-gray-400 rounded-full animate-bounce"></div>
                      <div className="w-1 h-1 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                      <div className="w-1 h-1 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                    </div>
                  </div>
                </div>
              </div>
            )}

            <div ref={messagesEndRef} />
          </div>
        </ScrollArea>

        {/* Message Input */}
        <div className="border-t p-4">
          <div className="flex items-center space-x-2">
            <Input
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder={isConnected ? "Type your message..." : "Connecting..."}
              disabled={!isConnected}
              className="flex-1"
            />
            <Button
              onClick={sendMessage}
              disabled={!isConnected || !newMessage.trim()}
              size="sm"
            >
              <Send className="h-4 w-4" />
            </Button>
          </div>

          {!isConnected && (
            <div className="flex items-center justify-center mt-2 text-sm text-gray-500">
              <Clock className="h-4 w-4 mr-1" />
              Connecting to chat server...
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/SMSIntegration.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { MessageSquare, Phone, CheckCircle, XCircle, Clock } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";

interface SMSIntegrationProps {
  campaignId: string;
  leadId?: string;
  communicationType?: 'email' | 'email_sms' | 'sms';
  onCommunicationTypeChange?: (type: 'email' | 'email_sms' | 'sms') => void;
}

export function SMSIntegration({ 
  campaignId, 
  leadId, 
  communicationType = 'email',
  onCommunicationTypeChange 
}: SMSIntegrationProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [customOptInMessage, setCustomOptInMessage] = useState('');

  // Get SMS status for lead if provided
  const { data: smsStatus, isLoading } = useQuery({
    queryKey: ['/api/leads', leadId, 'sms-status'],
    enabled: !!leadId,
  });

  // Send SMS opt-in mutation
  const optInMutation = useMutation({
    mutationFn: async () => {
      return apiRequest('/api/sms/opt-in', 'POST', {
        leadId,
        campaignId,
        optInMessage: customOptInMessage || undefined,
      });
    },
    onSuccess: () => {
      toast({
        title: "SMS Opt-in Sent",
        description: "SMS opt-in request has been sent to the lead.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/leads', leadId, 'sms-status'] });
    },
    onError: (error) => {
      toast({
        title: "Failed to Send SMS",
        description: "Could not send SMS opt-in request. Please try again.",
        variant: "destructive",
      });
      console.error('SMS opt-in error:', error);
    },
  });

  const getSMSStatusBadge = () => {
    if (!smsStatus) return null;

    if (!smsStatus.hasPhone) {
      return (
        <Badge variant="secondary" className="flex items-center gap-1">
          <XCircle className="h-3 w-3" />
          No Phone Number
        </Badge>
      );
    }

    switch (smsStatus.optInStatus) {
      case 'opted-in':
        return (
          <Badge className="flex items-center gap-1 bg-green-500">
            <CheckCircle className="h-3 w-3" />
            SMS Enabled
          </Badge>
        );
      case 'opted-out':
        return (
          <Badge variant="destructive" className="flex items-center gap-1">
            <XCircle className="h-3 w-3" />
            SMS Declined
          </Badge>
        );
      case 'pending':
        return (
          <Badge variant="outline" className="flex items-center gap-1">
            <Clock className="h-3 w-3" />
            Waiting Response
          </Badge>
        );
      default:
        return (
          <Badge variant="secondary" className="flex items-center gap-1">
            <MessageSquare className="h-3 w-3" />
            Not Requested
          </Badge>
        );
    }
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <MessageSquare className="h-5 w-5" />
          Communication Settings
          {leadId && getSMSStatusBadge()}
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="space-y-4">
          <div className="space-y-2">
            <Label>Communication Type</Label>
            <Select 
              value={communicationType} 
              onValueChange={(value: any) => onCommunicationTypeChange?.(value)}
            >
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="email">Email Only</SelectItem>
                <SelectItem value="email_sms">Email â†’ SMS Handover</SelectItem>
                <SelectItem value="sms">SMS Only</SelectItem>
              </SelectContent>
            </Select>
            <div className="text-sm text-muted-foreground">
              {communicationType === 'email' && "Campaign will use email communication only."}
              {communicationType === 'email_sms' && "Start with email, offer SMS option if customer shows interest."}
              {communicationType === 'sms' && "Campaign will use SMS communication only."}
            </div>
          </div>

          {communicationType === 'email_sms' && (
            <div className="p-4 border rounded-lg bg-muted/50">
              <div className="space-y-3">
                <div className="text-sm font-medium">Email â†’ SMS Handover</div>
                <div className="text-sm text-muted-foreground">
                  When a customer responds positively to your emails or shows interest, 
                  the system will automatically offer SMS communication for faster responses.
                </div>
                <div className="text-xs text-muted-foreground">
                  Default message: "Would you like to continue this conversation via text? 
                  Reply YES to receive SMS updates about your automotive interests."
                </div>
              </div>
            </div>
          )}

          {leadId && smsStatus && (
            <div className="space-y-4">
              <div className="space-y-2">
                <Label>Lead SMS Status</Label>
                <div className="p-3 border rounded-lg">
                  <div className="grid grid-cols-2 gap-4 text-sm">
                    <div>
                      <span className="font-medium">Phone Number:</span>
                      <div className="text-muted-foreground">
                        {smsStatus.hasPhone ? "Available" : "Not provided"}
                      </div>
                    </div>
                    <div>
                      <span className="font-medium">SMS Status:</span>
                      <div className="text-muted-foreground">
                        {smsStatus.optInStatus === 'opted-in' && "Customer opted in for SMS"}
                        {smsStatus.optInStatus === 'opted-out' && "Customer declined SMS"}
                        {smsStatus.optInStatus === 'pending' && "Waiting for customer response"}
                        {smsStatus.optInStatus === 'unknown' && "SMS not requested yet"}
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {smsStatus.hasPhone && smsStatus.optInStatus === 'unknown' && (
                <div className="space-y-3">
                  <div className="space-y-2">
                    <Label>Custom Opt-in Message (Optional)</Label>
                    <Textarea
                      placeholder="Enter a custom SMS opt-in message or leave empty to use default"
                      value={customOptInMessage}
                      onChange={(e) => setCustomOptInMessage(e.target.value)}
                      rows={3}
                    />
                  </div>

                  <Button 
                    onClick={() => optInMutation.mutate()}
                    disabled={optInMutation.isPending}
                    className="w-full"
                  >
                    <Phone className="h-4 w-4 mr-2" />
                    Send SMS Opt-in Request
                  </Button>
                </div>
              )}

              {smsStatus.optInStatus === 'pending' && (
                <div className="p-3 border rounded-lg bg-blue-50 dark:bg-blue-950/20">
                  <div className="text-sm text-blue-700 dark:text-blue-300">
                    <Clock className="h-4 w-4 inline mr-1" />
                    SMS opt-in request sent. Waiting for customer response.
                  </div>
                </div>
              )}

              {smsStatus.optInStatus === 'opted-in' && (
                <div className="p-3 border rounded-lg bg-green-50 dark:bg-green-950/20">
                  <div className="text-sm text-green-700 dark:text-green-300">
                    <CheckCircle className="h-4 w-4 inline mr-1" />
                    Customer has opted in for SMS communication. Future messages can be sent via SMS.
                  </div>
                </div>
              )}

              {smsStatus.optInStatus === 'opted-out' && (
                <div className="p-3 border rounded-lg bg-red-50 dark:bg-red-950/20">
                  <div className="text-sm text-red-700 dark:text-red-300">
                    <XCircle className="h-4 w-4 inline mr-1" />
                    Customer has declined SMS communication. Continue using email only.
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/hooks/useWebSocket.ts.content:
import { useEffect, useRef, useState } from 'react';

interface WebSocketMessage {
  type: string;
  [key: string]: any;
}

interface UseWebSocketOptions {
  onMessage?: (message: WebSocketMessage) => void;
  onConnect?: () => void;
  onDisconnect?: () => void;
  reconnectInterval?: number;
  maxReconnectAttempts?: number;
}

export function useWebSocket(options: UseWebSocketOptions = {}) {
  const {
    onMessage,
    onConnect,
    onDisconnect,
    reconnectInterval = 3000,
    maxReconnectAttempts = 5
  } = options;

  const ws = useRef<WebSocket | null>(null);
  const [isConnected, setIsConnected] = useState(false);
  const [connectionState, setConnectionState] = useState<'connecting' | 'connected' | 'disconnected'>('disconnected');
  const reconnectAttempts = useRef(0);
  const reconnectTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  const connect = () => {
    if (ws.current?.readyState === WebSocket.OPEN) return;

    try {
      const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
      const wsUrl = `${protocol}//${window.location.host}/ws`;

      setConnectionState('connecting');
      ws.current = new WebSocket(wsUrl);

      ws.current.onopen = () => {
        console.log('WebSocket connected');
        setIsConnected(true);
        setConnectionState('connected');
        reconnectAttempts.current = 0;
        onConnect?.();
      };

      ws.current.onmessage = (event) => {
        try {
          const message = JSON.parse(event.data);
          onMessage?.(message);
        } catch (error) {
          console.error('WebSocket message parsing error:', error);
        }
      };

      ws.current.onclose = () => {
        console.log('WebSocket disconnected');
        setIsConnected(false);
        setConnectionState('disconnected');
        onDisconnect?.();

        // Attempt to reconnect if not manually closed
        if (reconnectAttempts.current < maxReconnectAttempts) {
          reconnectAttempts.current++;
          console.log(`Attempting to reconnect... (${reconnectAttempts.current}/${maxReconnectAttempts})`);

          reconnectTimeoutRef.current = setTimeout(() => {
            connect();
          }, reconnectInterval);
        }
      };

      ws.current.onerror = (error) => {
        console.error('WebSocket error:', error);
        setConnectionState('disconnected');
      };

    } catch (error) {
      console.error('WebSocket connection error:', error);
      setConnectionState('disconnected');
    }
  };

  const disconnect = () => {
    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current);
      reconnectTimeoutRef.current = null;
    }

    reconnectAttempts.current = maxReconnectAttempts; // Prevent reconnection

    if (ws.current) {
      ws.current.close();
      ws.current = null;
    }

    setIsConnected(false);
    setConnectionState('disconnected');
  };

  const sendMessage = (message: WebSocketMessage) => {
    if (ws.current?.readyState === WebSocket.OPEN) {
      ws.current.send(JSON.stringify(message));
      return true;
    } else {
      console.warn('WebSocket not connected. Message not sent:', message);
      return false;
    }
  };

  const joinConversation = (conversationId: string, userId: string) => {
    return sendMessage({
      type: 'join_conversation',
      conversationId,
      userId
    });
  };

  const sendChatMessage = (content: string) => {
    return sendMessage({
      type: 'send_message',
      content
    });
  };

  useEffect(() => {
    connect();

    return () => {
      disconnect();
    };
  }, []);

  return {
    isConnected,
    connectionState,
    sendMessage,
    joinConversation,
    sendChatMessage,
    connect,
    disconnect
  };
}

