import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Bell, Mail, AlertTriangle, TrendingUp, Settings, Send, CheckCircle } from "lucide-react";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Alert,
  AlertDescription,
  AlertTitle,
} from "@/components/ui/alert";

interface NotificationPreferences {
  emailNotifications: boolean;
  campaignAlerts: boolean;
  leadAlerts: boolean;
  systemAlerts: boolean;
  monthlyReports: boolean;
  highEngagementAlerts: boolean;
  quotaWarnings: boolean;
}

interface NotificationType {
  type: string;
  name: string;
  description: string;
  urgency: 'low' | 'medium' | 'high';
}

export default function NotificationsPage() {
  const [testNotificationType, setTestNotificationType] = useState<string>("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Get current user ID (in real app, this would come from auth context)
  const userId = "075f86dc-d36e-4ef2-ab61-2919f9468515"; // Default user ID for demo

  const { data: preferences, isLoading: preferencesLoading } = useQuery({
    queryKey: [`/api/notifications/preferences/${userId}`],
  });

  const { data: notificationTypes, isLoading: typesLoading } = useQuery({
    queryKey: ["/api/notifications/types"],
  });

  const updatePreferencesMutation = useMutation({
    mutationFn: async (newPreferences: NotificationPreferences) => {
      return await apiRequest(`/api/notifications/preferences/${userId}`, "PUT", newPreferences);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Notification preferences updated successfully",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/notifications/preferences/${userId}`] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update notification preferences",
        variant: "destructive",
      });
    },
  });

  const sendTestMutation = useMutation({
    mutationFn: async (type: string) => {
      return await apiRequest(`/api/notifications/test/${userId}`, "POST", { type });
    },
    onSuccess: (data) => {
      toast({
        title: "Test Notification Sent",
        description: `${data.type} notification sent successfully to your email`,
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to send test notification",
        variant: "destructive",
      });
    },
  });

  const handlePreferenceChange = (key: keyof NotificationPreferences, value: boolean) => {
    if (!preferences?.preferences) return;

    const newPreferences = {
      ...preferences.preferences,
      [key]: value,
    };
    updatePreferencesMutation.mutate(newPreferences);
  };

  const handleSendTest = () => {
    if (!testNotificationType) {
      toast({
        title: "Error",
        description: "Please select a notification type to test",
        variant: "destructive",
      });
      return;
    }
    sendTestMutation.mutate(testNotificationType);
  };

  const getUrgencyColor = (urgency: string) => {
    switch (urgency) {
      case 'high': return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200';
      case 'medium': return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200';
      case 'low': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200';
    }
  };

  const getUrgencyIcon = (urgency: string) => {
    switch (urgency) {
      case 'high': return <AlertTriangle className="h-4 w-4" />;
      case 'medium': return <Bell className="h-4 w-4" />;
      case 'low': return <CheckCircle className="h-4 w-4" />;
      default: return <Bell className="h-4 w-4" />;
    }
  };

  if (preferencesLoading || typesLoading) {
    return (
      <div className="p-6">
        <div className="animate-pulse space-y-4">
          <div className="h-8 bg-gray-200 rounded w-1/4"></div>
          <div className="h-32 bg-gray-200 rounded"></div>
          <div className="h-32 bg-gray-200 rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6">
      <div className="flex items-center space-x-2">
        <Bell className="h-6 w-6 text-blue-600" />
        <h1 className="text-2xl font-bold text-gray-900 dark:text-white">
          Notification Settings
        </h1>
      </div>

      <Alert>
        <Mail className="h-4 w-4" />
        <AlertTitle>Email Notifications</AlertTitle>
        <AlertDescription>
          Manage your email notification preferences for campaigns, leads, and system alerts.
          All notifications will be sent to your registered email address.
        </AlertDescription>
      </Alert>

      {/* Notification Preferences */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <Settings className="h-5 w-5" />
            <span>Notification Preferences</span>
          </CardTitle>
          <CardDescription>
            Configure which notifications you want to receive via email
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          <div className="grid gap-4">
            {[
              {
                key: 'emailNotifications' as keyof NotificationPreferences,
                label: 'Email Notifications',
                description: 'Master switch for all email notifications'
              },
              {
                key: 'campaignAlerts' as keyof NotificationPreferences,
                label: 'Campaign Alerts',
                description: 'Notifications when campaigns are executed or completed'
              },
              {
                key: 'leadAlerts' as keyof NotificationPreferences,
                label: 'Lead Alerts',
                description: 'Notifications when new leads are assigned to campaigns'
              },
              {
                key: 'systemAlerts' as keyof NotificationPreferences,
                label: 'System Alerts',
                description: 'Important system notifications and maintenance alerts'
              },
              {
                key: 'monthlyReports' as keyof NotificationPreferences,
                label: 'Monthly Reports',
                description: 'Monthly performance summaries and analytics'
              },
              {
                key: 'highEngagementAlerts' as keyof NotificationPreferences,
                label: 'High Engagement Alerts',
                description: 'Notifications when campaigns show exceptional performance'
              },
              {
                key: 'quotaWarnings' as keyof NotificationPreferences,
                label: 'Quota Warnings',
                description: 'Alerts when approaching usage limits'
              }
            ].map((item) => (
              <div key={item.key} className="flex items-center justify-between p-4 border rounded-lg">
                <div className="space-y-1">
                  <h4 className="font-medium">{item.label}</h4>
                  <p className="text-sm text-gray-600 dark:text-gray-400">{item.description}</p>
                </div>
                <Switch
                  checked={preferences?.preferences?.[item.key] || false}
                  onCheckedChange={(checked) => handlePreferenceChange(item.key, checked)}
                  disabled={updatePreferencesMutation.isPending}
                />
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Notification Types */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <TrendingUp className="h-5 w-5" />
            <span>Available Notification Types</span>
          </CardTitle>
          <CardDescription>
            Overview of all notification types and their purposes
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid gap-4 md:grid-cols-2">
            {notificationTypes?.notificationTypes?.map((type: NotificationType) => (
              <div key={type.type} className="p-4 border rounded-lg space-y-3">
                <div className="flex items-center justify-between">
                  <h4 className="font-medium">{type.name}</h4>
                  <Badge className={getUrgencyColor(type.urgency)}>
                    <span className="flex items-center space-x-1">
                      {getUrgencyIcon(type.urgency)}
                      <span className="capitalize">{type.urgency}</span>
                    </span>
                  </Badge>
                </div>
                <p className="text-sm text-gray-600 dark:text-gray-400">{type.description}</p>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Test Notifications */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <Send className="h-5 w-5" />
            <span>Test Notifications</span>
          </CardTitle>
          <CardDescription>
            Send a test notification to verify your email configuration
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex space-x-4">
            <div className="flex-1">
              <Select value={testNotificationType} onValueChange={setTestNotificationType}>
                <SelectTrigger>
                  <SelectValue placeholder="Select notification type to test" />
                </SelectTrigger>
                <SelectContent>
                  {notificationTypes?.notificationTypes?.map((type: NotificationType) => (
                    <SelectItem key={type.type} value={type.type}>
                      {type.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <Button 
              onClick={handleSendTest}
              disabled={!testNotificationType || sendTestMutation.isPending}
            >
              {sendTestMutation.isPending ? "Sending..." : "Send Test"}
            </Button>
          </div>

          {testNotificationType && notificationTypes?.notificationTypes && (
            <div className="p-3 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
              <p className="text-sm text-blue-700 dark:text-blue-300">
                <strong>Test Preview:</strong>{' '}
                {notificationTypes.notificationTypes.find((t: NotificationType) => t.type === testNotificationType)?.description}
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/scoring-config.tsx.content:
import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
// import { Slider } from '@/components/ui/slider';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Badge } from '@/components/ui/badge';
import { 
  Settings, 
  Target, 
  Car, 
  Building, 
  Crown,
  Save,
  Plus,
  Trash2
} from 'lucide-react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface ScoringProfile {
  id: string;
  name: string;
  type: 'dealership' | 'luxury' | 'commercial';
  description: string;
  weights: {
    response_speed: number;
    message_quality: number;
    vehicle_specificity: number;
    urgency_indicators: number;
    financial_readiness: number;
    engagement_frequency: number;
    contact_completeness: number;
    timing_patterns: number;
  };
  thresholds: {
    hot: number;
    warm: number;
  };
}

const defaultProfiles: ScoringProfile[] = [
  {
    id: 'dealership-standard',
    name: 'Standard Dealership',
    type: 'dealership',
    description: 'Balanced scoring for general automotive dealerships',
    weights: {
      response_speed: 20,
      message_quality: 15,
      vehicle_specificity: 25,
      urgency_indicators: 15,
      financial_readiness: 10,
      engagement_frequency: 5,
      contact_completeness: 5,
      timing_patterns: 5
    },
    thresholds: { hot: 70, warm: 40 }
  },
  {
    id: 'luxury-dealership',
    name: 'Luxury Dealership',
    type: 'luxury',
    description: 'Premium focus with emphasis on financial readiness and engagement quality',
    weights: {
      response_speed: 15,
      message_quality: 25,
      vehicle_specificity: 20,
      urgency_indicators: 10,
      financial_readiness: 20,
      engagement_frequency: 5,
      contact_completeness: 3,
      timing_patterns: 2
    },
    thresholds: { hot: 75, warm: 50 }
  },
  {
    id: 'commercial-fleet',
    name: 'Commercial Fleet',
    type: 'commercial',
    description: 'Volume-focused scoring for commercial and fleet sales',
    weights: {
      response_speed: 25,
      message_quality: 10,
      vehicle_specificity: 30,
      urgency_indicators: 20,
      financial_readiness: 5,
      engagement_frequency: 5,
      contact_completeness: 3,
      timing_patterns: 2
    },
    thresholds: { hot: 65, warm: 35 }
  },
  {
    id: 'subprime-automotive',
    name: 'Sub-Prime Automotive',
    type: 'dealership',
    description: 'Optimized scoring for sub-prime automotive customers with focus on engagement and urgency',
    weights: {
      response_speed: 30,
      message_quality: 10,
      vehicle_specificity: 15,
      urgency_indicators: 25,
      financial_readiness: 5,
      engagement_frequency: 10,
      contact_completeness: 3,
      timing_patterns: 2
    },
    thresholds: { hot: 60, warm: 30 }
  }
];

export default function ScoringConfigPage() {
  const [selectedProfile, setSelectedProfile] = useState<ScoringProfile>(defaultProfiles[0]);
  const [isEditing, setIsEditing] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: profiles = defaultProfiles } = useQuery({
    queryKey: ['/api/intelligence/scoring-profiles']
  });

  const saveMutation = useMutation({
    mutationFn: async (profile: ScoringProfile) => {
      return apiRequest(`/api/intelligence/scoring-profiles`, 'POST', profile);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/intelligence/scoring-profiles'] });
      toast({ title: "Success", description: "Scoring profile saved successfully" });
      setIsEditing(false);
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to save scoring profile", variant: "destructive" });
    }
  });

  const handleWeightChange = (criterion: string, value: number[]) => {
    setSelectedProfile(prev => ({
      ...prev,
      weights: {
        ...prev.weights,
        [criterion]: value[0]
      }
    }));
  };

  const handleThresholdChange = (threshold: 'hot' | 'warm', value: number[]) => {
    setSelectedProfile(prev => ({
      ...prev,
      thresholds: {
        ...prev.thresholds,
        [threshold]: value[0]
      }
    }));
  };

  const getProfileIcon = (type: string) => {
    switch (type) {
      case 'luxury': return <Crown className="h-4 w-4" />;
      case 'commercial': return <Building className="h-4 w-4" />;
      default: return <Car className="h-4 w-4" />;
    }
  };

  const getProfileColor = (type: string) => {
    switch (type) {
      case 'luxury': return 'bg-purple-100 text-purple-800';
      case 'commercial': return 'bg-blue-100 text-blue-800';
      default: return 'bg-green-100 text-green-800';
    }
  };

  return (
    <div className="space-y-6 p-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold flex items-center">
            <Settings className="h-8 w-8 mr-3 text-blue-600" />
            Lead Scoring Configuration
          </h1>
          <p className="text-gray-600">Configure scoring weights and thresholds for different automotive use cases</p>
        </div>
        <Button onClick={() => setIsEditing(!isEditing)}>
          {isEditing ? 'Cancel' : 'Edit Profile'}
        </Button>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
        {/* Profile Selection */}
        <div className="space-y-4">
          <h3 className="font-medium">Scoring Profiles</h3>
          {(profiles as ScoringProfile[]).map((profile: ScoringProfile) => (
            <Card 
              key={profile.id} 
              className={`cursor-pointer transition-colors ${selectedProfile.id === profile.id ? 'ring-2 ring-blue-500' : ''}`}
              onClick={() => setSelectedProfile(profile)}
            >
              <CardContent className="p-4">
                <div className="flex items-center space-x-2 mb-2">
                  {getProfileIcon(profile.type)}
                  <span className="font-medium">{profile.name}</span>
                </div>
                <Badge className={getProfileColor(profile.type)} variant="outline">
                  {profile.type}
                </Badge>
                <p className="text-xs text-gray-500 mt-2">{profile.description}</p>
              </CardContent>
            </Card>
          ))}
        </div>

        {/* Configuration Panel */}
        <div className="lg:col-span-3">
          <Tabs defaultValue="weights" className="space-y-4">
            <TabsList>
              <TabsTrigger value="weights">Scoring Weights</TabsTrigger>
              <TabsTrigger value="thresholds">Priority Thresholds</TabsTrigger>
              <TabsTrigger value="preview">Preview & Test</TabsTrigger>
            </TabsList>

            <TabsContent value="weights" className="space-y-4">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center">
                    <Target className="h-5 w-5 mr-2" />
                    {selectedProfile.name} - Scoring Weights
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-6">
                  {Object.entries(selectedProfile.weights).map(([criterion, weight]) => (
                    <div key={criterion} className="space-y-2">
                      <div className="flex justify-between items-center">
                        <Label className="text-sm font-medium capitalize">
                          {criterion.replace('_', ' ')}
                        </Label>
                        <span className="text-sm text-gray-500">{weight}%</span>
                      </div>
                      <Input
                        type="range"
                        min="0"
                        max="50"
                        step="5"
                        value={weight}
                        onChange={(e) => handleWeightChange(criterion, [parseInt(e.target.value)])}
                        disabled={!isEditing}
                        className="w-full"
                      />
                      <div className="text-xs text-gray-500">
                        {criterion === 'response_speed' && 'How quickly the lead responds to initial contact'}
                        {criterion === 'message_quality' && 'Quality and detail level of lead messages'}
                        {criterion === 'vehicle_specificity' && 'Specific interest in particular vehicles or features'}
                        {criterion === 'urgency_indicators' && 'Language indicating immediate purchase intent'}
                        {criterion === 'financial_readiness' && 'Evidence of financing or budget preparation'}
                        {criterion === 'engagement_frequency' && 'How often the lead initiates contact'}
                        {criterion === 'contact_completeness' && 'Completeness of contact information provided'}
                        {criterion === 'timing_patterns' && 'Contact timing indicating serious interest'}
                      </div>
                    </div>
                  ))}

                  <div className="pt-4 border-t">
                    <p className="text-sm text-gray-600">
                      Total Weight: {Object.values(selectedProfile.weights).reduce((a, b) => a + b, 0)}%
                    </p>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="thresholds" className="space-y-4">
              <Card>
                <CardHeader>
                  <CardTitle>Priority Thresholds</CardTitle>
                </CardHeader>
                <CardContent className="space-y-6">
                  <div className="space-y-4">
                    <div className="space-y-2">
                      <div className="flex justify-between items-center">
                        <Label className="text-sm font-medium">Hot Lead Threshold</Label>
                        <span className="text-sm text-gray-500">{selectedProfile.thresholds.hot}%</span>
                      </div>
                      <Input
                        type="range"
                        min="0"
                        max="100"
                        step="5"
                        value={selectedProfile.thresholds.hot}
                        onChange={(e) => handleThresholdChange('hot', [parseInt(e.target.value)])}
                        disabled={!isEditing}
                        className="w-full"
                      />
                      <p className="text-xs text-gray-500">Leads scoring above this threshold are marked as "Hot"</p>
                    </div>

                    <div className="space-y-2">
                      <div className="flex justify-between items-center">
                        <Label className="text-sm font-medium">Warm Lead Threshold</Label>
                        <span className="text-sm text-gray-500">{selectedProfile.thresholds.warm}%</span>
                      </div>
                      <Input
                        type="range"
                        min="0"
                        max="100"
                        step="5"
                        value={selectedProfile.thresholds.warm}
                        onChange={(e) => handleThresholdChange('warm', [parseInt(e.target.value)])}
                        disabled={!isEditing}
                        className="w-full"
                      />
                      <p className="text-xs text-gray-500">Leads scoring above this threshold are marked as "Warm"</p>
                    </div>
                  </div>

                  <div className="bg-gray-50 p-4 rounded-lg">
                    <h4 className="font-medium mb-2">Priority Classification</h4>
                    <div className="space-y-2 text-sm">
                      <div className="flex items-center justify-between">
                        <span className="flex items-center">
                          <div className="w-3 h-3 bg-red-500 rounded-full mr-2"></div>
                          Hot Leads
                        </span>
                        <span>{selectedProfile.thresholds.hot}%+ score</span>
                      </div>
                      <div className="flex items-center justify-between">
                        <span className="flex items-center">
                          <div className="w-3 h-3 bg-orange-500 rounded-full mr-2"></div>
                          Warm Leads
                        </span>
                        <span>{selectedProfile.thresholds.warm}% - {selectedProfile.thresholds.hot - 1}% score</span>
                      </div>
                      <div className="flex items-center justify-between">
                        <span className="flex items-center">
                          <div className="w-3 h-3 bg-blue-500 rounded-full mr-2"></div>
                          Cold Leads
                        </span>
                        <span>Below {selectedProfile.thresholds.warm}% score</span>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="preview" className="space-y-4">
              <Card>
                <CardHeader>
                  <CardTitle>Configuration Preview</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div>
                      <h4 className="font-medium mb-2">Profile Summary</h4>
                      <div className="bg-gray-50 p-4 rounded-lg">
                        <div className="flex items-center space-x-2 mb-2">
                          {getProfileIcon(selectedProfile.type)}
                          <span className="font-medium">{selectedProfile.name}</span>
                          <Badge className={getProfileColor(selectedProfile.type)}>
                            {selectedProfile.type}
                          </Badge>
                        </div>
                        <p className="text-sm text-gray-600">{selectedProfile.description}</p>
                      </div>
                    </div>

                    <div>
                      <h4 className="font-medium mb-2">Top Weighted Criteria</h4>
                      <div className="space-y-2">
                        {Object.entries(selectedProfile.weights)
                          .sort(([,a], [,b]) => b - a)
                          .slice(0, 5)
                          .map(([criterion, weight]) => (
                            <div key={criterion} className="flex justify-between items-center">
                              <span className="text-sm capitalize">{criterion.replace('_', ' ')}</span>
                              <span className="text-sm font-medium">{weight}%</span>
                            </div>
                          ))}
                      </div>
                    </div>

                    {isEditing && (
                      <div className="pt-4">
                        <Button 
                          onClick={() => saveMutation.mutate(selectedProfile)}
                          disabled={saveMutation.isPending}
                          className="w-full"
                        >
                          <Save className="h-4 w-4 mr-2" />
                          {saveMutation.isPending ? 'Saving...' : 'Save Configuration'}
                        </Button>
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        </div>
      </div>
    </div>
  );
}

server/services/campaign-execution/LeadA.content:
import { storage } from '../../storage';
import { webSocketService } from '../websocket';
import type { Lead, Campaign, Conversation } from '@shared/schema';

export interface AssignmentRule {
  id: string;
  name: string;
  enabled: boolean;
  criteria: {
    vehicleInterest?: string[];
    budget?: { min?: number; max?: number };
    timeframe?: string[];
    source?: string[];
    location?: string[];
  };
  priority: number; // Higher number = higher priority
  assignTo?: string; // User ID or team
  campaignId?: string;
}

export interface AssignmentResult {
  success: boolean;
  assignedLeads: number;
  skippedLeads: number;
  errors: string[];
  assignments: Array<{
    leadId: string;
    campaignId?: string;
    conversationId?: string;
    assignedTo?: string;
  }>;
}

export class LeadAssignmentService {
  private assignmentRules: AssignmentRule[] = [
    {
      id: 'high_value_leads',
      name: 'High Value Leads',
      enabled: true,
      criteria: {
        budget: { min: 50000 },
        timeframe: ['immediate', 'within_month']
      },
      priority: 10,
      assignTo: 'senior_sales'
    },
    {
      id: 'luxury_vehicles',
      name: 'Luxury Vehicle Interest',
      enabled: true,
      criteria: {
        vehicleInterest: ['BMW', 'Mercedes', 'Audi', 'Lexus', 'Tesla']
      },
      priority: 8,
      assignTo: 'luxury_specialist'
    },
    {
      id: 'quick_conversion',
      name: 'Quick Conversion Potential',
      enabled: true,
      criteria: {
        timeframe: ['immediate', 'within_week']
      },
      priority: 7,
      assignTo: 'conversion_team'
    }
  ];

  /**
   * Assign leads to campaigns based on intelligent rules
   */
  async assignLeadsToCampaigns(
    leads: Lead[],
    availableCampaigns: Campaign[]
  ): Promise<AssignmentResult> {
    const result: AssignmentResult = {
      success: true,
      assignedLeads: 0,
      skippedLeads: 0,
      errors: [],
      assignments: []
    };

    try {
      for (const lead of leads) {
        try {
          const assignment = await this.assignSingleLead(lead, availableCampaigns);

          if (assignment.campaignId) {
            // Update lead with campaign assignment
            await storage.updateLead(lead.id, {
              campaignId: assignment.campaignId,
              status: 'assigned'
            });

            // Create conversation if needed
            if (assignment.createConversation) {
              const conversation = await storage.createConversation({
                subject: `Lead Assignment: ${lead.firstName} ${lead.lastName}`,
                status: 'active',
                priority: assignment.priority || 'normal',
                campaignId: assignment.campaignId
              });

              assignment.conversationId = conversation.id;
            }

            result.assignments.push({
              leadId: lead.id,
              campaignId: assignment.campaignId,
              conversationId: assignment.conversationId,
              assignedTo: assignment.assignedTo
            });

            result.assignedLeads++;

            // Send real-time notification
            webSocketService.broadcast('leadAssigned', {
              leadId: lead.id,
              campaignId: assignment.campaignId,
              assignmentReason: assignment.reason,
              timestamp: new Date()
            });

          } else {
            result.skippedLeads++;
          }

        } catch (leadError) {
          result.errors.push(`Failed to assign lead ${lead.id}: ${leadError instanceof Error ? leadError.message : 'Unknown error'}`);
          result.skippedLeads++;
        }
      }

      result.success = result.errors.length === 0 || result.assignedLeads > 0;

    } catch (error) {
      result.success = false;
      result.errors.push(`Assignment process failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }

    return result;
  }

  /**
   * Assign a single lead to the most appropriate campaign
   */
  private async assignSingleLead(
    lead: Lead, 
    availableCampaigns: Campaign[]
  ): Promise<{
    campaignId?: string;
    assignedTo?: string;
    priority?: 'low' | 'normal' | 'high' | 'urgent';
    reason?: string;
    createConversation?: boolean;
    conversationId?: string;
  }> {

    // Skip if lead already has a campaign
    if (lead.campaignId) {
      return { reason: 'Lead already assigned to campaign' };
    }

    // Find best matching rule
    const matchingRule = this.findBestMatchingRule(lead);

    if (!matchingRule) {
      // Default assignment to general campaign
      const generalCampaign = availableCampaigns.find(c => 
        c.status === 'active' && 
        (c.name.toLowerCase().includes('general') || c.name.toLowerCase().includes('default'))
      );

      return {
        campaignId: generalCampaign?.id,
        priority: 'normal',
        reason: 'Default assignment - no specific rules matched',
        createConversation: true
      };
    }

    // Find campaign matching the rule
    let targetCampaign: Campaign | undefined;

    if (matchingRule.campaignId) {
      targetCampaign = availableCampaigns.find(c => c.id === matchingRule.campaignId);
    } else {
      // Find campaign based on criteria
      targetCampaign = this.findMatchingCampaign(lead, availableCampaigns, matchingRule);
    }

    if (!targetCampaign) {
      // Fall back to any active campaign
      targetCampaign = availableCampaigns.find(c => c.status === 'active');
    }

    const priority = this.determinePriority(matchingRule.priority);

    return {
      campaignId: targetCampaign?.id,
      assignedTo: matchingRule.assignTo,
      priority,
      reason: `Matched rule: ${matchingRule.name}`,
      createConversation: true
    };
  }

  /**
   * Find the best matching assignment rule for a lead
   */
  private findBestMatchingRule(lead: Lead): AssignmentRule | null {
    const activeRules = this.assignmentRules
      .filter(rule => rule.enabled)
      .sort((a, b) => b.priority - a.priority); // Sort by priority (highest first)

    for (const rule of activeRules) {
      if (this.leadMatchesRule(lead, rule)) {
        return rule;
      }
    }

    return null;
  }

  /**
   * Check if a lead matches a specific rule
   */
  private leadMatchesRule(lead: Lead, rule: AssignmentRule): boolean {
    const { criteria } = rule;

    // Check vehicle interest
    if (criteria.vehicleInterest && criteria.vehicleInterest.length > 0) {
      if (!lead.vehicleInterest || 
          !criteria.vehicleInterest.some(interest => 
            lead.vehicleInterest!.toLowerCase().includes(interest.toLowerCase())
          )) {
        return false;
      }
    }

    // Check budget range
    if (criteria.budget) {
      const leadBudget = null; // Note: budget field doesn't exist in Lead schema
      if (leadBudget !== null) {
        if (criteria.budget.min && leadBudget < criteria.budget.min) return false;
        if (criteria.budget.max && leadBudget > criteria.budget.max) return false;
      } else if (criteria.budget.min || criteria.budget.max) {
        return false; // Budget criteria specified but lead has no valid budget
      }
    }

    // Check timeframe
    if (criteria.timeframe && criteria.timeframe.length > 0) {
      if (!criteria.timeframe.includes('immediate')) { // Note: timeframe field doesn't exist in Lead schema
        return false;
      }
    }

    // Check source
    if (criteria.source && criteria.source.length > 0) {
      if (!lead.leadSource || 
          !criteria.source.includes(lead.leadSource.toLowerCase())) {
        return false;
      }
    }

    return true;
  }

  /**
   * Find matching campaign based on lead and rule criteria
   */
  private findMatchingCampaign(
    lead: Lead, 
    campaigns: Campaign[], 
    rule: AssignmentRule
  ): Campaign | undefined {

    const activeCampaigns = campaigns.filter(c => c.status === 'active');

    // Look for campaigns with relevant context/content
    if (lead.vehicleInterest) {
      const vehicleSpecificCampaign = activeCampaigns.find(c => 
        c.context?.toLowerCase().includes(lead.vehicleInterest!.toLowerCase()) ||
        c.name.toLowerCase().includes(lead.vehicleInterest!.toLowerCase())
      );
      if (vehicleSpecificCampaign) return vehicleSpecificCampaign;
    }

    // Look for timeframe-specific campaigns
    if (lead.timeframe) {
      const timeframeCampaign = activeCampaigns.find(c => 
        c.context?.toLowerCase().includes(lead.timeframe!.toLowerCase()) ||
        c.name.toLowerCase().includes(lead.timeframe!.toLowerCase())
      );
      if (timeframeCampaign) return timeframeCampaign;
    }

    // Return first active campaign as fallback
    return activeCampaigns[0];
  }

  /**
   * Parse budget string to number
   */
  private parseBudget(budget?: string): number | null {
    if (!budget) return null;

    // Remove non-numeric characters except decimal points and commas
    const cleaned = budget.replace(/[^\d.,]/g, '');

    // Handle common formats
    if (cleaned.includes('k') || cleaned.includes('K')) {
      return parseFloat(cleaned.replace(/[kK]/g, '')) * 1000;
    }

    const parsed = parseFloat(cleaned.replace(/,/g, ''));
    return isNaN(parsed) ? null : parsed;
  }

  /**
   * Determine conversation priority based on rule priority
   */
  private determinePriority(rulePriority: number): 'low' | 'normal' | 'high' | 'urgent' {
    if (rulePriority >= 10) return 'urgent';
    if (rulePriority >= 7) return 'high';
    if (rulePriority >= 4) return 'normal';
    return 'low';
  }

  /**
   * Get current assignment rules
   */
  getAssignmentRules(): AssignmentRule[] {
    return [...this.assignmentRules];
  }

  /**
   * Update assignment rules
   */
  updateAssignmentRules(rules: AssignmentRule[]): void {
    this.assignmentRules = rules;
    console.log(`Updated ${rules.length} assignment rules`);
  }

  /**
   * Add new assignment rule
   */
  addAssignmentRule(rule: AssignmentRule): void {
    this.assignmentRules.push(rule);
    console.log(`Added new assignment rule: ${rule.name}`);
  }

  /**
   * Remove assignment rule
   */
  removeAssignmentRule(ruleId: string): boolean {
    const initialLength = this.assignmentRules.length;
    this.assignmentRules = this.assignmentRules.filter(rule => rule.id !== ruleId);
    const removed = this.assignmentRules.length < initialLength;

    if (removed) {
      console.log(`Removed assignment rule: ${ruleId}`);
    }

    return removed;
  }
}

export const leadAssignmentService = new LeadAssignmentService();

