import { parse } from "csv-parse/sync";
import { z } from "zod";

// Validation schema for lead data
const leadValidationSchema = z.object({
  firstName: z.string().min(1, "First name is required").max(100),
  lastName: z.string().min(1, "Last name is required").max(100),
  email: z.string().email("Invalid email format"),
  phone: z.string().optional(),
  vehicleInterest: z.string().optional(),
  budget: z.string().optional(),
  timeframe: z.string().optional(),
  source: z.string().optional(),
  notes: z.string().optional(),
});

export interface ValidationResult {
  valid: boolean;
  data?: any[];
  errors?: string[];
  warnings?: string[];
  stats?: {
    totalRows: number;
    validRows: number;
    invalidRows: number;
    duplicateEmails: number;
  };
}

export interface CSVValidationOptions {
  maxFileSize?: number; // in bytes
  maxRows?: number;
  allowedColumns?: string[];
  requireColumns?: string[];
  sanitizeData?: boolean;
}

export class CSVValidationService {
  private static readonly DEFAULT_OPTIONS: CSVValidationOptions = {
    maxFileSize: 10 * 1024 * 1024, // 10MB
    maxRows: 10000,
    requireColumns: ['firstName', 'lastName', 'email'],
    sanitizeData: true,
  };

  /**
   * Validate and parse CSV file with comprehensive security checks
   */
  static async validateCSV(
    fileBuffer: Buffer,
    options: CSVValidationOptions = {}
  ): Promise<ValidationResult> {
    const config = { ...this.DEFAULT_OPTIONS, ...options };
    const errors: string[] = [];
    const warnings: string[] = [];

    try {
      // Security check: File size validation
      if (fileBuffer.length > config.maxFileSize!) {
        return {
          valid: false,
          errors: [`File size ${fileBuffer.length} bytes exceeds maximum allowed ${config.maxFileSize} bytes`]
        };
      }

      // Security check: Content validation (basic CSV format check)
      const content = fileBuffer.toString('utf8');

      // Check for suspicious content
      if (this.containsSuspiciousContent(content)) {
        return {
          valid: false,
          errors: ['File contains potentially malicious content']
        };
      }

      // Parse CSV with strict options
      let records: any[];
      try {
        records = parse(content, {
          columns: true,
          skip_empty_lines: true,
          trim: true,
          max_record_size: 10000, // Limit record size
          relax_column_count: true,
        });
      } catch (parseError) {
        return {
          valid: false,
          errors: [`CSV parsing failed: ${parseError instanceof Error ? parseError.message : 'Unknown error'}`]
        };
      }

      // Check row count limit
      if (records.length > config.maxRows!) {
        return {
          valid: false,
          errors: [`CSV contains ${records.length} rows, exceeding maximum allowed ${config.maxRows} rows`]
        };
      }

      if (records.length === 0) {
        return {
          valid: false,
          errors: ['CSV file is empty or contains no valid data rows']
        };
      }

      // Validate column headers
      const headers = Object.keys(records[0] || {});
      const columnValidation = this.validateColumns(headers, config);
      if (!columnValidation.valid) {
        return {
          valid: false,
          errors: columnValidation.errors
        };
      }

      // Validate and sanitize data
      const validatedRecords: any[] = [];
      const emailSet = new Set<string>();
      let duplicateCount = 0;

      for (let i = 0; i < records.length; i++) {
        const record = records[i];
        const rowNumber = i + 2; // +2 because CSV is 1-indexed and has header

        try {
          // Sanitize data if enabled
          const sanitizedRecord = config.sanitizeData 
            ? this.sanitizeRecord(record) 
            : record;

          // Validate against schema
          const validatedRecord = leadValidationSchema.parse(sanitizedRecord);

          // Check for duplicate emails
          if (emailSet.has(validatedRecord.email.toLowerCase())) {
            duplicateCount++;
            warnings.push(`Row ${rowNumber}: Duplicate email address ${validatedRecord.email}`);
            continue; // Skip duplicate
          }

          emailSet.add(validatedRecord.email.toLowerCase());
          validatedRecords.push(validatedRecord);

        } catch (validationError) {
          if (validationError instanceof z.ZodError) {
            const fieldErrors = validationError.errors
              .map(err => `${err.path.join('.')}: ${err.message}`)
              .join(', ');
            errors.push(`Row ${rowNumber}: ${fieldErrors}`);
          } else {
            errors.push(`Row ${rowNumber}: Validation failed - ${validationError instanceof Error ? validationError.message : 'Unknown error'}`);
          }
        }
      }

      // Determine if validation passed
      const hasValidData = validatedRecords.length > 0;
      const hasErrors = errors.length > 0;

      return {
        valid: hasValidData && !hasErrors,
        data: validatedRecords,
        errors: errors.length > 0 ? errors : undefined,
        warnings: warnings.length > 0 ? warnings : undefined,
        stats: {
          totalRows: records.length,
          validRows: validatedRecords.length,
          invalidRows: records.length - validatedRecords.length,
          duplicateEmails: duplicateCount,
        }
      };

    } catch (error) {
      return {
        valid: false,
        errors: [`CSV validation failed: ${error instanceof Error ? error.message : 'Unknown error'}`]
      };
    }
  }

  /**
   * Validate CSV column headers
   */
  private static validateColumns(
    headers: string[], 
    config: CSVValidationOptions
  ): { valid: boolean; errors?: string[] } {
    const errors: string[] = [];

    // Check required columns
    if (config.requireColumns) {
      const missingColumns = config.requireColumns.filter(
        col => !headers.includes(col)
      );
      if (missingColumns.length > 0) {
        errors.push(`Missing required columns: ${missingColumns.join(', ')}`);
      }
    }

    // Check for allowed columns (if specified)
    if (config.allowedColumns) {
      const invalidColumns = headers.filter(
        header => !config.allowedColumns!.includes(header)
      );
      if (invalidColumns.length > 0) {
        errors.push(`Invalid columns found: ${invalidColumns.join(', ')}`);
      }
    }

    return {
      valid: errors.length === 0,
      errors: errors.length > 0 ? errors : undefined
    };
  }

  /**
   * Sanitize record data to prevent injection attacks
   */
  private static sanitizeRecord(record: any): any {
    const sanitized: any = {};

    for (const [key, value] of Object.entries(record)) {
      if (typeof value === 'string') {
        // Remove potentially dangerous characters and limit length
        sanitized[key] = value
          .replace(/[<>\"'&]/g, '') // Remove HTML/script characters
          .replace(/\r?\n|\r/g, ' ') // Replace newlines with spaces
          .trim()
          .substring(0, 1000); // Limit length
      } else {
        sanitized[key] = value;
      }
    }

    return sanitized;
  }

  /**
   * Check for suspicious content that might indicate malicious files
   */
  private static containsSuspiciousContent(content: string): boolean {
    const suspiciousPatterns = [
      /<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi,
      /javascript:/gi,
      /data:text\/html/gi,
      /vbscript:/gi,
      /onload\s*=/gi,
      /onerror\s*=/gi,
      /\.exe\b/gi,
      /\.bat\b/gi,
      /\.cmd\b/gi,
    ];

    return suspiciousPatterns.some(pattern => pattern.test(content));
  }

  /**
   * Generate detailed validation report
   */
  static generateValidationReport(result: ValidationResult): string {
    const report: string[] = [];

    report.push(`CSV Validation Report`);
    report.push(`Status: ${result.valid ? 'PASSED' : 'FAILED'}`);

    if (result.stats) {
      report.push(`\nStatistics:`);
      report.push(`  Total rows processed: ${result.stats.totalRows}`);
      report.push(`  Valid rows: ${result.stats.validRows}`);
      report.push(`  Invalid rows: ${result.stats.invalidRows}`);
      report.push(`  Duplicate emails: ${result.stats.duplicateEmails}`);
    }

    if (result.errors && result.errors.length > 0) {
      report.push(`\nErrors:`);
      result.errors.forEach(error => report.push(`  âŒ ${error}`));
    }

    if (result.warnings && result.warnings.length > 0) {
      report.push(`\nWarnings:`);
      result.warnings.forEach(warning => report.push(`  âš ï¸ ${warning}`));
    }

    return report.join('\n');
  }
}

// Export for backward compatibility
export const validateCSV = CSVValidationService.validateCSV.bind(CSVValidationService);
export const generateValidationReport = CSVValidationService.generateValidationReport.bind(CSVValidationService);

client/src/components/campaigns/Campaign.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { Switch } from "@/components/ui/switch";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { 
  Play, 
  Users, 
  Mail, 
  BarChart3, 
  Calendar, 
  TestTube,
  AlertCircle,
  CheckCircle,
  Clock,
  Send
} from "lucide-react";

interface Campaign {
  id: string;
  name: string;
  status: string;
  templates: string;
  emailsSent?: number;
  createdAt: Date;
  lastExecuted?: Date;
}

interface CampaignExecutionModalProps {
  campaign: Campaign;
  children: React.ReactNode;
}

export default function CampaignExecutionModal({ campaign, children }: CampaignExecutionModalProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [testMode, setTestMode] = useState(false);
  const [scheduleAt, setScheduleAt] = useState('');
  const [executionResults, setExecutionResults] = useState<any>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  let templates: any[] = [];
  try {
    templates = JSON.parse(campaign.templates || '[]');
  } catch (error) {
    templates = [];
  }

  const executeCampaignMutation = useMutation({
    mutationFn: async (data: { scheduleAt?: string; testMode: boolean }) => {
      return await apiRequest(`/api/campaigns/${campaign.id}/execute`, "POST", data);
    },
    onSuccess: (result) => {
      setExecutionResults(result);
      queryClient.invalidateQueries({ queryKey: ["/api/campaigns"] });
      toast({
        title: testMode ? "Test Email Sent" : "Campaign Executed",
        description: result.message,
      });
    },
    onError: (error: unknown) => {
      const errorMessage = error instanceof Error ? error.message : "Failed to execute campaign";
      toast({
        title: "Execution Failed",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const sendFollowupMutation = useMutation({
    mutationFn: async (data: { templateIndex: number }) => {
      return await apiRequest(`/api/campaigns/${campaign.id}/send-followup`, "POST", data);
    },
    onSuccess: (result) => {
      queryClient.invalidateQueries({ queryKey: ["/api/campaigns"] });
      toast({
        title: "Follow-up Sent",
        description: result.message,
      });
    },
    onError: (error: unknown) => {
      const errorMessage = error instanceof Error ? error.message : "Failed to send follow-up";
      toast({
        title: "Follow-up Failed",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const handleExecute = () => {
    executeCampaignMutation.mutate({
      scheduleAt: scheduleAt || undefined,
      testMode
    });
  };

  const handleFollowup = (templateIndex: number) => {
    sendFollowupMutation.mutate({ templateIndex });
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "active":
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case "scheduled":
        return <Clock className="h-4 w-4 text-blue-500" />;
      case "draft":
        return <AlertCircle className="h-4 w-4 text-yellow-500" />;
      default:
        return <AlertCircle className="h-4 w-4 text-gray-500" />;
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        {children}
      </DialogTrigger>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Play className="h-5 w-5" />
            Execute Campaign: {campaign.name}
          </DialogTitle>
          <DialogDescription>
            Launch your automotive email campaign and track its performance
          </DialogDescription>
        </DialogHeader>

        <Tabs defaultValue="execute" className="space-y-4">
          <TabsList className="grid w-full grid-cols-3">
            <TabsTrigger value="execute">Execute</TabsTrigger>
            <TabsTrigger value="templates">Templates</TabsTrigger>
            <TabsTrigger value="analytics">Analytics</TabsTrigger>
          </TabsList>

          <TabsContent value="execute" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  {getStatusIcon(campaign.status)}
                  Campaign Status
                </CardTitle>
                <CardDescription>
                  Current status and execution settings
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex items-center justify-between">
                  <div className="space-y-1">
                    <p className="text-sm font-medium">Status</p>
                    <Badge variant={campaign.status === 'active' ? 'default' : 'secondary'}>
                      {campaign.status}
                    </Badge>
                  </div>
                  <div className="space-y-1 text-right">
                    <p className="text-sm font-medium">Emails Sent</p>
                    <p className="text-lg font-bold">{campaign.emailsSent || 0}</p>
                  </div>
                  <div className="space-y-1 text-right">
                    <p className="text-sm font-medium">Templates</p>
                    <p className="text-lg font-bold">{templates.length}</p>
                  </div>
                </div>

                <Separator />

                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <Label htmlFor="test-mode">Test Mode</Label>
                    <Switch
                      id="test-mode"
                      checked={testMode}
                      onCheckedChange={setTestMode}
                    />
                  </div>

                  {testMode && (
                    <div className="p-3 bg-yellow-50 border border-yellow-200 rounded-md">
                      <div className="flex items-center gap-2">
                        <TestTube className="h-4 w-4 text-yellow-600" />
                        <p className="text-sm text-yellow-800">
                          Test mode will send the first email template to only the first lead
                        </p>
                      </div>
                    </div>
                  )}

                  <div className="space-y-2">
                    <Label htmlFor="schedule">Schedule for Later (Optional)</Label>
                    <Input
                      id="schedule"
                      type="datetime-local"
                      value={scheduleAt}
                      onChange={(e) => setScheduleAt(e.target.value)}
                      min={new Date().toISOString().slice(0, 16)}
                    />
                  </div>

                  <Button 
                    onClick={handleExecute} 
                    disabled={executeCampaignMutation.isPending}
                    className="w-full"
                    size="lg"
                  >
                    {executeCampaignMutation.isPending ? (
                      "Executing..."
                    ) : scheduleAt ? (
                      <><Calendar className="h-4 w-4 mr-2" /> Schedule Campaign</>
                    ) : (
                      <><Send className="h-4 w-4 mr-2" /> {testMode ? "Send Test" : "Execute Now"}</>
                    )}
                  </Button>
                </div>
              </CardContent>
            </Card>

            {executionResults && (
              <Card>
                <CardHeader>
                  <CardTitle className="text-green-600">Execution Results</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    <div className="text-center">
                      <p className="text-2xl font-bold text-green-600">{executionResults.successful || 0}</p>
                      <p className="text-sm text-gray-600">Successful</p>
                    </div>
                    <div className="text-center">
                      <p className="text-2xl font-bold text-red-600">{executionResults.failed || 0}</p>
                      <p className="text-sm text-gray-600">Failed</p>
                    </div>
                    <div className="text-center">
                      <p className="text-2xl font-bold text-blue-600">{executionResults.total || 0}</p>
                      <p className="text-sm text-gray-600">Total</p>
                    </div>
                    <div className="text-center">
                      <p className="text-2xl font-bold text-purple-600">
                        {executionResults.total > 0 ? Math.round((executionResults.successful / executionResults.total) * 100) : 0}%
                      </p>
                      <p className="text-sm text-gray-600">Success Rate</p>
                    </div>
                  </div>
                </CardContent>
              </Card>
            )}
          </TabsContent>

          <TabsContent value="templates" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Mail className="h-5 w-5" />
                  Email Templates ({templates.length})
                </CardTitle>
                <CardDescription>
                  Manage and send follow-up emails from your campaign sequence
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                {templates.length === 0 ? (
                  <div className="text-center py-8">
                    <Mail className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                    <p className="text-gray-500">No email templates found</p>
                    <p className="text-sm text-gray-400">Generate templates using AI to get started</p>
                  </div>
                ) : (
                  templates.map((template, index) => (
                    <Card key={index}>
                      <CardHeader className="pb-3">
                        <div className="flex items-center justify-between">
                          <CardTitle className="text-base">
                            Template {index + 1}: {template.title || `Email ${index + 1}`}
                          </CardTitle>
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => handleFollowup(index)}
                            disabled={sendFollowupMutation.isPending}
                          >
                            <Send className="h-3 w-3 mr-2" />
                            Send Follow-up
                          </Button>
                        </div>
                      </CardHeader>
                      <CardContent>
                        <div className="space-y-2">
                          <p className="text-sm font-medium">Subject: {template.subject || 'No subject'}</p>
                          <div className="p-3 bg-gray-50 rounded text-xs max-h-32 overflow-y-auto">
                            <div dangerouslySetInnerHTML={{ __html: template.content || 'No content' }} />
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  ))
                )}
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="analytics" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <BarChart3 className="h-5 w-5" />
                  Campaign Analytics
                </CardTitle>
                <CardDescription>
                  Performance metrics and engagement data
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="text-center py-8">
                  <BarChart3 className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-500">Analytics coming soon</p>
                  <p className="text-sm text-gray-400">
                    Execute your campaign to start tracking performance metrics
                  </p>
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/campaigns/Handover.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Slider } from "@/components/ui/slider";
import { Switch } from "@/components/ui/switch";
import { Separator } from "@/components/ui/separator";
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { 
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { 
  Users, 
  Plus, 
  X, 
  Settings, 
  Target, 
  Clock,
  TrendingUp,
  AlertTriangle,
  Phone
} from "lucide-react";

interface HandoverRecipient {
  name: string;
  email: string;
  role: string;
}

interface HandoverCriteria {
  qualificationThreshold: number;
  intentScore: number;
  engagementThreshold: number;
  messageCount: number;
  timeSpentMinutes: number;
  goalCompletionRequired: string[];
  handoverRecipients: HandoverRecipient[];
  automotiveKeywords: string[];
  urgentKeywords: string[];
}

interface HandoverConfigProps {
  handoverCriteria?: HandoverCriteria;
  onHandoverCriteriaChange?: (criteria: HandoverCriteria) => void;
  campaignGoals?: string[];
}

const defaultHandoverCriteria: HandoverCriteria = {
  qualificationThreshold: 75,
  intentScore: 70,
  engagementThreshold: 60,
  messageCount: 5,
  timeSpentMinutes: 10,
  goalCompletionRequired: ['test_drive_interest', 'pricing_inquiry', 'financing_discussion'],
  handoverRecipients: [
    { name: 'Sales Manager', email: 'sales@dealership.com', role: 'sales' },
    { name: 'Service Manager', email: 'service@dealership.com', role: 'service' },
    { name: 'Finance Manager', email: 'finance@dealership.com', role: 'finance' }
  ],
  automotiveKeywords: [
    'test drive', 'financing', 'trade-in', 'lease', 'warranty',
    'maintenance', 'service appointment', 'parts', 'insurance'
  ],
  urgentKeywords: [
    'urgent', 'ASAP', 'today', 'immediately', 'emergency', 'breakdown'
  ]
};

export default function HandoverConfig({
  handoverCriteria = defaultHandoverCriteria,
  onHandoverCriteriaChange,
  campaignGoals = ['test_drive_interest', 'pricing_inquiry', 'financing_discussion', 'service_booking']
}: HandoverConfigProps) {
  const [newRecipient, setNewRecipient] = useState<HandoverRecipient>({
    name: '',
    email: '',
    role: 'sales'
  });
  const [newKeyword, setNewKeyword] = useState('');
  const [keywordType, setKeywordType] = useState<'automotive' | 'urgent'>('automotive');
  const [isRecipientDialogOpen, setIsRecipientDialogOpen] = useState(false);
  const [isKeywordDialogOpen, setIsKeywordDialogOpen] = useState(false);

  const updateCriteria = (key: keyof HandoverCriteria, value: any) => {
    const updated = { ...handoverCriteria, [key]: value };
    onHandoverCriteriaChange?.(updated);
  };

  const addRecipient = () => {
    if (newRecipient.name && newRecipient.email) {
      updateCriteria('handoverRecipients', [
        ...handoverCriteria.handoverRecipients,
        newRecipient,
      ]);
      setNewRecipient({ name: '', email: '', role: 'sales' });
      setIsRecipientDialogOpen(false);
    }
  };

  const removeRecipient = (index: number) => {
    updateCriteria('handoverRecipients', 
      handoverCriteria.handoverRecipients.filter((_, i) => i !== index)
    );
  };

  const addKeyword = () => {
    if (newKeyword.trim()) {
      const targetArray = keywordType === 'automotive' 
        ? handoverCriteria.automotiveKeywords 
        : handoverCriteria.urgentKeywords;

      const targetKey = keywordType === 'automotive' 
        ? 'automotiveKeywords' 
        : 'urgentKeywords';

      updateCriteria(targetKey, [...targetArray, newKeyword.trim().toLowerCase()]);
      setNewKeyword('');
      setIsKeywordDialogOpen(false);
    }
  };

  const removeKeyword = (keyword: string, type: 'automotive' | 'urgent') => {
    const targetArray = type === 'automotive' 
      ? handoverCriteria.automotiveKeywords 
      : handoverCriteria.urgentKeywords;

    const targetKey = type === 'automotive' 
      ? 'automotiveKeywords' 
      : 'urgentKeywords';

    updateCriteria(targetKey, targetArray.filter(k => k !== keyword));
  };

  const toggleGoalRequirement = (goal: string) => {
    const current = handoverCriteria.goalCompletionRequired;
    updateCriteria(
      'goalCompletionRequired',
      current.includes(goal)
        ? current.filter(g => g !== goal)
        : [...current, goal]
    );
  };

  const getRoleColor = (role: string) => {
    switch (role) {
      case 'sales': return 'bg-blue-100 text-blue-800';
      case 'service': return 'bg-green-100 text-green-800';
      case 'finance': return 'bg-purple-100 text-purple-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center gap-2 mb-4">
        <Settings className="h-6 w-6 text-blue-600" />
        <h3 className="text-2xl font-bold">Handover Configuration</h3>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Threshold Settings */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Target className="h-5 w-5" />
              Qualification Thresholds
            </CardTitle>
            <CardDescription>
              Configure when conversations should be handed over to human agents
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-6">
            <div>
              <Label className="text-sm font-medium">
                Qualification Threshold: {handoverCriteria.qualificationThreshold}%
              </Label>
              <Slider
                value={[handoverCriteria.qualificationThreshold]}
                onValueChange={([value]) => updateCriteria('qualificationThreshold', value)}
                max={100}
                step={5}
                className="mt-2"
              />
              <p className="text-xs text-gray-600 mt-1">
                Lead qualification score required for handover
              </p>
            </div>

            <div>
              <Label className="text-sm font-medium">
                Intent Score: {handoverCriteria.intentScore}%
              </Label>
              <Slider
                value={[handoverCriteria.intentScore]}
                onValueChange={([value]) => updateCriteria('intentScore', value)}
                max={100}
                step={5}
                className="mt-2"
              />
              <p className="text-xs text-gray-600 mt-1">
                Purchase intent indicator threshold
              </p>
            </div>

            <div>
              <Label className="text-sm font-medium">
                Engagement Level: {handoverCriteria.engagementThreshold}%
              </Label>
              <Slider
                value={[handoverCriteria.engagementThreshold]}
                onValueChange={([value]) => updateCriteria('engagementThreshold', value)}
                max={100}
                step={5}
                className="mt-2"
              />
              <p className="text-xs text-gray-600 mt-1">
                Conversation engagement threshold
              </p>
            </div>

            <Separator />

            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label className="text-sm font-medium">Message Count</Label>
                <Input
                  type="number"
                  value={handoverCriteria.messageCount}
                  onChange={(e) => updateCriteria('messageCount', parseInt(e.target.value) || 0)}
                  min={1}
                  className="mt-1"
                />
              </div>
              <div>
                <Label className="text-sm font-medium">Time Spent (minutes)</Label>
                <Input
                  type="number"
                  value={handoverCriteria.timeSpentMinutes}
                  onChange={(e) => updateCriteria('timeSpentMinutes', parseInt(e.target.value) || 0)}
                  min={1}
                  className="mt-1"
                />
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Goal Requirements */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <TrendingUp className="h-5 w-5" />
              Goal Requirements
            </CardTitle>
            <CardDescription>
              Select which campaign goals trigger handover when detected
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {campaignGoals.map((goal) => (
                <div key={goal} className="flex items-center justify-between">
                  <Label className="text-sm font-medium cursor-pointer">
                    {goal.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}
                  </Label>
                  <Switch
                    checked={handoverCriteria.goalCompletionRequired.includes(goal)}
                    onCheckedChange={() => toggleGoalRequirement(goal)}
                  />
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Recipients */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Users className="h-5 w-5" />
              Handover Recipients
            </CardTitle>
            <CardDescription>
              Configure who receives handover notifications
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {handoverCriteria.handoverRecipients.map((recipient, index) => (
                <div key={index} className="flex items-center justify-between p-3 border rounded-lg">
                  <div className="flex-1">
                    <div className="flex items-center gap-2">
                      <span className="font-medium">{recipient.name}</span>
                      <Badge className={getRoleColor(recipient.role)}>
                        {recipient.role}
                      </Badge>
                    </div>
                    <p className="text-sm text-gray-600">{recipient.email}</p>
                  </div>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => removeRecipient(index)}
                  >
                    <X className="h-4 w-4" />
                  </Button>
                </div>
              ))}

              <Dialog open={isRecipientDialogOpen} onOpenChange={setIsRecipientDialogOpen}>
                <DialogTrigger asChild>
                  <Button variant="outline" className="w-full">
                    <Plus className="h-4 w-4 mr-2" />
                    Add Recipient
                  </Button>
                </DialogTrigger>
                <DialogContent>
                  <DialogHeader>
                    <DialogTitle>Add Handover Recipient</DialogTitle>
                    <DialogDescription>
                      Add a new recipient for handover notifications
                    </DialogDescription>
                  </DialogHeader>
                  <div className="space-y-4">
                    <div>
                      <Label htmlFor="recipient-name">Name</Label>
                      <Input
                        id="recipient-name"
                        value={newRecipient.name}
                        onChange={(e) => setNewRecipient(prev => ({ ...prev, name: e.target.value }))}
                        placeholder="Sales Manager"
                      />
                    </div>
                    <div>
                      <Label htmlFor="recipient-email">Email</Label>
                      <Input
                        id="recipient-email"
                        type="email"
                        value={newRecipient.email}
                        onChange={(e) => setNewRecipient(prev => ({ ...prev, email: e.target.value }))}
                        placeholder="sales@dealership.com"
                      />
                    </div>
                    <div>
                      <Label htmlFor="recipient-role">Role</Label>
                      <Select 
                        value={newRecipient.role} 
                        onValueChange={(value) => setNewRecipient(prev => ({ ...prev, role: value }))}
                      >
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="sales">Sales</SelectItem>
                          <SelectItem value="service">Service</SelectItem>
                          <SelectItem value="finance">Finance</SelectItem>
                          <SelectItem value="manager">Manager</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                    <div className="flex gap-2">
                      <Button onClick={addRecipient} className="flex-1">Add Recipient</Button>
                      <Button variant="outline" onClick={() => setIsRecipientDialogOpen(false)}>
                        Cancel
                      </Button>
                    </div>
                  </div>
                </DialogContent>
              </Dialog>
            </div>
          </CardContent>
        </Card>

        {/* Keywords */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <AlertTriangle className="h-5 w-5" />
              Detection Keywords
            </CardTitle>
            <CardDescription>
              Keywords that trigger different handover behaviors
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {/* Automotive Keywords */}
              <div>
                <Label className="text-sm font-medium">Automotive Keywords</Label>
                <div className="flex flex-wrap gap-1 mt-2">
                  {handoverCriteria.automotiveKeywords.map((keyword) => (
                    <Badge key={keyword} variant="secondary" className="text-xs">
                      {keyword}
                      <Button
                        variant="ghost"
                        size="sm"
                        className="h-auto p-0 ml-1"
                        onClick={() => removeKeyword(keyword, 'automotive')}
                      >
                        <X className="h-3 w-3" />
                      </Button>
                    </Badge>
                  ))}
                </div>
              </div>

              {/* Urgent Keywords */}
              <div>
                <Label className="text-sm font-medium">Urgent Keywords</Label>
                <div className="flex flex-wrap gap-1 mt-2">
                  {handoverCriteria.urgentKeywords.map((keyword) => (
                    <Badge key={keyword} variant="destructive" className="text-xs">
                      {keyword}
                      <Button
                        variant="ghost"
                        size="sm"
                        className="h-auto p-0 ml-1 text-red-100 hover:text-red-200"
                        onClick={() => removeKeyword(keyword, 'urgent')}
                      >
                        <X className="h-3 w-3" />
                      </Button>
                    </Badge>
                  ))}
                </div>
              </div>

              <Dialog open={isKeywordDialogOpen} onOpenChange={setIsKeywordDialogOpen}>
                <DialogTrigger asChild>
                  <Button variant="outline" className="w-full">
                    <Plus className="h-4 w-4 mr-2" />
                    Add Keyword
                  </Button>
                </DialogTrigger>
                <DialogContent>
                  <DialogHeader>
                    <DialogTitle>Add Detection Keyword</DialogTitle>
                    <DialogDescription>
                      Add a new keyword for conversation analysis
                    </DialogDescription>
                  </DialogHeader>
                  <div className="space-y-4">
                    <div>
                      <Label htmlFor="keyword-type">Keyword Type</Label>
                      <Select value={keywordType} onValueChange={(value: 'automotive' | 'urgent') => setKeywordType(value)}>
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="automotive">Automotive</SelectItem>
                          <SelectItem value="urgent">Urgent</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                    <div>
                      <Label htmlFor="new-keyword">Keyword</Label>
                      <Input
                        id="new-keyword"
                        value={newKeyword}
                        onChange={(e) => setNewKeyword(e.target.value)}
                        placeholder="Enter keyword..."
                        onKeyPress={(e) => e.key === 'Enter' && addKeyword()}
                      />
                    </div>
                    <div className="flex gap-2">
                      <Button onClick={addKeyword} className="flex-1">Add Keyword</Button>
                      <Button variant="outline" onClick={() => setIsKeywordDialogOpen(false)}>
                        Cancel
                      </Button>
                    </div>
                  </div>
                </DialogContent>
              </Dialog>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/components/dashboard/SystemHe.content:
import { useQuery } from "@tanstack/react-query";
import { CheckCircle, AlertCircle, XCircle, Mail, Zap, Database } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";

interface SystemStatus {
  service: string;
  status: 'online' | 'warning' | 'offline';
  lastCheck: string;
  details?: string;
}

export default function SystemHealth() {
  const { data: emailStatus } = useQuery({
    queryKey: ['/api/email-monitor/status'],
    retry: false
  });

  const systemStatuses: SystemStatus[] = [
    {
      service: "Email Monitor",
      status: emailStatus?.running ? 'online' : 'warning',
      lastCheck: "Just now",
      details: emailStatus?.running ? "Processing emails" : "Ready to start"
    },
    {
      service: "AI Processing", 
      status: 'online',
      lastCheck: "Just now",
      details: "OpenRouter API connected"
    },
    {
      service: "Database",
      status: 'online', 
      lastCheck: "Just now",
      details: "PostgreSQL connected"
    }
  ];

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'online':
        return <CheckCircle className="w-4 h-4 text-green-500" />;
      case 'warning':
        return <AlertCircle className="w-4 h-4 text-yellow-500" />;
      case 'offline':
        return <XCircle className="w-4 h-4 text-red-500" />;
      default:
        return <AlertCircle className="w-4 h-4 text-gray-500" />;
    }
  };

  const getServiceIcon = (service: string) => {
    switch (service) {
      case 'Email Monitor':
        return <Mail className="w-4 h-4 text-blue-500" />;
      case 'AI Processing':
        return <Zap className="w-4 h-4 text-purple-500" />;
      case 'Database':
        return <Database className="w-4 h-4 text-orange-500" />;
      default:
        return <CheckCircle className="w-4 h-4 text-gray-500" />;
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'online':
        return 'Online';
      case 'warning': 
        return 'Warning';
      case 'offline':
        return 'Offline';
      default:
        return 'Unknown';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'online':
        return 'text-green-600 bg-green-50';
      case 'warning':
        return 'text-yellow-600 bg-yellow-50';
      case 'offline':
        return 'text-red-600 bg-red-50';
      default:
        return 'text-gray-600 bg-gray-50';
    }
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="text-lg font-semibold">System Health</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-3">
        </div>
      </CardContent>
    </Card>
  );
}

