import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Users, Target, Mail } from "lucide-react";

interface LeadCampaignAssignmentProps {
  children: React.ReactNode;
}

export default function LeadCampaignAssignment({ children }: LeadCampaignAssignmentProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedCampaign, setSelectedCampaign] = useState<string>("");
  const [selectedLeads, setSelectedLeads] = useState<string[]>([]);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: campaigns } = useQuery({
    queryKey: ["/api/campaigns"],
  });

  const { data: leads } = useQuery({
    queryKey: ["/api/leads"],
  });

  const assignLeadsMutation = useMutation({
    mutationFn: async (data: { campaignId: string; leadIds: string[] }) => {
      // Update each lead with the campaign ID
      const promises = data.leadIds.map(leadId =>
        apiRequest(`/api/leads/${leadId}`, "PUT", { campaignId: data.campaignId })
      );
      return Promise.all(promises);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/leads"] });
      toast({
        title: "Leads Assigned",
        description: `Successfully assigned ${selectedLeads.length} leads to campaign`,
      });
      setIsOpen(false);
      setSelectedCampaign("");
      setSelectedLeads([]);
    },
    onError: () => {
      toast({
        title: "Assignment Failed",
        description: "Failed to assign leads to campaign",
        variant: "destructive",
      });
    },
  });

  const handleAssign = () => {
    if (selectedCampaign && selectedLeads.length > 0) {
      assignLeadsMutation.mutate({
        campaignId: selectedCampaign,
        leadIds: selectedLeads
      });
    }
  };

  const campaignList = Array.isArray(campaigns) ? campaigns : [];
  const leadList = Array.isArray(leads) ? leads : [];
  const unassignedLeads = leadList.filter(lead => !lead.campaignId);

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        {children}
      </DialogTrigger>
      <DialogContent className="max-w-2xl">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Target className="h-5 w-5" />
            Assign Leads to Campaign
          </DialogTitle>
          <DialogDescription>
            Assign unassigned leads to a specific campaign for targeted outreach
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          {/* Campaign Selection */}
          <Card>
            <CardHeader>
              <CardTitle className="text-base">Select Campaign</CardTitle>
              <CardDescription>Choose which campaign to assign the leads to</CardDescription>
            </CardHeader>
            <CardContent>
              <Select value={selectedCampaign} onValueChange={setSelectedCampaign}>
                <SelectTrigger>
                  <SelectValue placeholder="Select a campaign" />
                </SelectTrigger>
                <SelectContent>
                  {campaignList.map((campaign) => (
                    <SelectItem key={campaign.id} value={campaign.id}>
                      <div className="flex items-center gap-2">
                        <span>{campaign.name}</span>
                        <Badge variant="secondary" className="text-xs">
                          {campaign.status}
                        </Badge>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </CardContent>
          </Card>

          {/* Lead Selection */}
          <Card>
            <CardHeader>
              <CardTitle className="text-base flex items-center gap-2">
                <Users className="h-4 w-4" />
                Available Leads ({unassignedLeads.length})
              </CardTitle>
              <CardDescription>Select leads to assign to the campaign</CardDescription>
            </CardHeader>
            <CardContent>
              {unassignedLeads.length === 0 ? (
                <div className="text-center py-8">
                  <Users className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-500">No unassigned leads available</p>
                  <p className="text-sm text-gray-400">All leads are already assigned to campaigns</p>
                </div>
              ) : (
                <div className="space-y-2 max-h-60 overflow-y-auto">
                  <div className="flex items-center gap-2 mb-2">
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => setSelectedLeads(
                        selectedLeads.length === unassignedLeads.length 
                          ? [] 
                          : unassignedLeads.map(lead => lead.id)
                      )}
                    >
                      {selectedLeads.length === unassignedLeads.length ? "Deselect All" : "Select All"}
                    </Button>
                    <span className="text-sm text-gray-600">
                      {selectedLeads.length} of {unassignedLeads.length} selected
                    </span>
                  </div>

                  {unassignedLeads.map((lead) => (
                    <div
                      key={lead.id}
                      className={`p-3 border rounded-lg cursor-pointer transition-colors ${
                        selectedLeads.includes(lead.id)
                          ? "border-blue-500 bg-blue-50"
                          : "border-gray-200 hover:border-gray-300"
                      }`}
                      onClick={() => {
                        setSelectedLeads(prev =>
                          prev.includes(lead.id)
                            ? prev.filter(id => id !== lead.id)
                            : [...prev, lead.id]
                        );
                      }}
                    >
                      <div className="flex items-center justify-between">
                        <div>
                          <p className="font-medium">
                            {lead.firstName} {lead.lastName}
                          </p>
                          <p className="text-sm text-gray-600">{lead.email}</p>
                          {lead.vehicleInterest && (
                            <p className="text-xs text-gray-500">
                              Interested in: {lead.vehicleInterest}
                            </p>
                          )}
                        </div>
                        <Badge variant="secondary" className="text-xs">
                          {lead.status}
                        </Badge>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>

          {/* Action Buttons */}
          <div className="flex gap-2">
            <Button
              onClick={handleAssign}
              disabled={!selectedCampaign || selectedLeads.length === 0 || assignLeadsMutation.isPending}
              className="flex-1"
            >
              <Mail className="h-4 w-4 mr-2" />
              {assignLeadsMutation.isPending ? "Assigning..." : `Assign ${selectedLeads.length} Leads`}
            </Button>
            <Button variant="outline" onClick={() => setIsOpen(false)}>
              Cancel
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/leads/LeadImportDi.content:
import React, { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Progress } from '@/components/ui/progress';
import { Card, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Upload, FileText, Check, X, AlertTriangle } from 'lucide-react';
import { toast } from '@/hooks/use-toast';

interface FieldMapping {
  csvColumn: string;
  leadField: string;
}

interface ImportResult {
  total: number;
  successful: number;
  failed: number;
  errors: Array<{ row?: number; error: string }>;
  leads: any[];
}

interface LeadImportDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onImportComplete?: (result: ImportResult) => void;
  campaigns?: Array<{ id: string; name: string }>;
}

export function LeadImportDialog({ 
  open, 
  onOpenChange, 
  onImportComplete,
  campaigns = []
}: LeadImportDialogProps) {
  const [step, setStep] = useState<'upload' | 'mapping' | 'importing' | 'complete'>('upload');
  const [file, setFile] = useState<File | null>(null);
  const [csvData, setCsvData] = useState<any>(null);
  const [mappings, setMappings] = useState<FieldMapping[]>([]);
  const [selectedCampaign, setSelectedCampaign] = useState<string>('');
  const [importProgress, setImportProgress] = useState(0);
  const [importResult, setImportResult] = useState<ImportResult | null>(null);

  const leadFields = [
    { value: '', label: 'Ignore Column' },
    { value: 'firstName', label: 'First Name' },
    { value: 'lastName', label: 'Last Name' },
    { value: 'email', label: 'Email (Required)' },
    { value: 'phone', label: 'Phone' },
    { value: 'source', label: 'Source' },
    { value: 'vehicleInterest', label: 'Vehicle Interest' },
    { value: 'employer', label: 'Employer/Company' },
    { value: 'jobTitle', label: 'Job Title' },
    { value: 'income', label: 'Income' },
    { value: 'creditScore', label: 'Credit Score' },
    { value: 'notes', label: 'Notes' }
  ];

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = event.target.files?.[0];
    if (selectedFile && selectedFile.type === 'text/csv') {
      setFile(selectedFile);
    } else {
      toast({
        title: 'Invalid File',
        description: 'Please select a CSV file.',
        variant: 'destructive'
      });
    }
  };

  const analyzeFile = async () => {
    if (!file) return;

    setImportProgress(10);

    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch('/api/leads/import/analyze', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error('Failed to analyze CSV file');
      }

      const data = await response.json();
      setCsvData(data);
      setMappings(data.suggestedMappings || []);
      setStep('mapping');
      setImportProgress(25);
    } catch (error) {
      console.error('CSV analysis error:', error);
      toast({
        title: 'Analysis Failed',
        description: 'Unable to analyze the CSV file. Please check the format.',
        variant: 'destructive'
      });
    }
  };

  const updateMapping = (csvColumn: string, leadField: string) => {
    setMappings(prev => 
      prev.map(mapping => 
        mapping.csvColumn === csvColumn 
          ? { ...mapping, leadField }
          : mapping
      )
    );
  };

  const startImport = async () => {
    if (!file) return;

    setStep('importing');
    setImportProgress(50);

    try {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('mappings', JSON.stringify(mappings));
      if (selectedCampaign) {
        formData.append('campaignId', selectedCampaign);
      }

      const response = await fetch('/api/leads/import', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error('Import failed');
      }

      const result = await response.json();
      setImportResult(result);
      setStep('complete');
      setImportProgress(100);

      if (onImportComplete) {
        onImportComplete(result);
      }

      toast({
        title: 'Import Complete',
        description: `Successfully imported ${result.successful} leads${result.failed > 0 ? ` (${result.failed} failed)` : ''}`,
        variant: result.failed > 0 ? 'destructive' : 'default'
      });
    } catch (error) {
      console.error('Import error:', error);
      toast({
        title: 'Import Failed',
        description: 'Unable to import leads. Please try again.',
        variant: 'destructive'
      });
      setStep('mapping');
    }
  };

  const resetDialog = () => {
    setStep('upload');
    setFile(null);
    setCsvData(null);
    setMappings([]);
    setSelectedCampaign('');
    setImportProgress(0);
    setImportResult(null);
  };

  const hasValidEmailMapping = mappings.some(m => m.leadField === 'email');

  return (
    <Dialog open={open} onOpenChange={(open) => {
      onOpenChange(open);
      if (!open) resetDialog();
    }}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Import Leads from CSV</DialogTitle>
        </DialogHeader>

        {/* Progress Bar */}
        <div className="mb-6">
          <Progress value={importProgress} className="h-2" />
          <div className="flex justify-between text-xs text-gray-500 mt-1">
            <span>Upload</span>
            <span>Field Mapping</span>
            <span>Importing</span>
            <span>Complete</span>
          </div>
        </div>

        {/* Step 1: File Upload */}
        {step === 'upload' && (
          <div className="space-y-6">
            <div>
              <Label htmlFor="csvFile">Select CSV File</Label>
              <div className="mt-2 flex items-center justify-center w-full">
                <label className="flex flex-col items-center justify-center w-full h-64 border-2 border-gray-300 border-dashed rounded-lg cursor-pointer bg-gray-50 hover:bg-gray-100">
                  <div className="flex flex-col items-center justify-center pt-5 pb-6">
                    <Upload className="w-10 h-10 mb-3 text-gray-400" />
                    <p className="mb-2 text-sm text-gray-500">
                      <span className="font-semibold">Click to upload</span> or drag and drop
                    </p>
                    <p className="text-xs text-gray-500">CSV files only</p>
                  </div>
                  <input
                    id="csvFile"
                    type="file"
                    accept=".csv"
                    className="hidden"
                    onChange={handleFileSelect}
                  />
                </label>
              </div>
              {file && (
                <div className="mt-2 flex items-center space-x-2">
                  <FileText className="h-4 w-4 text-green-600" />
                  <span className="text-sm text-green-600">{file.name}</span>
                </div>
              )}
            </div>

            <div className="bg-blue-50 p-4 rounded-lg">
              <h3 className="font-semibold text-blue-800 mb-2">CSV Format Requirements:</h3>
              <ul className="text-sm text-blue-700 space-y-1">
                <li>â€¢ First row should contain column headers</li>
                <li>â€¢ Email column is required for all leads</li>
                <li>â€¢ Supported fields: Name, Email, Phone, Vehicle Interest, etc.</li>
                <li>â€¢ Maximum file size: 10MB</li>
              </ul>
            </div>

            <div className="flex justify-end">
              <Button onClick={analyzeFile} disabled={!file}>
                Next: Map Fields
              </Button>
            </div>
          </div>
        )}

        {/* Step 2: Field Mapping */}
        {step === 'mapping' && csvData && (
          <div className="space-y-6">
            <div>
              <h3 className="text-lg font-semibold mb-2">Map CSV Columns to Lead Fields</h3>
              <p className="text-sm text-gray-600 mb-4">
                Found {csvData.totalRows} rows with {csvData.headers.length} columns
              </p>
            </div>

            {/* Campaign Selection */}
            <div>
              <Label htmlFor="campaign">Assign to Campaign (Optional)</Label>
              <Select value={selectedCampaign} onValueChange={setSelectedCampaign}>
                <SelectTrigger>
                  <SelectValue placeholder="Select a campaign" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="">No Campaign</SelectItem>
                  {campaigns.map(campaign => (
                    <SelectItem key={campaign.id} value={campaign.id}>
                      {campaign.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Field Mappings */}
            <div className="space-y-3">
              {mappings.map((mapping, index) => (
                <div key={index} className="flex items-center space-x-4 p-3 border rounded">
                  <div className="flex-1">
                    <Label className="text-sm font-medium">{mapping.csvColumn}</Label>
                    {csvData.previewRows[0] && (
                      <p className="text-xs text-gray-500 mt-1">
                        Example: {csvData.previewRows[0][mapping.csvColumn]}
                      </p>
                    )}
                  </div>
                  <div className="w-48">
                    <Select 
                      value={mapping.leadField} 
                      onValueChange={(value) => updateMapping(mapping.csvColumn, value)}
                    >
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        {leadFields.map(field => (
                          <SelectItem key={field.value} value={field.value}>
                            {field.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                </div>
              ))}
            </div>

            {/* Validation */}
            <div className="bg-yellow-50 p-4 rounded-lg">
              {!hasValidEmailMapping ? (
                <div className="flex items-center space-x-2 text-yellow-700">
                  <AlertTriangle className="h-4 w-4" />
                  <span className="text-sm">âš ï¸ Email field mapping is required</span>
                </div>
              ) : (
                <div className="flex items-center space-x-2 text-green-700">
                  <Check className="h-4 w-4" />
                  <span className="text-sm">âœ… Ready to import</span>
                </div>
              )}
            </div>

            <div className="flex justify-between">
              <Button variant="outline" onClick={() => setStep('upload')}>
                Back
              </Button>
              <Button onClick={startImport} disabled={!hasValidEmailMapping}>
                Import Leads
              </Button>
            </div>
          </div>
        )}

        {/* Step 3: Importing */}
        {step === 'importing' && (
          <div className="text-center space-y-4">
            <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-blue-600 mx-auto"></div>
            <h3 className="text-lg font-semibold">Importing Leads...</h3>
            <p className="text-gray-600">Please wait while we process your CSV file</p>
          </div>
        )}

        {/* Step 4: Complete */}
        {step === 'complete' && importResult && (
          <div className="space-y-6">
            <div className="text-center">
              <div className="mx-auto w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mb-4">
                <Check className="w-8 h-8 text-green-600" />
              </div>
              <h3 className="text-lg font-semibold">Import Complete!</h3>
            </div>

            {/* Results Summary */}
            <div className="grid grid-cols-3 gap-4">
              <Card>
                <CardContent className="p-4 text-center">
                  <div className="text-2xl font-bold text-blue-600">{importResult.total}</div>
                  <div className="text-sm text-gray-600">Total Processed</div>
                </CardContent>
              </Card>
              <Card>
                <CardContent className="p-4 text-center">
                  <div className="text-2xl font-bold text-green-600">{importResult.successful}</div>
                  <div className="text-sm text-gray-600">Successfully Imported</div>
                </CardContent>
              </Card>
              <Card>
                <CardContent className="p-4 text-center">
                  <div className="text-2xl font-bold text-red-600">{importResult.failed}</div>
                  <div className="text-sm text-gray-600">Failed</div>
                </CardContent>
              </Card>
            </div>

            {/* Errors */}
            {importResult.errors.length > 0 && (
              <div>
                <h4 className="font-semibold mb-2">Import Errors:</h4>
                <div className="max-h-32 overflow-y-auto space-y-1">
                  {importResult.errors.slice(0, 10).map((error, index) => (
                    <div key={index} className="text-sm text-red-600 flex items-center space-x-2">
                      <X className="h-3 w-3" />
                      <span>Row {error.row}: {error.error}</span>
                    </div>
                  ))}
                  {importResult.errors.length > 10 && (
                    <p className="text-sm text-gray-500">
                      ... and {importResult.errors.length - 10} more errors
                    </p>
                  )}
                </div>
              </div>
            )}

            <div className="flex justify-center">
              <Button onClick={() => onOpenChange(false)}>
                Close
              </Button>
            </div>
          </div>
        )}
      </DialogContent>
    </Dialog>
  );
}

